:root {
--colour:#569cd1;
--bground:url(https://img-fotki.yandex.ru/get/876984/500851312.1/0_149997_97eeca71_orig.jpg);
--huerot:hue-rotate(0deg);
}

#page_header_cont .back:before {
    content: '';
    position: fixed;
    height: 46px;
    width: 46px;
    top: 0;
    right: -42px;
    background: url('https://img-fotki.yandex.ru/get/876523/500851312.1/0_149a3d_662f465_orig.gif') 50% 50% no-repeat;
    animation: trixieanim 50s infinite linear;
    transform: translateZ(0);

}

@keyframes trixieanim {
0%{
   transform: scalex(1);
   right: -42px;
}
49%{
   right: calc(42px + 100%);
   transform: scalex(1);
}
52%{
   right: calc(42px + 100%);
   transform: scalex(-1);
}

98%{
   right: -42px;
   transform: scalex(-1);   
}
100%{
   right: -42px;
   transform: scalex(1);   
}
}

