@-moz-document domain("zone-telechargement1.com") {
@font-face
  {
    font-family: 'Bebas Neue Bold';
    font-style: normal;
    font-weight: normal;
    src: local('Bebas Neue Bold'), url('https://therolf.esy.es/font/BEBASNEUE BOLD.woff') format('woff');
  }

  *
  {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    outline: none;
    background: none !important;
  }

  ::-webkit-input-placeholder
  {
        /* WebKit browsers */
    color: transparent;
  }

  :-moz-placeholder
  {
        /* Mozilla Firefox 4 to 18 */
    color: transparent;
  }

  ::-moz-placeholder
  {
        /* Mozilla Firefox 19+ */
    color: transparent;
  }

  :-ms-input-placeholder
  {
        /* Internet Explorer 10+ */
    color: transparent;
  }

  textarea::-webkit-input-placeholder
  {
        /* WebKit browsers */
    color: transparent;
  }

  textarea:-moz-placeholder
  {
        /* Mozilla Firefox 4 to 18 */
    color: transparent;
  }

  textarea::-moz-placeholder
  {
        /* Mozilla Firefox 19+ */
    color: transparent;
  }

  textarea:-ms-input-placeholder
  {
        /* Internet Explorer 10+ */
    color: transparent;
  }

  html, body, .tiles .tile .element
  {
    height: 100%;
  }

  body
  {
    margin: 0;
    font-family: 'Open Sans', Arial, sans-serif;
  }

  .element
  {
    background-color: #f1f1f1;
    padding: 10px 20px;
    -moz-box-shadow: 0px 1px 2px 0px rgba(0, 0, 0, 0.1);
    -webkit-box-shadow: 0px 1px 2px 0px rgba(0, 0, 0, 0.1);
    -o-box-shadow: 0px 1px 2px 0px rgba(0, 0, 0, 0.1);
    box-shadow: 0px 1px 2px 0px rgba(0, 0, 0, 0.1);
    position: relative;
  }
    /* cyrillic */
@font-face
  {
    font-family: 'Oswald';
    font-style: normal;
    font-weight: 400;
    src: local('Oswald Regular'), local('Oswald-Regular'), url(https://fonts.gstatic.com/s/oswald/v16/DgBpgaYycijFA8v2hNt7MfesZW2xOQ-xsNqO47m55DA.woff2) format('woff2');
  }
/* vietnamese */
@font-face
  {
    font-family: 'Oswald';
    font-style: normal;
    font-weight: 400;
    src: local('Oswald Regular'), local('Oswald-Regular'), url(https://fonts.gstatic.com/s/oswald/v16/peRd8sj511qE2lHtK-QfcPesZW2xOQ-xsNqO47m55DA.woff2) format('woff2');
  }
/* latin-ext */
@font-face
  {
    font-family: 'Oswald';
    font-style: normal;
    font-weight: 400;
    src: local('Oswald Regular'), local('Oswald-Regular'), url(https://fonts.gstatic.com/s/oswald/v16/yg0glPPxXUISnKUejCX4qfesZW2xOQ-xsNqO47m55DA.woff2) format('woff2');
  }
/* latin */
@font-face
  {
    font-family: 'Oswald';
    font-style: normal;
    font-weight: 400;
    src: local('Oswald Regular'), local('Oswald-Regular'), url(https://fonts.gstatic.com/s/oswald/v16/pEobIV_lL25TKBpqVI_a2w.woff2) format('woff2');
  }

  h1, h2, h3, h4, h5, h6
  {
    font-weight: 600;
    margin: 0;
    line-height: 1;
  }

  img, video, iframe
  {
    max-height: 100%;
    max-width: 100%;
  }

  input, textarea
  {
    outline: none;
    -moz-resize: none;
    resize: none;
    font-family: inherit;
  }

  ul, li
  {
    margin: 0;
    padding: 0;
    list-style-type: none;
  }

  a, a:hover
  {
    color: inherit;
    text-decoration: none !important;
  }

  .top>*
  {
    display: inline-block;
    vertical-align: top;
  }

  .middle>*
  {
    display: inline-block;
    vertical-align: middle;
  }

  .bottom>*
  {
    display: inline-block;
    vertical-align: bottom;
  }

  .center
  {
    text-align: center;
  }

  .tier
  {
    width: 33.33%;
  }

  .doubletier
  {
    width: 66.66%;
  }

  .demiw
  {
    width: 50%;
  }

  .demih
  {
    height: 50%;
  }

  .taille
  {
    height: 100%;
  }

  .width960
  {
    width: 960px;
    max-width: 100%;
    margin: 0 auto;
  }

  .width1020
  {
    width: 1020px;
    max-width: 100%;
    margin: 0 auto;
  }

  .width660
  {
    width: 660px;
    max-width: 100%;
  }

  .noselect
  {
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
  }

  .floatleft
  {
    float: left;
  }

  .floatright
  {
    float: right;
  }

  table
  {
    border-collapse: collapse;
        /* Les bordures du tableau seront collÃ©es (plus joli) */;
  }

  .icon-middle, .icon-middle + *
  {
    display: inline-block;
    vertical-align: middle;
  }

  .icon-middle + *
  {
    margin-left: 5px;
  }

  .min-40
  {
    height: 40px;
    width: 40px;
    border-radius: 50%;
    overflow: hidden;
  }

  .note
  {
    color: #ccc;
    font-style: italic;
  }

  input.nostyle
  {
    border: 0;
    padding: 0;
    background: transparent;
    background: none;
    font-size: inherit;
    text-transform: uppercase;
    color: inherit;
  }

  .tiles .tile
  {
    height: 150px;
    font-size: 28px;
    margin-bottom: 20px;
    color: white;
  }

  .tiles .tile .title
  {
    font-weight: 700;
    font-weight: 600;
  }

  .tiles .tile .desc
  {
    font-size: .8em;
  }

  .triple-2-1 .doubletier, .triple-1-1-1 .tier:first-child
  {
    padding: 0 10px 0 0;
  }

  .triple-2-1 .tier, .triple-1-1-1 .tier:last-child
  {
    padding: 0 0 0 10px;
  }

  .triple-1-1-1 .tier:nth-child(2)
  {
    padding: 0 10px;
  }
    @media screen and (max-width: 1020px)
  {
    .desktop, .tiles .tile img {
            display: none;
  }

  .demiw
  {
    width: 100%;
  }
}

@media screen and (min-width: 1020px)
{
  .mobile
  {
    display: none;
  }
}

.none
{
  display: none;
}

html {
    text-align: center;
  background: rgb(56, 56, 56) !important;
}
body
{
  margin: 0 auto !important;
}

.leftside .blocks .dcont, .rightside .blocks .dcont {
	width: auto;
}
.archives:before {
	content: "Archives";
    padding-left: 24px;
    font-size: 14px;
    color: #ada8a8 !important;
    font-weight: bold;
}
.border_dot a, .genre_style:hover {
    padding-left: 24px;
    font-size: 14px;
    color: #ada8a8 !important;
    font-weight: bold;
}
.archives .annee {
	padding-left: 24px;
}
.archives .annee form select {
	border: 0px none;
    background: #fff !important;
    margin-top: 10px;
}
#calendar-layer {
	margin: 0 20px;
    padding: 5px 0;
	background: #fff !important;
}
.s_left, .s_right
{
  padding: 0 !important;
}

.wrapper
{
  width: auto !important;
  min-width: 0 !important;
  max-width: none !important;
}

.tophead
{
  overflow: auto !important;
  height: 0px !important;
}

.recherche_menu
{
  position: absolute;
  left: 0;
  top: 98px;
  z-index: 1000;
  padding: 0 15px;
}

.recherche_menu > *
{
  background: white !important;
}

.searchbox .sinputt input
{
  color: black !important;
}

.recherche_menu *
{
  padding: 0 !important;
  max-width: 100%;
  height: 30px !important;
}

.searchbox .sinputt input
{
  line-height: 30px;
  padding: 2px 2px 2px 40px !important;
}

.searchbox .submit
{
  right: auto !important;
  left: 0;
}

h1.logo
{
  top: 0!important;
  left: 0 !important;
  z-index: 1000;
}

h1.logo a
{
  background: #CD2920 !important;
  overflow: hidden;
  text-align: center;
  text-transform: uppercase;
  color: white;
  padding: 20px 0;
  display: block;
}

h1.logo a:before
{
  content: "ZONE TELECHARGEMENT";
  font-family: inherit;
  line-height: 48px;
  font-size: 20px;
}

h1.logo a img
{
  display: none;
}

.tophead .dright
{
  position: static;
}

.container
{
  position: relative;
}

.container .centerside
{
  margin: 0;
  padding: 0;
}

.centerside .centersideinn
{
    padding: 10px;
    color: white;
}

.centerside #dle-content, body center > b
{
  width: 1020px;
  max-width: 100%;
  margin: 0 auto;
  border: 0px none;
	padding: 0px;
    display: block;
}
body .wrapper.toppad + br + center {
	padding-left: 320px;
}
#dle-content > .base
{
  border: 0px none !important;
}

.corps >center:first-child, .corps > br
{
  display: none;
}

.titrearticles
{
  padding-bottom: 0;
}

.otherversions
{
	margin: 0 !important;
	border-top: 0px none !important;
}

#searchform.searchbox .sinputt div
{
  background: #e50914;
}
.corps {
	padding: 0;
}
.searchbox .btn, .searchbox .btnhover
{
  background: url(http://svgshare.com/i/3sg.svg) no-repeat center center !important;
  background-size: auto 55% !important;
}

.actualitesite, .menuico, .centerside > center, .leftside .dtop, body > font, .see_more, .cover_infos_genre, .cover_infos_release_date, .cover_global > img + span, #fullsearch, #fullsearch + * + *, font > .s_left ~ *, div:empty
{
  display: none !important;
}

.leftside #new-cat #new-title-cat a
{
  color: #ada8a8 !important;
}

#new-title-cat
{
  border: 0px none;
}

#new-title-cat #new-cat a
{
  color: #ABABAB !important;
}

.recherche_menu, .leftside, .main_menu_left, .leftside, .main_menu_left, h1.logo a, body:after
{
  width: 320px !important;
}

.container .centerside, body > center
{
  padding-left: 320px;
}

.leftside
{
  position: relative;
  left: 0;
  top: 0;
  bottom: 0;
  z-index: 500;
  padding-top: 128px;
}

.dpad > br
{
  display: none;
}

body:after
{
  content: "";
  display: block;
  position: fixed;
  z-index: 1;
  left: 0;
  top: 0;
  bottom: 0;
  background: #222 !important;
}

.centersideinn #dle-content .dpad
{
  text-align: left !important;
}

#new-cat a
{
  color: #eee;
}
#new-cat a:hover {
	color: #f00;
}

.onetitle
{
  text-align: left;
  margin: 20px 10px;
}

.onetitle a
{
  color: white !important;
  background: #CD2920 !important;
	padding: 10px;
}
.otherversions h3 {
	margin-bottom: 10px;
    color: white !important;
	padding: 10px;
}
.cover_global
{
    position: relative;
    padding: 0;
    -webkit-box-shadow: 0px 0px 5px 0px #202020;
    -moz-box-shadow: 0px 0px 5px 0px #202020;
    -o-box-shadow: 0px 0px 5px 0px #202020;
    box-shadow: 0px 0px 5px 0px #202020;
    border: 0px none;
    background: black !important;
    height: 193px !important;
    text-align: center;
}
.dpad .cover_global {
    padding-top: 103px;
}
.cover_global, .cover_global * {
  transition: all .2s ease;
}
.cover_infos_title {
	position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    opacity: 0;
    z-index: 5;
}
.cover_global .cover_infos_global {
	overflow: visible;
}
.cover_global .cover_infos_title {
	opacity: 1;
}
.dpad .cover_global > *:first-child
{
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  opacity: 1;
  z-index: 1;
  margin: 0;
  height: 193px !important;
}

.dpad .cover_global > *
{
  opacity: 0;
  transition: all .2s ease;
  position: relative;
  z-index: 5;
}

.cover_global:hover > *
{
  opacity: 1;
}

.cover_global:hover div img
{
  opacity: .6;
}
.cover_global img.mainimg {
	position: absolute;
    left: 0;
    border: 0px none;
}
.cover_infos_global a
{
  color: white !important;
}
#dle-content > .cover_global > *:nth-child(2) {
	position: absolute;
    top: 0;
    left: 0;
    right: 0;
    z-index: 4;
}

.navigation
{
  padding: 20px 0;
}

.rightside
{
  padding: 0;
  margin: 20px;
}
.footer, .link_cat, .titrearticles, .divinnews {
	display: none;
}
.otherquality {
    background: #e2e2e2 !important;
}
#comms_contener, .dle-comments-navigation .navigation > * {
	background: white !important;
}
}
