css:
@-moz-document domain("xenmenu.com") {
/** ALTERNATING BG FOR MESSAGE THREADS
.siropuChatMessages > li:nth-child(odd)
{	background: #2d2d2d;    }**/

#siropuChatFull #siropuChatContent { bottom: 6%!important; }
#siropuChat { background-color: rgb(41, 44, 48); overflow: hidden; }
#siropuChatHeader { background: #2d2d2d; border-radius: 0; }
#siropuChatHeader h3 { display: none; }
#siropuChatOptions { float: none!important; text-align: center; }
#siropuChatNotice { display: block!important; text-align: center; }
#siropuChatContent { border-top: 1px solid #464646; top: 6%!important; }
.tabs { border: none; padding: 0; margin-top: 0; }
.tabs li a { border-radius: 0; border-right: 1px solid #464646!important; line-height: 36px!important; padding: 4px 16px!important; }
.tabs li a, .tabs.noLinks li { background-color: transparent; }
.tabs li.active a, .tabs.noLinks li.active { background-color: transparent; }
#siropuChatTabs { margin-top: 0; display: none; }
.siropuChatMessages { background-color: rgb(54, 57, 62); padding: 0; }
.siropuChatMessages > li, .siropuChatPrivateMessages > li { margin: 0; padding-left: 10px!important; border-bottom: 1px solid #333; }
.siropuChatMessages > li .DateTime { background-color: transparent; }
.siropuChatContentLeft { padding: 7px 0 7px 0; }
.siropuChatContentLeft .avatar { display: none; }
.bbCodeImageFullSize { left: 50%; width: inherit!important; max-width: 50%!important; max-height: 50%; }
.siropuChatMeta { padding-right: 10px; }
.DateTime { padding: 0!important; font-size: 12px!important; margin-top: 8%; }
.siropuChatTagged, .siropuChatAdmin .siropuChatTagged { background-color: rgba(255, 127, 80, 0.2)!important; border-radius: 0; padding: 0!important; }
#siropuChatEditor { background-color: rgb(54, 57, 62); border-top: 1px solid #464646; bottom: -30px!important; }
#siropuChatEditor .textCtrl { background-color: rgb(72, 75, 81); }
.textCtrl { border-radius: 5px; border: none; }
#siropuChatBBCode { position: relative; top: -37px; left: 35px; float: left; }
.button { background: transparent; }
.button:nth-child(n+5) { content: ''!important; text-indent: 100%; overflow: hidden; white-space: nowrap; width: 32px; margin-right: 10px; padding-right: 15px; }
.button:nth-child(5)::before { content: '? ' }
.button:nth-child(6)::before { content: '? ' }
.button:nth-child(7)::before { content: '? ' }
.button:nth-child(8)::before { content: '✨ ' }
.button:nth-child(9)::before { content: '? ' }
.button:nth-child(10)::before { content: '? ' }
.button:nth-child(11)::before { content: '? ' }
.button:nth-child(12)::before { content: '? ' }
button.siropuChatLogout { padding-right: 70px!important; text-indent: 100%; display: none; }
#siropuChatSubmit { position: relative; top: -37px; right: 5px; color: #fff; background: rgb(105, 126, 196); border-radius: 5px; }
a.callToAction span { border-radius: 5px; }
.secondaryContent { background: transparent; }
#siropuChatSmilies {
    /**background: #464646**/
    
    ; border-radius: 25px; position: relative; top: -10px; padding: 10px!important; }
#siropuChatSmilies .smilieContainer { overflow: hidden; }
.siropuChatUsers { background-color: rgb(47, 49, 54); width: 250px; float: left; }
.siropuChatUsers > li:first-child::before { width: 90%; content: 'Currently in-chat'; position: absolute; top: 10px; padding-bottom: 5px; border-bottom: 1px solid #c9c4c4; overflow: auto; }
.siropuChatUsers > li:first-child { padding: 30px 30px 0 10px; }
.siropuChatUsers > li { padding: 2px 0 2px 10px; }
.siropuChatUsers .username { padding-left: 10px; position: relative; top: 0; }
.siropuChatUserStatus { position: relative; top: -5px; left: 32px; margin-left: 5px; width: 75%; padding: 2px 0 2px 0; }
.siropuChatHasStatus { margin-bottom: -10px; }
#siropuChat .siropuChatHasStatus .avatar img { top: 17px; }
#siropuChat .avatar img, .avatar .img { width: 24px; height: 24px; margin: 0 auto; top: 10px; }
.avatar img, .avatar .img, .avatarCropper { border-radius: 25px; }
.siropuChatActive, .siropuChatIdle, .siropuChatIdle2 { background: none; }
.siropuChatActive .avatar img { border: 2px solid #2ECC71; }
.siropuChatIdle .avatar img { border: 2px solid #D3D3D3; }
.siropuChatIdle2 .avatar img { border: 2px solid gold; }
.siropuChatWhisperPolice .siropuChatWhisperAction { color: firebrick; }
}

@-moz-document url("https://xenmenu.com/chat/"), url-prefix("https://xenmenu.com/forums/") {
#siropuChat { overflow: visible; }
#siropuChatContent > div { height: 200%; }
#content > .pageWidth > .pageContent { padding-bottom: 80px; }
#content .pageContent::after { height: 0!important; }
#content .pageContent >:last-child { margin-top: 0!important; }
.tabs li a { line-height: 36px!important; }
/**.siropuChatUsers
{ height: 88%; }

.siropuChatMessages
{ height: 80%!important; }**/

.siropuChatMessages { border-left: 1px solid #323232; }
#siropuChatEditor { border: 1px solid #464646; position: relative; bottom: -240px!important; height: 50px; }
#siropuChatEditor .textCtrl { width: 30%; }
.textCtrl .UserTagger::active { width: 80%; }
#siropuChatBBCode { left: 35%!important; }
.secondaryContent { padding: 10px!important; }
#siropuChatSmilies { background: transparent!important; }
#siropuChatSmilies .smilieContainer { width: 100%; position: relative; top: -150px; }
.button { padding: 0 10px!important; height: 36px!important }
#siropuChatSubmit { float: none!important; top: 0!important; right: 35px!important; }
.siropuChatUsers { border-right: 1px solid #464646; }
.breadBoxBottom { display: none; }
.footer .pageContent { margin-top: 20px; }
}

@-moz-document url-prefix("https://xenmenu.com") {
#content .pageContent { background: rgba(0, 0, 0, 0.3); }
/**.secondaryContent { background: rgb(36, 37, 38); }**/
}

@-moz-document url("https://xenmenu.com/") {
.funbox { text-align: left!important; }
#siropuChatBBCode { top: -5px!important; }
}

@-moz-document url("https://xenmenu.com/chat/fullpage") {
#siropuChatNotice { display: none!important; }
.tabs li a { line-height: 16px!important; }
#siropuChatBBCode { float: right!important; left: 0!important; }
.button:nth-child(n+5) { content: ''!important; text-indent: 100%; overflow: hidden; white-space: nowrap; width: 0; margin-right: 10px; padding-right: 15px; }
.button:nth-child(5)::before { content: '? ' }
.button:nth-child(6)::before { content: '? ' }
.button:nth-child(7)::before { content: '? ' }
.button:nth-child(8)::before { content: '✨ ' }
.button:nth-child(9)::before { content: '? ' }
.button:nth-child(10)::before { content: '? ' }
.button:nth-child(11)::before { content: '? ' }
.button:nth-child(12)::before { content: '? ' }
#siropuChatSubmit { position: relative; top: -37px; right: 5px; color: #fff; background: rgb(105, 126, 196); border-radius: 10px; }
}
