css:
@-moz-document regexp("https?://.*\\.wikia\\.com/(.*/)?wiki/.*") {
.wds-global-navigation {
    position: relative;
    margin: 0 auto;
    box-shadow: none;
    border-top: none;
    background: none;
}

.wds-global-navigation-wrapper {
	height: 49px;
}

#WikiaMainContent {
    width: 100%;
}

body.wikia-bar-visible, .WikiaFooter, .wds-global-footer {
    padding: 0;
    margin: 0;
}

.WikiaPageContentWrapper {
	-webkit-box-shadow: none;
	-moz-box-shadow: none;
}

.WikiaPage {
	-moz-box-shadow: none;
	-webkit-box-shadow: none;
    box-shadow: none !important;
}

.WikiaPage .page-header__contribution-buttons > :not(:last-child) {
	margin-right: 0px;
}

.WikiaPage {
    border-bottom: 0;
}

#WikiaNotifications, #WikiaBar, a.wds-global-navigation__link, .wds-global-navigation__wikis-menu, .wds-global-navigation__logo, .wds-global-navigation__start-a-wiki, .wds-global-footer, #WikiaRailWrapper, #mixed-content-footer, #ShareEntryPoint {
    display: none;
    padding: 0px;
    margin: 0px;
}
}
