css:
@-moz-document domain('vk.com')
{
#ts_input{background: #3c3c3c url(https://nmr1.ru/img/poisk.svg) no-repeat; background-position: left 8px; color: #828282;}
.top_audio_play__button{filter:gray;-webkit-filter: grayscale(100%);}
.input_back{color: #8c8e91;}
#page_header_cont .back{background: #2f2f2f !important;border-bottom: 1px solid #424242 !important;}
.top_audio_player:hover, .top_nav_link:hover, .top_nav_btn:hover {background-color: #3c3c3c;}
.top_nav_btn#top_notify_btn .top_nav_btn_icon, .top_nav_btn#top_notify_btn .top_nav_btn_icon:hover{background-image: url(https://nmr1.ru/img/kolokol.svg) !important;}
.top_nav_btn.active, .top_nav_link.active{background-color:#3f3f3f !important}
.top_audio_player .top_audio_player_title{color: #bfbfbf;}
.top_audio_player .savefrom_vk_download{display: none;}
.ts_wrap .input_back_content{color: #b6b6b6;}
.stl_active.over_fast #stl_bg{background-color: #e6e6e6;}
.page_current_info .current_info{color: #939393;}
.button_blue button, .button_gray button, .button_light_gray button, .flat_button{background-color: #656565;}
.button_blue button.hover, .button_blue button:hover, .flat_button.hover, .flat_button:hover {background-color: #2f2f2f;}
.box_controls .flat_button.secondary:hover, .flat_button.secondary.button_light:hover {background-color: #dcdcdc;}
.box_controls .flat_button.secondary, .flat_button.secondary.button_light {color: #616161;}
.left_count_wrap{color: #828282; background: #dcdcdc;}
#side_bar ol li .left_row, .page_add_media, .audio_row__play_btn, .audio_row__performer_title, .audio_page_player_ctrl, .flat_button, .ui_rmenu_count, .ui_actions_menu_item.im-action, ._im_right_menu, .ui_rmenu_pr, .ui_toggler, .stories_feed_preview_add, .stories_feed_preview_author_name{filter:grayscale(94%);}
.story_feed_new_item.stories_feed_preview_item .stories_feed_preview_author .stories_feed_preview_authors_wrapper::after {border:2px solid #fff !important}
#stl_text{color: #969696;}
.chat_onl_inner{background-color: #e4e4e4;}
.chat_tab_wrap:hover{background-color: #bfbfbf;}
.chat_onl {color: #929292;}
.fc_tab_head {background-color: #989898; border: 1px solid #929292;}
.fc_msgs_out .fc_msgs {background-color: #e6e6e6;border: 1px solid #e4e4e4;}
.button_gray button, .flat_button.secondary {background-color: #efefef; color: #777777;}
.button_gray button.hover, .button_gray button:hover, .flat_button.secondary.hover, .flat_button.secondary:hover {background-color: #e8e8e8;}
.ui_actions_menu_item{color: #929292;}
.top_notify_count{border: 1px solid #48494a !important;color:#000;background-color:#c4c4c5 !important;}
.top_nav_btn:hover .top_notify_count{border-color:#9e9e9e;}
.page_actions_wrap{border: 1px solid #d2d2d2;}
.page_actions_header{background: #dcdcdc;}
.page_actions_item{color: #505050;}
.page_actions_item:hover {background-color: #e6e6e6;}
.story_feed_new_item .stories_feed_item_ava{border: 2px solid #6b6b6b;}
.ui_actions_menu{border: 1px solid #cecece;}
.page_counter .count{color: #2b2b2b;}
.nim-dialog.nim-dialog_failed .nim-dialog--unread, .nim-dialog.nim-dialog_unread .nim-dialog--unread, .nim-dialog.nim-dialog_unread.nim-dialog_prep-injected .nim-dialog--unread {background: #757575;}
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_unread.nim-dialog_classic, .nim-dialog:not(.nim-dialog_deleted):hover {background: #f5f5f5;}
.nim-dialog.nim-dialog_classic.nim-dialog_unread-out .nim-dialog--inner-text, .nim-dialog.nim-dialog_classic.nim-dialog_unread-out.nim-dialog_muted .nim-dialog--inner-text {background: #f1f1f1;}
.media_selector .ms_items_more .ms_item {color: #7d7d7d;}
.people_module .module_body a, .reply_author a, .post_author a, .copy_post_author a, .mention_tt_title a{color: #4a3232;}
.post_full_like_wrap, .wall_module .reply .like_wrap, .wall_signed{filter: grayscale(75%)}
.profile_msg_split .profile_btn_cut_left {border-right: 1px solid #737373;}
.wall_module .copy_quote {border-left: 2px solid #e8e8e8;}
.wall_post_more, .wall_reply_more, .wall_copy_more {color: #929292;}
.audio_row.audio_claimed .audio_row__performer_title, .audio_row.audio_claimed .audio_row__play_btn, .audio_row.audio_moder_claimed .audio_row__performer_title, .audio_row.audio_moder_claimed .audio_row__play_btn{opacity:.2}
.box_title_wrap{background-color: #5e5e5e !important;}
.audio_inline_player .slider .slider_amount, .audio_page_player .slider .slider_amount, .audio_inline_player .slider .slider_handler, .audio_page_player .slider .slider_handler{background-color:#969696}
.audio_inline_player .slider .slider_back, .audio_page_player .slider .slider_back{background-color:#dedede}
._audio_section__search .audio_row.audio_has_lyrics .audio_row__title_inner, .ap_layer__content .audio_row.audio_has_lyrics .audio_row__title_inner, .audio_page__audio_rows .audio_row.audio_has_lyrics .audio_row__title_inner, .audio_section_global_search__audios_block .audio_row.audio_has_lyrics .audio_row__title_inner{color:#545454}
.audio_page_player_title_performer a {opacity:.2}
.audio_row .audio_row__performer{color:#000}
.eltt{border: 1px solid #c1c1c1;}
.audio_feed_text a{color:#2d3161;}
.feed_new_posts>b{background-color:#2f2f2f;}
.feed_new_posts{color:#000000;}
}
