    border: none;
    color: #000;
    cursor: pointer;
    height: 30px;
    margin: 0;
    outline: 0;
    vertical-align: top;
}

jhp input[type="submit"]:hover, .sbdd_a input:hover, .gbqfba:hover 
{
    -moz-box-shadow: 0 1px 1px rgba(0,0,0,0.1);
