css:
@-moz-document domain("isis.tu-berlin.de"), domain("shibboleth.tubit.tu-berlin.de") {
#page-footer {
    border-top: 2px solid #444 !important;
}

hr {
    border-top: 1px solid #444;
    border-bottom: 1px solid #555;
}

h1,
h2,
h3,
h4,
h5,
h6,
span,
.not-a {
    color: white !important;
    background-color: transparent  !important;
}

.block .header h2 {
    text-shadow: 0 1px 0 rgba(0,0,0,.5) !important;
}

.block,
.forumpost {
    border-color: #444;
}

.nav-tabs>.active>a, .nav-tabs>.active>a:hover, .nav-tabs>.active>a:focus {
    color: #fff !important;
    background-color: #444;
    border: 1px solid #555;
}

.nav-tabs>li>a:hover, .nav-tabs>li>a:focus {
    border-color: #555 #555 #444;
}

.nav-tabs {
    border-bottom: 1px solid #555;
}

.nav>li>a:hover, .nav>li>a:focus {
    background-color: #444;
}

body,
.layout,
.page,
.course-list,
.content,
.header,
#main,
.main,
.notifications,
.coursebox.even,
table#form td.submit,
.form-buttons, .path-admin .buttons,
#fitem_id_submitbutton,
.fp-content-center form+div,
div.backup-section+form,
#fgroup_id_buttonar,
div[style*="background: #ffffff"] {
    color: white !important;
    background-color: #191919 !important;
}

.box:not(.generalbox),
.block,
.info,
.content,
.section,
table:not(.glossarycategoryheader),
table:not(.glossarycategoryheader) tr,
table:not(.glossarycategoryheader) tbody,
table:not(.glossarycategoryheader) thead,
table:not(.glossarycategoryheader) td,
table:not(.glossarycategoryheader) th,
.maincontent,
.forumpost,
.box-centering,
#home,
#rightheader,
div[style*="background: #fafafa"],
.panel,
.navbar-default,
.TUspeeddial_list,
.well {
    color: white !important;
    background-color: #262626 !important;
}

.TUspeeddial_more {
	border-color: #555;
    box-shadow: 1px 1px 1px #000;
}

.TUspeeddial_list {
	border-color: #555;  
    box-shadow: 4px 4px 6px #000;
}

.TUspeeddial_list, .TUspeeddial_link {
	    opacity: 1.0 !important;   
}

.TUspeeddial_link:hover {
    background-color: #191919;
    border: 1px solid #555;
}

.loginbox .guestsub {
    border-top: 1px solid #444;
}
.coursebox:hover {
    background-color: #222 !important;
}

.block .content h3 {
    text-shadow: 0 1px 0 rgba(0,0,0,.5)
}

a:not(.not-a):not(.btn):not(.ical-link) {
    color: #f24052;
}

.navmeta {
	background-color: #333;   
}

#rightheader img:not(.icon),
a[href*="https://isis.tu-berlin.de/my/"] div,
a[href*="https://www.mydesk.tu-berlin.de/"] div,
a[href*="http://www.studienberatung.tu-berlin.de/"] div,
a[href*="https://matet.isis.tu-berlin.de/"] div,
a[href*="http://www2.lsf.tu-berlin.de/"] div,
a[href*="http://www.moses.tu-berlin.de/"] div {
    filter: hue-rotate(185.5deg) invert(100%) saturate(500%) contrast(200%);
}

a[href*="http://www.tu-sport.de/index.php"] div{
    filter: hue-rotate(180deg) invert(100%) saturate(700%) brightness(90%) contrast(80%);
}

.speech {
    filter: invert(80%) brightness(154%) contrast(180%);
}

#rightheader .avatars img, .defaultuserpic {
    filter: invert(80%) brightness(180%) contrast(180%);
}

#rightheader img.icon {
    filter: hue-rotate(180deg) invert(100%) brightness(200%);
}   

.userpicture {
	background-color: #7e7e7e;   
}


.navbar-fixed-top .navbar-inner, .navbar-static-top .navbar-inner {
    background: #191919 !important;
}

.search-box {
   background-color: #555; 
}

.search-box img {
    filter: brightness(90%);
}

.TUspeeddial_button  {
	filter: brightness(160%);   
}

.jsenabled .usermenu .moodle-actionmenu.show {
    background-color: #444;
    border-radius: 5px;
}

.moodle-actionmenu[data-enhanced].show .menu {
    background-color: #444;
    -webkit-box-shadow: 5px 5px 20px 0 #000;
    -moz-box-shadow: 5px 5px 20px 0 #000;
    box-shadow: 5px 5px 20px 0 #000;
}

.navbar .nav>li>a {
    text-shadow: 0 1px 0 #000;
    color: #f24052 !important;
}

.navbar .nav.pull-right {
    border-left: 1px solid #444 !important;
}

.jsenabled .moodle-actionmenu[data-enhance] .toggle-display.textmenu .caret, #rightheader {
    border-top-color: #888 !important;
}

.navbar-fixed-top .navbar-inner, .navbar-static-top .navbar-inner, .well {
	border-color: #444;   
}

.course-content ul li.section.main {
    border-bottom: 2px solid #444;
}
}
