css:
@-moz-document domain("www.se7ensins.com") {
/*Header Logo*/
#logo a img {
    content: url("http://i.imgur.com/LiZuMgy.png");
}
/*Scrollbar*/
::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
    background-color: #272727;
    border-radius: 10px;
}
::-webkit-scrollbar {
    width: 10px;
    background-color: #272727;
}
::-webkit-scrollbar-thumb {
    background-color: #d3000a;
    border-radius: 10px;
}


/*Sidebar*/
.sidebar .secondaryContent, .xengallerySideBar .secondaryContent {
    border: 1px solid #8c0007;
    border-radius: 3px;
}
.avatar img, .avatar .img, .avatarCropper {
    padding: 2px;
    border: 1px solid #8c0007;
    border-radius: 3px;
}
.sidebar .visitorPanel .username {
    font-weight: bold;
    font-size: 11pt;
    color: #d3000a;
}


/*Membercard*/
.xenOverlay.memberCard .userInfo h3 a {
    color: #d3000a;
}

/*Buttons*/
#newMatchButton {
    padding: 0px;
    margin: 10px 30px;
    border: 1px solid #8c0007;
    border-radius: 5px;
    background: #d3000a;
    background: -moz-linear-gradient(top, #5a9d32 0%, #8c0007 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#d3000a), color-stop(100%,#8c0007));
    background: -webkit-linear-gradient(top, #d3000a 0%,#8c0007 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#8c0007 100%);
    background: linear-gradient(to bottom, #d3000a 0%, #8c0007 100%);
    /*filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#5a9d32', endColorstr#'#8c0007',GradientType=0 );*/
    text-align: center;
    line-height: 30px;
    box-shadow: 0px 2px 5px rgba(0,0,0, 0.2);
    display: block;
    cursor: pointer;
    height: 30px;
}
#newMatchButton:hover .inner {
    text-decoration: none;
    background-color: #8c0007;
}


/*Random*/
.navTabs {
    font-size: 13px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    background-color: rgb(26, 26, 26);
    padding: 0 25px;
    border-top: 6px solid rgb(26, 26, 26);
    border-bottom: 6px solid #8c0007;
    height: 40px;
}
.messageHeading {
    font-size: 13px;
    color: #FFF;
    font-weight: bold;
    background: #d3000a;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#d3000a), color-stop(100%,#8c0007));
    background: -webkit-linear-gradient(top, #d3000a 0%,#8c0007 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #d3000a 0%,#8c0007 100%);
    padding: 9px 10px;
    overflow: hidden;
    border-radius: 3px;
    margin-bottom: 5px;
}
html .navTabs .navTab.PopupOpen .navLink {
    color: rgb(234, 234, 234);
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
    background: #d3000a;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #d3000a), color-stop(100%,#8c0007));
    background: -webkit-linear-gradient(top, #d3000a 0%,#8c0007 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #d3000a 0%,#8c0007 100%);
}
.Menu {
    font-size: 12px;
    border: 0px solid rgb(26, 26, 26);
    border-top: 5px solid #8c0007;
    overflow: hidden;
    box-shadow: 0px 5px 5px rgba(0,0,0, 0.5);
    min-width: 200px;
    position: absolute;
    z-index: 7500;
    display: none;
}
.blockLinksList a:hover, .blockLinksList a:focus, .blockLinksList li.kbSelect a, .blockLinksList label:hover, .blockLinksList label:focus, .blockLinksList li.kbSelect label {
    color: rgb(255, 255, 255);
    text-decoration: none;
    background-color: #d3000a;
}
.messageUserBlock.xbOnlineRegular div.avatarHolder .onlineMarker {
    position: absolute;
    top: 9px;
    left: 27px;
    border: 7px solid transparent;
    border-top-color: #d3000a;
    border-left-color: #d3000a;
    border-top-left-radius: 3px;
}
.button {
    font-style: normal;
    font-size: 12px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(255, 255, 255);
    padding: 0px 5px;
    border: 1px solid #8c0007;
    border-radius: 5px;
    text-align: center;
    outline: none;
    line-height: 26px;
    display: inline-block;
    cursor: pointer;
    box-sizing: border-box;
    box-shadow: 0 -3px 0 rgba(0, 0, 0, 0.06) inset;
    background: #8c0007;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#d3000a), color-stop(100%,#8c0007));
    background: -webkit-linear-gradient(top, #d3000a 0%,#8c0007 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #d3000a 0%,#8c0007 100%);
    height: 28px;
}
.extraFooter {
    font-size: 12px;
    color: rgb(126, 126, 126);
    background-color: rgb(26, 26, 26);
    padding: 15px;
    border-top: 6px solid #8c0007;
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
    border-bottom-right-radius: 10px;
    border-bottom-left-radius: 10px;
    display: block;
    overflow: hidden;
}
a:link, a:visited {
    color: #dedede;
    text-decoration: none;
}
a.callToAction {
    padding: 0px;
    border: 1px solid #8c0007;
    border-radius: 5px;
    background: #d3000a;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#d3000a), color-stop(100%,#8c0007));
    background: -webkit-linear-gradient(top, #d3000a 0%,#8c0007 100%);
    background: -o-linear-gradient(top, #d3000a 0%,#8c0007 100%);
    background: -ms-linear-gradient(top, #d3000a 0%,#8c0007 100%);
    background: linear-gradient(to bottom, #d3000a 0%,#8c0007 100%);
    /*filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#5a9d32', endColorstr='#527c39',GradientType=0 );*/
    display: inline-block;
    line-height: 28px;
    box-shadow: 1px 1px 5px rgba(0,0,0, 0.15);
    outline: 0 none;
    height: 28px;
}
html .navTabs .navTab.selected .navLink {
    position: relative;
    font-weight: bold;
    color: rgb(255, 255, 255);
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
    background: #d3000a;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#d3000a), color-stop(100%,#8c0007));
    background: -webkit-linear-gradient(top, #d3000a 0%,#8c0007 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #d3000a 0%,#8c0007 100%);
    /*filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#5a9d32', endColorstr='#527c39',GradientType=0 );*/
}
.extraFooter h3 {
    font-size: 16px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: #d3000a;
    padding-bottom: 5px;
    margin: 0 auto 7px;
    font-weight: 700;
}
.footer a, .footer a:visited {
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: #d3000a;
    padding: 11px 5px;
    display: block;
}
.XenBase .amsArticleViewItem .main .featured, .XenBase .sidebar .featuredNotice {
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #d3000a 0%,#8c0007 100%);
    /*filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#5a9d32', endColorstr='#527c39',GradientType=0 );*/
    color: #fff;
    border: 1px solid #8c0007;
    font-size: 12px;
    line-height: 1.4;
}
.PageNav a:hover, .PageNav a:focus {
    color: rgb(234, 234, 234);
    text-decoration: none;
    background-color: #d3000a;
    border-color: #8c0007;
}
.PageNav a.currentPage {
    color: rgb(234,234,234);
    background-color: #d3000a;
    border-color: #8c0007;
    position: relative;
}
.xmgCarouselContainer .sectionMain .titleStrip {
    font-size: 14px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(255, 255, 255);
    padding: 9px 10px;
    margin: 3px auto 0;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #d3000a 0%,#8c0007 100%);
    /*filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#5a9d32', endColorstr='#527c39',GradientType=0 );*/
    margin-bottom: 5px;
    border-radius: 5px;
    min-height: 6px;
    margin-bottom: 10px;
}
.nodeList .categoryStrip {
    font-size: 14px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(255, 255, 255);
    padding: 9px 10px;
    margin: 3px auto 0;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #d3000a 0%,#8c0007 100%);
    /*filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#5a9d32', endColorstr='#527c39',GradientType=0 );*/
    margin-bottom: 5px;
    border-radius: 5px;
    min-height: 6px;
}
.node .tinyIcon {
    font-size: 17px;
    color: #d3000a;
    background-color: transparent;
    margin: 1px 4px;
    display: block;
    white-space: nowrap;
    opacity: 0.25;
    width: 12px;
    height: 16px;
}
.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3, .XenBase .xengallerySideBarContainer .xengallerySideBar .section h3 {
    font-size: 14px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: #d3000a;
    background-color: rgb(26, 26, 26);
    padding-top: 7px;
    padding-bottom: 7px;
    padding-left: 10px;
    margin: -3px -3px 5px;
    border-bottom: 3px solid #8c0007;
    border-radius: 3px;
}
.tglSidebar.facss.active .tgl_fa {
    font-size: 14px;
    color: #d3000a;
    margin-top: 4px;
    margin-right: 8px;
}
.secondaryContentCollapsed h3 {
    font-weight: bold;
    font-size: 14px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: #d3000a;
    background-color: rgb(26, 26, 26);
    padding-top: 7px;
    padding-bottom: 7px;
    padding-left: 10px;
    margin: -3px -3px 5px;
    border-bottom: 3px solid #8c0007;
    border-radius: 3px;
}
.tglSidebar.facss.inactive .tgl_fa {
    font-size: 14px;
    color: #d3000a;
    margin-top: 4px;
    margin-right: 8px;
}
.Popup .arrowWidget {
    color: #d3000a;
    background-color: transparent;
    margin-top: -2px;
    margin-left: 2px;
    display: inline-block;
    vertical-align: middle;
    width: 13px;
    height: 14px;
}
.subForumsPopup.Popup .PopupControl.PopupOpen {
    background-image: none;
    color: #d3000a !important;
}
.XenBase .subForumsPopup .PopupOpen .dt {
    color: #d3000a;
}
.discussionList .sectionHeaders {
    font-size: 14px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(255, 255, 255);
    padding: 9px 10px;
    margin: 3px auto 0;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #d3000a 0%,#8c0007 100%);
    /*filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#5a9d32', endColorstr='#527c39',GradientType=0 );*/
    padding: 0;
}
#DiscussionListOptionsHandle a {
    font-size: 12px;
    color: #d3000a;
    background-color: rgb(26, 26, 26);
    padding: 0 20px;
    margin: 0 auto;
    border: 1px solid rgb(26, 26, 26);
    border-top: 1px none black;
    border-bottom-right-radius: 10px;
    border-bottom-left-radius: 10px;
    display: inline-block;
    outline: 0;
    position: relative;
    right: -50%;
}
.thread_view .threadAlerts dt {
    font-weight: bold;
    color: #d3000a;
    display: inline;
}
.bbCodeBlock .type {
    font-size: 12px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: #d3000a;
    background-color: rgb(26, 26, 26);
    padding: 7px 8px;
    margin-bottom: 5px;
    border-radius: 5px;
}
.ugc a:link, .ugc a:visited {
    color: #d3000a;
    padding: 0 3px;
    margin: 0 -3px;
    border-radius: 5px;
}
.xenOverlay .formOverlay .heading {
    font-weight: bold;
    font-size: 14px;
    color: rgb(247, 247, 247);
    background-color: rgb(104, 159, 16);
    padding: 7px 10px;
    margin-bottom: 10px;
    border: 1px solid rgb(31, 31, 31);
    border-radius: 5px;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #d3000a 0%,#8c0007 100%);
    /*filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#5a9d32', endColorstr='#527c39',GradientType=0 );*/
}
.chosen-container .chosen-results li {
    display: none;
    margin: 0;
    list-style: none;
    -webkit-touch-callout: none;
    color: #d3000a;
    padding: 5px 10px;
    display: block;
    outline: 0 none;
}
a.callToAction:active span {
    background-color: #d3000a;
}
a.callToAction:hover span {
    background-color: #8c0007;
}
.breadcrumb .crust .arrow {
    border: 12px solid transparent;
    border-right: 1px none black;
    border-left-color: #8c0007;
    -moz-border-right-colors: rgb(82, 125, 57);
    display: block;
    position: absolute;
    right: -12px;
    top: 0px;
    z-index: 50;
    width: 0px;
    height: 0px;
}
.breadcrumb .crust:hover a.crumb {
    color: #d3000a;
    background-color: rgb(22, 22, 22);
}
.navTabs .navTab.selected .tabLinks a:hover, .navTabs .navTab.selected .tabLinks a:focus {
    color: #d3000a;
    text-decoration: none;
    background: url(http://www.se7ensins.com/rgba.php?r=0&g=0&b=0&a=0);
    background: rgba(0, 0, 0, 0);
    /*_filter: progid:DXImageTransform.Microsoft.gradient(startColorstr=#00000000,endColorstr=#00000000);*/
    border-width: 0px;
    outline: 0;
}
.statusEditorCounter {
    color: #d3000a;
}
.Popup .PopupControl.PopupOpen, .Popup.PopupContainerControl.PopupOpen {
    color: #d3000a;
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
    border-bottom-right-radius: 0px;
    border-bottom-left-radius: 0px;
}
.Popup .PopupOpen .arrowWidget:before {
    content: "\f077";
    color: #d3000a;
}
.bubbleLinksList a:hover, .bubbleLinksList a:active {
    color: rgb(255, 255, 255);
    text-decoration: none;
    background-color: #d3000a;
}
.Popup .arrowWidget:before {
    color: #8c0007;
    content: "\f078";
    display: block;
    font-family: FontAwesome;
    line-height: 13px;
    margin-left: 2px;
}
.blockLinksList a, .blockLinksList label {
    color: #dedede;
    padding: 5px 10px;
    display: block;
    outline: 0 none;
}
.node .forumNodeInfo .nodeIcon,
.node .categoryForumNodeInfo .nodeIcon {
    background-image: url('https://i.imgur.com/IAJ6gnq.png');
    background-repeat: no-repeat;
    background-position: 0 0;
}
.node .forumNodeInfo.unread .nodeIcon,
.node .categoryForumNodeInfo.unread .nodeIcon {
    background-image: url('http://i.imgur.com/NiNtuEh.png');
    background-repeat: no-repeat;
    background-position: -36px 0;
}
}
