css:
@-moz-document domain("forums.digitalspy.com") {
body {
    background-color: #333333;
}
.nav-sticky {
    background-color: #444444;
    color: #FFFFFF;
}
a {
    color: #1dc7ad !important;
}
a:hover {
    color: #0a877d !important;
}
body h1,
body h2,
body h3,
body h4,
body h5,
body h6,
span {
    color: #FFFFFF !important;
}
.ButtonGroup a {
    color: #FFFFFF !important;
}
.H {
    color: #FFFFFF !important;
}
.P {
    background-color: #444444 !important;
    color: #CCCCCC;
}
.DataTable {
    background-color: #444444;
    color: #FFFFFF;
}
section .page-content .DataTable tbody tr {
    background-color: #444444;
}
section .page-content .DataTable tbody tr:hover {
    background-color: #333333 !important;
}
.DataTable .Alt {
    background-color: #555555 !important
}
.DataTable .Alt:hover {
    background-color: #4F4F4F !important
}
.ItemPoll,
.markering {
    display: none;
}
section .page-content .CategoryGroup .DataTable thead td,
section .page-content .DataTableWrap .DataTable thead td {
    background-color: #888888 !important;
    color: #CCCCCC !important;
}
section .page-content .CategoryGroup .DataTable thead td,
section .page-content .DataTableWrap .DataTable thead td {
    background-color: #444444 !important;
}
.DataTableWrap .DiscussionsTable thead tr {
    background-color: #444444 !important;
}
.PageTitle {
    color: #FFFFFF;
}
.ItemDiscussion {
    background-color: #444444 !important;
    color: white;
}
.MessageList.Discussion .ItemDiscussion:before {
    background-color: #555555;
    color: white;
}
.DiscussionHeader,
.CommentHeader {
    background-color: #555555 !important;
    border-top: 1px solid #444444 !important;
}
.ActionBlock,
.Conversation .Item .MItem,
.Conversation .Item .MItem a,
.Gloss,
.Item .AuthorInfo,
.Item .Meta,
.Item .Meta a,
.OptionsMenu .SpFlyoutHandle,
.text-muted {
    color: #CCCCCC !important;
}
.CommentHeading {
    display: block;
}
.Comment {
    background-color: #444444 !important;
}
.H {
    background-color: #333333 !important;
}
.CommentHeader {
    padding-top: 20px !important;
    border-top: 41px solid #444444 !important;
}
.Message {
    color: white;
}
.MessageForm,
.CommentForm,
.Buttons {
    border-radius: 0px;
    background-color: #444444 !important;
}
.Buttons {
    border-top: 1px solid #333333 !important;
}
.MessageForm,
.CommentForm {
    border: 1px solid #222222;
}
.WhoIs a:hover,
.MeMenu a:hover {
    background-color: rgba(0, 0, 0, 0.2) !important;
}
.FlyoutMenu {
    background-color: rgba(50, 50, 50, 0.9);
    color: white !important;
    -webkit-box-shadow: 4px 4px 6px 0px rgba(0, 0, 0, 0.3);
    -moz-box-shadow: 4px 4px 6px 0px rgba(0, 0, 0, 0.3);
    box-shadow: 4px 4px 6px 0px rgba(0, 0, 0, 0.3);
}
.Flyout {
    background-color: rgba(50, 50, 50, 0.9);
    color: white !important;
    -webkit-box-shadow: 4px 4px 6px 0px rgba(0, 0, 0, 0.3);
    -moz-box-shadow: 4px 4px 6px 0px rgba(0, 0, 0, 0.3);
    box-shadow: 4px 4px 6px 0px rgba(0, 0, 0, 0.3);
}
.Flyout li a:hover {
    background-color: rgba(40, 40, 40, 0.6);
}
.DataList .Item {
    border-color: #222222 !important;
}
.DataList .Foot:first-child,
.DataList .Item:first-child,
.DataList.Activities .CommentForm:first-child,
.PanelInfo li:first-child a,
.list-group-item:first-child {
    border-radius: 0px !important;
}
.DataList .Foot:last-child,
.DataList .Item:last-child,
.DataList.Activities .CommentForm:last-child,
.PanelInfo li:last-child a,
.list-group-item:last-child {
    border-radius: 0px !important;
}
.About.P {
    background-color: #333333 !important;
}
.CategoryBox,
.CategoryGroup,
.DataTableWrap,
.Empty,
.MessageForm,
.MessageList .Item,
.NoResults,
.Tiles .Tile,
.panel {
    background-color: #444444 !important;
}
section .page-content .Pager.NumberedPager {
    background-color: #444444;
}
section .page-content .Pager.NumberedPager .Previous {
    background-color: #555555;
}
section .page-content .Pager.NumberedPager a {
    background-color: #444444;
}
section .page-content .Pager.NumberedPager .Highlight {
    color: #0da79d !important;
}
section .page-content .Pager.NumberedPager a,
section .page-content .Pager.NumberedPager span,
section .page-content .Pager.NumberedPager .QuickSearch {
    background-color: #444444;
}
.QuickSearchButton {
    color: white !important;
}
.QuickSearchWrap {
    background-color: rgba(50, 50, 50, 0.7);
    border: 1px solid #444444;
}
.QuickSearchWrap button {
    line-height: 2px;
}
.Comment .Options {
    background-color: #555555 !important;
}
.CommentMeta .DateCreated {
    position: relative;
    top: 0px;
}
.Item .Comment .Options {
    display: none;
}
.Item .Comment {
    border: 1px solid black !important;
    -webkit-box-shadow: 4px 4px 6px 0px rgba(0, 0, 0, 0.3);
    -moz-box-shadow: 4px 4px 6px 0px rgba(0, 0, 0, 0.3);
    box-shadow: 4px 4px 6px 0px rgba(0, 0, 0, 0.3);
}
.Item .Item-Header {
    border: 0px !important;
}
.ItemComment.Mine .Comment {
    border: 1px solid #0a877d !important;
    border-radius: 0px !important;
}
.ItemComment.Mine .Comment .Options {
    border-top: 1px solid #0a877d !important;
    border-right: 1px solid #0a877d !important;
    border-bottom: 1px solid #444444 !important;
    width: calc(100% - 1px) !important;
    display: block;
}
.ItemComment.Mine .Comment .Item-Header {
    padding-top: 50px !important;
}
.FilterMenu li>a:focus,
.FilterMenu li>a:hover,
.MeBox .FlyoutButton:focus,
.MeBox .FlyoutButton:hover,
.MeBox .Username:focus,
.MeBox .Username:hover,
.nav>li>a:focus,
.nav>li>a:hover {
    background-color: transparent !important;
    border-color: #0da79d !important;
}
.CrumbLabel.Last a span {
    color: #0da79d !important;
    text-decoration: none;
}
.CategoryGroup {
    margin-top: 20px;
    border-top: 1px solid white !important;
}
.CategoryGroup .H {
    background-color: rgba(40, 80, 100, 0.3) !important;
}
.CategoryGroup .H a {
    color: #1dc7ad !important;
}
.HomepageTitle {
    width: 100%;
    margin-bottom: 0px !important;
    padding-bottom: 20px !important;
}
textarea {
    background-color: #333333 !important;
    border-radius: 0px !important;
    color: white !important;
    border-color: #666666 !important;
}
.TextBoxWrapper .token-input-list {
    background-color: #333333 !important;
    border-radius: 0px !important;
    color: white !important;
    border-color: #666666 !important;
}
input[type='text'] {
    background-color: #333333 !important;
    border-radius: 0px !important;
    color: white !important;
    border-color: #666666 !important;
}
.FormWrapper .Info {
    color: white;
}
.FormWrapper .P label {
    padding-left: 5px;
    position: relative;
    top: 3px;
}
.token-input-dropdown {
    background-color: rgba(50, 50, 50, 0.9) !important;
    -webkit-box-shadow: 4px 4px 6px 0px rgba(0, 0, 0, 0.3) !important;
    -moz-box-shadow: 4px 4px 6px 0px rgba(0, 0, 0, 0.3) !important;
    box-shadow: 4px 4px 6px 0px rgba(0, 0, 0, 0.3) !important;
}
.token-input-dropdown p {
    color: white !important;
}
.token-input-dropdown ul li {
    background-color: rgba(50, 50, 50, 0.9) !important;
    color: lightgray;
}
.Item.Read td:first-child a {
    color: #AAAAAA !important;
}
.Item td:first-child a {
    color: white !important;
}
.Item.Participated td:first-child .Options:before {
    font-family: FontAwesome;
    font-size: 14px;
    line-height: 1.42857143;
    content: "\f003";
    color: #1aa79d;
    margin-right: 4px;
}
.Item.Read.Participated td:first-child .Options:before {
    font-family: FontAwesome;
    font-size: 14px;
    line-height: 1.42857143;
    content: "\f2b7";
    color: white;
}
}
