css:
@-moz-document url-prefix("https://archlinux.org.ru/"), url-prefix("http://archlinux.org.ru/") {
/* Author - Haron Prime
License WTFPL Â© 2017 http://www.wtfpl.net/ */

body {
    background-color: #252525 !important;
	color: #aaa !important;
	line-height: 1.3 !important;
}
.topic-list>tbody>tr:nth-child(odd)>td, .category-table>tbody>tr:nth-child(odd)>td, .topic-list>tbody>tr:nth-child(odd)>th, .category-table>tbody>tr:nth-child(odd)>th, .post-info, .post thead, .post tfoot, .block .header, .block .info, .navbar-nav>li>.dropdown-menu, textarea {
    background-color: #222 !important;
}
.navbar, .navbar-inverse, .navbar-inverse .navbar-collapse.wrapper  {
    position: fixed !important;
	top: 0px !important;
    z-index: 999 !important;
    width: 100% !important;
    min-height: 0px !important;
    background-image: none !important;
    border: none !important;
    background-color: #222 !important;
    box-shadow: 0px 6px 12px rgba(0,0,0,0.5) !important;
    font-size: 0.8em !important;
}
.navbar .nav li a {
	color: #959595 !important;
}
.navbar .nav li a:hover {
	color: #7bd !important;
}
.navbar .nav .active a {
	background: #222 !important;
	color: #7bd !important;
	font-weight: bold !important;
}
.dropdown-menu>li>a:hover, .dropdown-menu>li>a:focus {
	background-image: none !important;
	background-color: #222 !important;
}
.input-group {
	top: -2px !important;
}
.input-group .form-control:first-child, .input-group-btn > .btn-default {
    height: 20px !important;
    font-size: 12px !important;
    vertical-align: middle !important;
    margin-top: 6px !important;
    padding-top: 0px !important;
    padding-bottom: 2px !important;
}
.breadcrumb {
    padding: 55px 8px 0px 8px !important;
	margin-bottom: 10px !important;
    background: inherit !important;
	font-size: 1.2em !important;
}
.breadcrumb a {
	color: #3a87ad !important;
}
.nav>li>a>img {
	content: url('data:image/png;base64,') !important;
}
.table.topic-list {
	margin-bottom: 10px !important;
}
.table>thead>tr>th, .table>tbody>tr>th, .table>tfoot>tr>th, .table>thead>tr>td, .table>tbody>tr>td, .table>tfoot>tr>td {
	line-height: 1.3 !important;
}
.forum h1 {
/* 	font-size: 1.1em !important;
	border: none !important;
	color: #aaa !important;
	font-weight: bold !important; */
	display: none !important;
}
.label-info {
    background-color: #333 !important;
    color: #3a87ad !important;
    border: solid 1px #555 !important;
}
.label-danger {
    background-color: #333 !important;
    color: #d9534f !important;
    border: solid 1px #555 !important;
}
.forum .text-center .pagination {
	margin: 10px 0 !important;
}
.pagination>li>a, .pagination>li>span{
    line-height: 1.2 !important;
    font-size: 0.8em !important;
    margin: 8px 0 !important;
}
.pagination>.active>a, .pagination>.active>span, .pagination>.active>a:hover, .pagination>.active>span:hover, .pagination>.active>a:focus, .pagination>.active>span:focus {
    color: #7bd !important;
    background-color: #555 !important;
    border-color: #555 !important;
}
a:hover, a:focus {
    color: #7bd !important;
	text-decoration: none !important;
}
.btn-default, .btn-primary, .btn-success, .btn-info, .btn-warning, .btn-danger {
    line-height: 1.2 !important;
    box-shadow: none !important;
    background-image: none !important;
    background-color: #333 !important;
    border: solid 1px #555 !important;
	font-size: 0.8em !important;
}
.btn-primary:hover, .btn-primary:focus, .btn-default:hover, .btn-default:focus, .btn-success:hover, .btn-success:focus, .btn-info:hover, .btn-info:focus, .btn-warning:hover, .btn-warning:focus, .btn-danger:hover, .btn-danger:focus, .btn-logout:hover, .btn-logout:focus {
	background-color: #222 !important;
	color: #7bd !important;
}
.btn-group.top-page-controls  {
	margin-bottom: 20px !important;
}
pre {
    color: #959595 !important;
    background: #222 !important;
    border: 1px solid #555 !important;
}
blockquote {
	background: #333 !important;
    border-left: 3px solid #959595 !important;
}
.form-control {
    background-color: #333 !important;
    color: #aaa !important;
}
.post-signature {
	font-size: 0.8em !important;
	line-height: 1.2 !important;
}
.markItUpPreviewFrame {
	padding: 5px !important;
	background-color: #333 !important;
	border: 1px solid #555 !important;
}
.copyright {
	font-size: 0.8em !important;
}
@media (max-width: 980px) {
	.navbar-nav>li>a[href="https://archlinux.org/download/"], .navbar-nav>li>a[href="/donate/"] {
		display: none !important;
	}
}
}

@-moz-document url-prefix("https://cse.google.ru"), url-prefix("http://cse.google.ru") {
body, #cse-boby, #cse, .cse .gsc-control-cse, .gsc-control-cse, .gsc-preview-reviews, .gsc-control-cse .gs-snippet, .gsc-control-cse .gs-promotion em, .gsc-control-cse .gs-snippet, .gsc-control-cse .gs-promotion em, .gsc-above-wrapper-area-container, .gsc-result-info {
    background: #222 !important;
    color: #aaa !important;
}
.cse .gsc-control-cse, .gsc-control-cse, .gsc-webResult.gsc-result, .cse .gsc-webResult.gsc-result:hover, .gsc-webResult.gsc-result:hover, .gsc-webResult.gsc-result.gsc-promotion:hover, .gsc-results .gsc-imageResult-classic:hover, .gsc-results .gsc-imageResult-column:hover {
    border: 1px solid #555 !important;
}
.gsc-input-box {
	background: #333 !important;
	border: 1px solid #555 !important;
}
.gsc-input, .gsc-control-cse .gsc-option-menu-container, .gsc-selected-option-container, .gsc-control-cse .gsc-option-menu {
	background: #333 !important;
	color: #aaa !important;
}
.gsc-option-menu-item-highlighted {
	background: #454545 !important;
	color: #ddd !important;
}
.gsc-orderby-label.gsc-inline-block, .gsc-results .gsc-cursor-box .gsc-cursor-page, .lockup-brand {
	color: #aaa !important;
}
.gsc-above-wrapper-area {
	border-bottom: 1px solid #555 !important;
}
.gsc-control-cse .gs-spelling, .gsc-control-cse .gs-result .gs-title, .gsc-control-cse .gs-result .gs-title *, .gsc-results .gsc-cursor-box .gsc-cursor-current-page {
    color: #7bd !important;
}
}
