css:
@-moz-document domain("darkwebs.ws") {
.discussionList li.discussionListItem:nth-child(odd) {
  background-color: rgb(50, 52, 56);
}.discussionListItem, #QuickSearchQuery {
  background-color: rgb(20,21,23);
  border-bottom: 1px solid rgb(2,149,238);
}.lastPostInfo a.avatar {
  display: none;
}.main a.postDate {
  display: none;
}.discussionList .sectionHeaders .stats a {
  float: unset;
  height: 15px;
}dd.stats {
  padding: 0 0 10px;
}.PageNav nav a {
  border: solid 1px rgb(2,149,238);
  border-radius: 3px;
  color: rgb(237,237,237);
  padding: 3px !important;
}.PageNav .scrollable {
  height: 30px;
}body .itemPageNav a {
  color: rgb(237,237,237);
  background-color: rgb(18,18,18);
  border: 1px solid rgb(2,149,238);
  padding: 1px 5px;
}.LoggedIn .discussionListItem .unreadLink:before, .LoggedIn .discussionListItem .ReadToggle.unread:before {
  color: rgb(2,149,238);
  font-family: FontAwesome;
  font-weight: normal;
  margin-right: 5px;
  content: "\f111";
  font-size: 9px;
}.dark_postrating_thread_rating {
  float: right;
  font-family: 'Trebuchet MS',Helvetica,Arial,sans-serif;
  color: rgb(237,237,237);
  font-size: 11px;
  margin-left: 10px;
}.dark_postrating_outputlist li strong, .dark_postrating_thread_rating strong {
  font-weight: bold;
  color: rgba(221,221,221,1);
}.xbBoxed .Banner {
  display: none;
}#Offline_Status_GX, #Online_Status_GX, .dark_postrating_negative, .dark_postrating_positive {
  background-color: white;
  padding: 1px 3px;
  text-shadow: 0px 1px #000;
  border-radius: 3px;
}#Online_Status_GX {
  color: #ffffff;
  background-color: #09af16;
}#Offline_Status_GX {
  color: #ffffff;
  background-color: #fb0909;
}a.item.control.lich {
  background-color: green;
  padding: 1px 7px;
  color: white;
  border-radius: 5px;
}a.item.control.lich font {
  color: white !important;
}
}
