@-moz-document url-prefix("https://2sucres.org") {
/* ==UserStyle==

@name Fixed 2sucres
@version 1.54
@description Divers fixes pour 2sucres
@author Chef aka FoutreAuMiel

==UserStyle== */

.message-avatar-container,
.message-avatar,
.message-avatar.ng-scope
{
    border: none !important;
    background: none !important;
}

md-list-item .md-no-style.md-button{
    line-height: 15px;
}

md-list-item div.md-button.md-no-style
{
    padding-right: 0 !important;
}

div.md-secondary-container button:hover
{
    background-color: #a55 !important;
}

div.col-lg-4.col-md-3.col-xs-12.no-pr md-list md-list-item:nth-child(2n+1)
{
    background-color: rgb(40, 46, 53);
}
/*Couleur des liens*/

div.message-content a
{
    color: rgb(0, 107, 215)!important;
}

div.message-content a:visited
{
    color: rgb(50, 107, 180)!important;
}

div.message-content a:hover
{
    color: rgb(255, 64, 0)!important;
}
/*les messages pinned qui n'etait pas premier avec le dark theme n'avait pas du bordure verte*/

article.message-pinned
{
    border-color: rgb(43, 132, 10)!important;
}
/*risze des areas pour les message : posts et en MP*/

textarea
{
    resize: vertical !important;
}

#message_input
{
    min-height: 120px !important;
    /*taille minimum de la zone des message*/
}

div.risibank-stickers-container.smoothy-2.col-xs-12
{
    overflow: auto;
}

div.risibank-stickers-container.smoothy-2.col-xs-12 img
{
    margin: 7px 2px 2px 2px!important;
    border: 1px solid transparent;
    height: 44px;
    /*width46px;*/
}

div.risibank-stickers-container.smoothy-2.col-xs-12
{
    padding: 0;
}

div.risibank-stickers-container.smoothy-2.col-xs-12 img:hover
{
    border: 1px solid black;
    height: 44px;
    /*width: 46px;*/
}

div.form-group.pl-1.mr-1
{
    margin-top: 2px;
}

div.risibank-stickers-container.smoothy-2.col-xs-12 a span img
{
    height: 14px !important;
    width: 14px!important;
}

div.risibank-stickers-container.smoothy-2.col-xs-12 a span img:hover
{
    height: 14px!important;
    width: 14px!important;
}
/*badges sur le profile*/

md-list-item .md-list-item-inner img.md-avatar
{
    border-radius: 0px!important;
}
/*empeche le decalage des topic lors de l'auto refresch*/

.refresh-topic-list-btn
{
    position: absolute;
    left: 75px;
}

div.risibank-stickers-container.smoothy-2
{
    min-height: 120px;
    /*taille minimum de la zone des stikers*/
}

div.risibank-stickers-container.smoothy-2
{
    height: 0;
    overflow: auto;
    resize: vertical;
    max-height: 380px;
}

div.risibank-stickers-container.smoothy-2.col-xs-12
{
    bottom: 15px;
    padding-top: 15px;
    min-height: 125px;
    width: 100%;
    height: 100%;
    resize: none;
}
/*clique molette*/

.list-topics > tbody > tr > td
{
   
   box-sizing : content-box !important;
    padding : 0px!important;
    border : 0 !important;
    margin : 0 !important;
}
.list-topics > tbody > tr > td.topic-name
{
   
  
    padding : 0px!important;
    border : 0 !important;
    margin : 0 !important;
}
.list-topics > tbody > tr > td div.topic-name-truncate
{
   /* background-color:rgba(255,0,0,0.1);  /**/
    min-height: 21px;
    max-height: 28px;
    
   
   
    text-align : left;
    
  
   
}

a.topic-name-truncated.ng-binding
{
   /*background-color:rgba(0,255,0,0.1); /*zonne active*/
    
    padding-top: 2px;
    padding-bottom: 1px;
    padding-left: 2px!important;
    margin : 0;
    border : 0;
    
    box-sizing : border-box;
    
    min-height: 21px;
    max-height: 28px;
    max-width : 100%;
    
    display: block;
}

.list-topics > tbody > tr > td div.topic-name-truncate span
{
    
    position: relative;
    top: -33px;
    
}
/*Block de code sombre*/
.message-content pre{background : rgb(34, 39, 47);; color : #eee}
/*Video YT plus grand ***en construction*** */

#message-container div.ng-scope div.message-content p.no-mb iframe{
max-width : 100%;
    
}
/*header qui saute au scroll*/
nav.navbar.yamm.ms-navbar.ms-navbar-primary.navbar-static-top{
	margin : 0 !important;
}
#forum-container{margin-top : 0px!important;}

/*boutton sur les postes et signature caché sauf au survole du poste*/
#message-container > div > article{
padding : 10px 10px 0px 10px;
    min-height : 0px !important;
}
#message-container > div > article .message-content{

    min-height : 40px !important;
}

#message-container > div > article > div.message-wrapper> footer.message-footer {
border-top : 15px solid transparent;
    position : relative;
    box-sizing : border-box;
    max-height: 5px !important;
     
   transition-delay : .2s !important;
   opacity : 2;
    overflow : hidden;
    transition :  .5s
}
#message-container > div> article > div.message-wrapper> footer.message-footer:hover{
    max-height : 100px !important;
   opacity : .9;
                                            
   transition-delay : .2s !important;
    transition :  1s
}


/*block 1*/

#message-container > div > article > div.message-wrapper .message-header-detail-container > span,
#message-container > div > article > div.message-wrapper header.message-header .message-header-content .message-username a.message-quote{

    opacity: .05;
    transition: 0.2s
}
/*survole d'un message*/

#message-container > div > article > div.message-wrapper:hover > header .message-header-detail-container >span,
#message-container > div > article > div.message-wrapper header.message-header:hover .message-header-content .message-username a.message-quote{
    opacity: 1;
    transition: 0.5s
}


#message-container > div > article > div.message-wrapper .message-header-detail-container md-icon:hover {
    color: white !important;
    transition: 0.1s
}
/*top table*/
table th.topic-name, table th.topic-date,table th.topic-user{padding-left : 0 !important;}

/*largeur d'ecrant minimum pour ne pas passer au format mobile*/

#forum-container{width : 85%; max-width :1200px; padding : 0;}
#forum-right-col{width : 33% !important ; padding-left: 15px;}
.col-lg-8.col-md-9.col-xs-12.no-pr.no-pl {
    width: 66% !important;
    min-width : 610px;
 
    /*max-width: 66% !important;*/
}
@media only screen  and (max-width : 1100px) {
     /* .col-lg-8.col-md-9.col-xs-12.no-pr.no-pl {
        background : green;
    }*/
    table td.topic-user.sm-hide{
       
       max-width:100px;
                                            
        overflow:hidden;
       
    
      }
}
@media only screen  and (min-width : 940px) {
    /*.col-lg-8.col-md-9.col-xs-12.no-pr.no-pl {
        background : green;
    }*/
    /*table topics hauteur des lignes*/
    
    /*pseudo trop long*/

 table.list-topics tbody tr td.topic-user{
	
              overflow : hidden;
              display : block !important;
              word-break: keep-all !important;
              max-width : 110px;
            
              width : 110px;
}
    
table.list-topics tbody tr  *{
    min-height : 0px !important;
	height : 21px !important;
   line-height: 15px;
}/*https://2sucres.org/message/599902*/
#forum-container #message-container > div{
    position : relative;
    left : 15px!important;
    max-width : 97%
                                       
}
    table td.topic-user.sm-hide{
        display :block; 
       /*max-width:0;*/
        overflow:hidden;
        width : auto!important;
    
      }
    table th.topic-user.sm-hide{
       display :block; padding-left : 0 !important;
       
       }
    table.list-topics > thead > tr{border-bottom : 2px solid white !important;}
    table.list-topics > thead > tr > th{border : none}
    table span.sm-show{
       display : none !important; 
      
     }
    #forum-right-col{width : 31% !important ; padding-left: 15px; float : left !important; }
   #forum-container{width : 95%;}
}

@media only screen and (max-width: 940px) {
    .col-lg-8.col-md-9.col-xs-12.no-pr.no-pl {
       /* background : red;*/
        width: 100% !important;
    }
    /*table topics hauteur des lignes*/
table.list-topics tbody tr > *{
    min-height : 0px !important;
	height : 10px !important;
   line-height: 10px;
}
    table.list-topics tbody tr td.topic-name span{ position : relative !important; bottom : 50px !important; height : 15px !important; display:block !important; line-height : 24px;}
    .list-topics > tbody > tr > td.topic-name
{
    max-width: 416px;
    max-height: 28px;
    padding : 0px 0px 0px 0px !important;
	}
     #forum-container{width : 100%;}
 #forum-right-col{width : 100% !important ; padding-left: 0px; float : right !important;  }
}


/*rendering smileys*/
#message-container article div.message-content p img.smile.no-pb{
	image-rendering: optimizeSpeed  ;
}


#forum-right-col md-list-item{min-height : 36px; height : 36px;}

/*Agencement des badges sur profile*/
.profile-content aside.profile-section{
	width : 100%
}
.profile-content aside.profile-section:first-child > div ul li > div.profile-info-lib{
	width : auto;
    float : left
}
.profile-content aside.profile-section:last-child md-list {
	width : 100%;
    display : flex;
    flex-direction: row ;
    justify-content: space-between;
    flex-wrap:wrap;
}


.profile-content aside.profile-section:last-child md-list md-list-item{
	width : 25%;
    min-width : 350px;
     word-break: break-all;
}
.profile-content aside.profile-section:last-child md-list md-list-item .md-list-item-inner > .md-list-item-text{
  
	word-break: break-all;

    display : inline;
}
.profile-content aside.profile-section:last-child md-list md-list-item .md-list-item-inner > .md-list-item-text h4{
  
	word-break: break-all;

    display : inline;
}
/*padding  topic table topic*/
#forum-container{
padding-top : 20px !important;

}

/*croix topic fav et abonement*/
.md-secondary-container  button{
    padding : 0px !important;
    height : 30px !important;
    min-height : 30px !important;
      width : 30px !important;
    min-width : 30px !important;
    margin-right : 2px !important;
    background : rgb(170, 85, 85);
    position : absolute;
    right : 0px;
    	opacity : 0;
    transition : .2s;
   
}

md-list md-list-item._md-button-wrap:hover .md-secondary-container  button, md-list md-list-item._md-button-wrap .md-secondary-container:hover  button *{
   color : red !important;
    	opacity : 0.8;
    transition : .2s;
   
}


/*https://2sucres.org/message/599902*/
#forum-container #message-container > div{
    position : relative;
    left : 15px!important;
    max-width : 97%
                                       
}
 
