     --shade1-bg: #e69c53;
     --shade2-bg: #f7a859;
     --shade3-bg: #444;
 }


 @font-face {
    font-family: "Raleway";
    src: url(https://fonts.googleapis.com/css?family=Raleway) format("truetype");
}

 a:-webkit-any-link {
    color: #FFF;
    cursor: pointer;
    text-decoration: underline;
}

input, textarea {
    /*-webkit-appearance: textfield;*/
    background-color: #EEE;
    /*-webkit-rtl-ordering: logical;
    cursor: text;
    padding: 1px;
    border-width: 2px;
    border-style: inset;
    border-color: initial;
    border-image: initial;*/
}

i.fa-plus, i.fa-minus{
    display: none;
}
#livetable{
    font-size: 14px;
    padding:5px;
}
#livetable td em.dollar{
    color: #00C467;
}
#live div.type-forum, #livetable tr.type-forum td{
    background-color: #00C467
}
#live div.type-comp, #livetable tr.type-comp td{
    background-color: #e69c53
}
#forum > table th{
    background-color: #AAA;
