}

span.arrow {
    margin: 2px 0px 0px 0px;
    width: 100%;
    height: 14px;
    display: block;
    cursor: pointer;
    background-position: center center;
    background-repeat: no-repeat;
    width: 15px;
    margin-left: auto;
    margin-right: auto;
    outline: none;
}

span.arrow.up {
    position:relative;
}    
span.arrow.up:before {
    content: "\f106";  /* this is your text. You can also use UTF-8 character codes as I do here */
    font-family: FontAwesome;
    /*left:-10px;*/
    position:absolute;
    top:0;
 }

span.arrow.down{
    margin: 0 auto;
    background: url(http://b.thumbs.redditmedia.com/uByeV_kEFe16WfgXycLFaWs5dGDM-hzFwzeyWaJ5Occ.png) no-repeat;
    background-position: 0px -1647px;
    background-repeat: no-repeat;
}

 a {
    color: #FFF;
    text-decoration: none;
}
a:hover {
    color: #FFF;
    text-decoration: underline;
}
a.badge {
    background-color: #444;
    border: #696969 solid 1px;
    border-radius: 4px;
    color: #fff;
    display: inline-block;
    line-height: 24px;
    padding: 0 4px !important;
    white-space: nowrap;
}
a.badge:hover {
    background-color: #666;
    border-color: #666;
    text-decoration: none;
}
a.btn {
    display: inline-block;
    text-align: center;
	color:#FFF;
}
a.emailaddr {
