    color: #FFF;
    font-family: 'Raleway', sans-serif;
    font-size: 14px;
    margin: 0;
    padding: 0;
}
div.ac-result {
    background: #262626;
    border: #696969 solid 1px;
    box-shadow: #aaa 0 2px 3px;
    display: none;
    position: absolute;
    z-index: 101;
}
div.ac-result > ul {
    list-style: none;
    margin: 0;
    padding: 0;
}
div.ac-result > ul > li {
    color: #000;
    cursor: pointer;
    padding: 4px 6px;
    text-decoration: none;
}
div.ac-result > ul > li:hover,
