    height: 32px;
    margin-right: 3px;
    text-align: center;
    width: 32px;
}
div.ac-result > ul > li.searchac img {
    max-height: 32px;
    max-width: 32px;
}
div.ac-result > ul > li.section {
    background-color: #262626;
    cursor: inherit;
    font-weight: bold;
}
div.ac-result > ul > li.section:hover {
    background-color: #262626;
}
div.ac-result span.extra {
    color: #888;
    float: right;
    font-size: 85%;
