}
div.comment-hint > div:first-child > i:hover {
    color: #000;
}
div.comment-hint > div:first-child > span {
    cursor: pointer;
}
div.comment-hint > div:first-child > span > i {
    display: inline-block;
    text-align: center;
    width: 1.25em;
}
div.comment-hint > div.hint {
    background-color: white;
    border: #ccc inset 1px;
    border-radius: 5px;
    margin: 5px 0;
    padding: 2px 5px 5px 5px;
}
div.comment-op,
div.comment-owner {
    background-repeat: repeat-x;
    margin-left: -4px;
    margin-right: -4px;
    padding: 3px;
}
div.comment-op {
    background-image: none; /* url(https://www.ozbargain.com.au/files/images/shade-gray.png?v=1);*/
    border: #696969 solid 1px;
}
div.comment-owner {
    background-image: none; /* url(https://www.ozbargain.com.au/files/images/shade-orange.png?v=1);*/
    border: #696969 solid 1px;
}
div.comment-thread li {
    list-style: none;
    margin-bottom: 3px;
    padding: 2px;
}
div.comment-thread li.current {
