    margin-top: -3px;
    text-align: right;
}
div.comment-wrap:hover span.c-toggle {
    visibility: visible;
}
div.container-inline div {
  display: inline;
}
div.content,
div.content2 {
    line-height: 1.428571429;
}
div.content.vote-1 {
    opacity: 0.8;
}
div.content.vote-2 {
    opacity: 0.65;
}
div.content.vote-3 {
    opacity: 0.5;
}
div.content a,
a.external,
a.internal {
    background-image: url(https://www.ozbargain.com.au/files/images/linksprites.png?v=2);
    background-position: right -50px;
    background-repeat: no-repeat;
    padding-right: 7px;
    color: #FFF;
    cursor: pointer;
    text-decoration: underline;
}
div.content a.plain {
    background: none;
    padding: auto;
}
div.content a.reflink {
    background-position: right 0;
    border-bottom: 1px dotted #888;
    color: #888;
}
div.content a.reflink:hover {
    border-bottom-style: solid;
    color: #666;
    text-decoration: none;
}
div.content a.wikilink1,
a.internal {
    background-position: right -100px !important;
    color: #FFF
}
div.content img {
    max-width: 100%;
}
div.content div.footnotes {
    font-size: 85%;
}
div.content div.footnotes hr {
    background-color: #666;
    border: 0;
    color: #666;
    height: 1px;
    margin-left: 0;
    width: 150px;
}
div.content h1,
div.content h2,
div.content h3,
div.content h4,
div.content h5,
div.content h6 {
    margin: 0.5em 0;
}
div.content hr {
    background-color: #262626;
    border-color: #ccc;
    border-style: solid;
    border-width: 1px 0 0 0;
    height: 0;
}
div.content p {
    margin: 0.3em 0 0.8em;
}
div.content pre {
    overflow: hidden;
}
div.content pre:hover {
    overflow: visible;
}
div.content span.linkhost {
    color: #888;
    font-size: 75%;
    margin-left: 3px;
}
div.content > ul {
    margin-bottom: 0.8em;
    margin-top: 0.3em;
}
div.content ul {
    list-style-type: disc;
}
div.c-vote {
    color: #FFF;
    float: right;
    font-size: 85%;
}
div.c-vote span {
    float: left;
    margin-left: 2px;
}
div.c-vote span.cvb {
    margin-top: -1px;
}
div.c-vote span.votedown {
    background-color: transparent;
    border-color: transparent;
    border-radius: 0;
    color: #EEE;
}
div.c-vote span.voteup {
    background-color: transparent;
    border-color: transparent;
    border-radius: 0;
    color: #EEE;
}
/*------------*/
span.nvb.voteup, span.nvb.votedown{
    background-color: transparent;
    color: #EEE;
}
span.nvb.voteup:hover, span.nvb.votedown:hover{
    background-color: transparent;
}

span.voteup {
    position:relative;
    background-color: transparent;
    border-color: transparent;
    color: #EEE;
    margin: 2px 0px 0px 0px;
    width: 100%;
    height: 14px;
    display: block;
    cursor: pointer;
    background-position: center center;
    background-repeat: no-repeat;
    width: 15px;
    margin-left: auto;
    margin-right: auto;
    outline: none;
    font-weight: bold;
    
}    
span.voteup:before {
    content: "\f106";  /* this is your text. You can also use UTF-8 character codes as I do here */
    font-family: FontAwesome;
    left:-10px;
    position:absolute;
    top:0;
 }

 span.votedown {
    position:relative;
    background-color: transparent;
    border-color: transparent;
    color: #EEE;
    margin: 2px 0px 0px 0px;
    width: 100%;
    height: 14px;
    display: block;
    cursor: pointer;
    background-position: center center;
    background-repeat: no-repeat;
    width: 15px;
    margin-left: auto;
    margin-right: auto;
    outline: none;
    font-weight: bold;
}    
span.votedown:before {
    content: "\f107";  /* this is your text. You can also use UTF-8 character codes as I do here */
    font-family: FontAwesome;
    left:-10px;
    position:absolute;
    top:0;
 }
 span.cvb.voteup:before, span.cvb.votedown:before{
     left:0px !important;
 }


 /*------------*/

div.c-vote.inact span.cvb {
    cursor: pointer;
}
cvb.votedown{
    left:0;
}
div.c-vote.inact span.votedown:hover {
    background-color: transparent;
    color: #dd6113;
}
div.c-vote.inact span.voteup:hover {
    background-color: transparent;
    color: #00C467;
}
div.c-vote.isrep span.votedown, div.c-vote.isrep span.voteup  {
    display: none;
}
div.c-vote.votedown span.votedown, div.c-vote.voteup span.votedown {
    background-color: transparent;
    border-color: transparent;
    color: #dd6113;
}
div.c-vote.voteup span.voteup, div.c-vote.votedown span.voteup {
    background-color:transparent;
    border-color: transparent;
    color: #00C467;
}

