/* NodeGrid2 */
ul.ng2-deals {
    background-color: #eee;
    list-style: none;
    margin: 0;
    padding: 10px 10px 0 10px;
    overflow: hidden;
}
ul.ng2-deals li {
    background-color: #262626;
    border-radius: 4px;
    box-shadow: #aaa 0 2px 3px;
    float: left;
    margin: 0 10px 10px 0;
    width: 170px;
}
ul.ng2-deals li div.ng2-dealimg {
    padding: 10px;
    position: relative;
}
ul.ng2-deals li div.ng2-dealimg > a {
    background-position: center;
    background-repeat: no-repeat;
    background-size: contain;
    display: block;
    height: 150px;
}
ul.ng2-deals li div.ng2-dealimg > a div.n-vote {
    position: absolute;
    left: 13px;
    bottom: 13px;
    width: 40px;
}
ul.ng2-deals li div.ng2-dealimg > a span.nvb {
    box-shadow: #888 1px 1px 1px;
}
ul.ng2-deals li div.ng2-dealinfo {
    font-size: 85%;
    margin-bottom: 8px;
    padding: 8px 10px 0 10px;
    overflow: hidden;
    line-height: 1.2em;
    height: 3.6em;
}
ul.ng2-deals li div.ng2-dealmeta {
    background-color: #444;
    border-radius: 0 0 4px 4px;
    color: #fff;
    font-size: 80%;
    overflow: hidden;
    padding: 5px 10px;
}
ul.ng2-deals li div.ng2-dealmeta div.hl {
    box-sizing: border-box;
    float: left;
    width: 50%;
}
ul.ng2-deals li div.ng2-dealmeta div.hr {
    box-sizing: border-box;
    float: right;
    width: 50%;
}
ul.ng2-deals li div.ng2-dealstore {
    background-color: #ddf;
    color: #666;
    font-size: 80%;
    font-style: italic;
    line-height: 140%;
    padding: 3px 10px;
    text-overflow: ellipsis;
    white-space: nowrap;
}
ul.ng2-deals li span.marker {
    box-shadow: #888 1px 1px 1px;
    display: block;
    text-transform: uppercase;
    padding: 2px 5px;
    position: absolute;
    top: 13px;
    right: 13px;
}
ul.ng2-deals li.expired div.ng2-dealimg > a {
    opacity: 0.5;
}

/* MenuPopup */
div.menupopup {
