/* MEDIA QUERY for MOBILE */
@media only screen and (max-width:460px) {
    div.dokuwiki div.multicol-3,
    div.wikipage div.multicol-3,
    div.dokuwiki div.multicol-4,
    div.wikipage div.multicol-4 {
        column-count: 1;
        -moz-column-count: 1;
        -webkit-column-count: 1;
    }
    table.login-btn-table {
        width: 300px !important;
    }
    table.login-btn-table .login-btn-cell {
        display: block;
    }
    table.login-btn-table .login-btn-cell table{
        width: 300px !important;
        margin-bottom: 15px;
    }
    ul.countdown li {
        margin-right: 10px;
    }
    ul.countdown span.countdown-cdval {
        font-size: 45px;
        height: 60px;
        line-height: 60px;
        width: 60px;
    }
    #overlay-box {
        top: 10%;
        width: 90%;
        left: 5%;
    }
}
@media only screen and (max-width:600px) {
    .nomobile {
        display: none !important;
    }
    div.dokuwiki div.multicol-2,
    div.wikipage div.multicol-2 {
        column-count: 1;
        -moz-column-count: 1;
        -webkit-column-count: 1;
    }
    div.dokuwiki div.multicol-3,
    div.wikipage div.multicol-3,
    div.dokuwiki div.multicol-4,
    div.wikipage div.multicol-4 {
        column-count: 2;
        -moz-column-count: 2;
        -webkit-column-count: 2;
    }
    #visitor-notice input {
        width: 90%;
    }
}
@media only screen and (min-width:600px) {
    .nodesktop {
        display: none !important;
    }
}
@media only screen and (min-width:1600px) {
    #main {
        background-color: #262626;
    }
    #main > div.wrap {
        background-color: #262626;
    }
}

