css:
@-moz-document domain("members.luscious.net") {
.mod-title {
    color: #29c529;
}
#sticky.user_menu li a:link,
#sticky.user_menu li a:visited {
    color: #00e5ff;
}
.profile_stats table td {
    padding: 5px 7px;
    background: #fd0b4114;
    vertical-align: middle;
    width: 180px;
    }
    
    
    body {
    font: normal 15px/1.5 'Lato',"Helvetica Neue",Helvetica,Arial,sans-serif;
    -webkit-font-smoothing: antialiased;
    color: #00cdf2;
    background: #262626;
}

.information_block li {
    display: inline-block;
    vertical-align: top;
    padding: 10px;
    background: #0000009c;
    margin-top: 10px;
}

a:link, a:visited {
    color: #d6c09c;
    text-decoration: none;
}

.online_status.online {
    background: #ff00f5;
}

.online_status {
    color: #fff900;
    text-align: center;
}

.online-title {
    color: #ffffff;
}


a.follow_btn {
    background: #ff005c;
    padding: 10px;
    margin-top: 6px;
    display: block;
    width: 60px;
    border-radius: 2px;
    font-weight: bold;
    border-bottom: 2px solid #27ff00;
    font-size: 13px;
}

.icon-bell.unread {
    color: #ff0000 !important;
}

.unread {
    color: #ff0000 !important;
}

#sticky.user_menu {
    text-transform: uppercase;
    font-weight: bold;
    background: #1b1b1b00;
    border-bottom: 3px solid #cc00ff;
    float: left;
    height: 43px;
}

#blogs.main .block_wrapper:nth-child(odd) .blog {
    background-color: #383838;
    color: #e600ea;
}

.warn_notice {
    background-color: #00000000;
    color: #25ff00;
    width: 50%;
    margin: 0 auto;
    padding: 20px;
    margin-top: 15px;
    text-align: center;
}

.user-title {
    font-size: .9em;
}

#user_meta .interests .body.markdown, .about_me.markdown {
    margin: 10px 0;
    line-height: 24px;
    background: #00000000;
    padding: 1px 5px 18px 15px;
    overflow: hidden;
}

#sticky.user_menu li a {
    display: block;
    padding: 4px 10px 4px 10px;
    line-height: 35px;
    font-size: 12px;
    color: #f7f100;
}

.online_status.offline {
    background: #71111100;
}

.like_icon {
    background-image: url(https://media.discordapp.net/attachments/397141610791698433/397170740186710016/Heart_adjusted.png);
}

.dislike_icon {
    background-image: url(https://media.discordapp.net/attachments/397141610791698433/397170749808443392/Heart_2_adjusted.png);
}


.grid .album_btn a {
    text-align: center;
    border-radius: 2px;
    display: inline-block;
    margin: 5px;
    color: #00ca11;
}

.post-meta li a {
    padding: 25px;
    line-height: 15px;
    cursor: pointer;
    color: #f30101;
}

input, textarea {
    background: #17f1f100;
    border: 1px solid #ff0000;
    display: block;
    width: 100%;
    color: inherit;
    font-size: inherit;
    padding: 10px;
}input[type='submit'] {
    background: #000000;
    border-radius: 4px;
    width: 150px;
    padding: 10px 15px 10px 15px;
    font-weight: bold;
    cursor: pointer;
    color: white;
}

.post-meta li a:link, .post-meta li a:visited {
    color: #09ff29;
}

.btn {
    border-radius: 2px;
    background: #3ac500;
    padding: 10px 15px 10px 15px;
}

a.mr_btn {
    display: inline-block;
    text-align: center;
    margin-bottom: 20px;
    font-weight: bold;
    padding: 8px;
    border-radius: 2px;
    font-size: 13px;
    color: #eee2e2;
    background: #e80ac3c7;
    border: 0;
    border-bottom: 2px solid #ff23ee;
}
.facet_list li a.positive {
    background: #ff00eb;
    padding: 0 5px;
    text-align: center;
    display: inline-block;
}

.disfavour-icon.set, .disfavour-icon:hover, .dislike_icon:hover, .dislike_icon.set {
    background-image: url(https://discordapp.com/assets/8fee3f6705505729fea8c7379934d794.svg);
}

.favour-icon.set, .favour-icon:hover, .like_icon:hover, .like_icon.set {
    background-image: url(https://discordapp.com/assets/a42df564f00ed8bbca652dc9345d3834.svg);
}

.menu li.page_controls a:link, .menu li.page_controls a:visited, .menu li.page_controls a:hover {
    color: #000;
}

.menu li a:link, .menu li a:visited {
    color: black;
}
.favour-icon, .like_icon {
    background-image: url(https://discordapp.com/assets/a42df564f00ed8bbca652dc9345d3834.svg);
}

.disfavour-icon, .dislike_icon {
    background-image: url(https://discordapp.com/assets/8fee3f6705505729fea8c7379934d794.svg);
    }
#sticky.user_menu .second_panel li a.btn {
    color: #040404 !important;
    margin-bottom: 10px;
    text-align: center;
}

#global_wrapper {
    width: 100%;
    background: #6900b900;
}

.flag-icon {
    background-image: url(https://discordapp.com/assets/10d135bf11670b6db1db682a512da004.svg);
}
html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, font, img, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td {
	cursor: url('http://i770.photobucket.com/albums/xx342/lexibeexoxo/th_cursor16.png'), progress !important;
    margin: 0;
    padding: 0;
    border: 0;
    outline: 0;
    font-size: 100%;
    vertical-align: baseline;
   
}
}
