css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("app.decipherchinese.com") {
  
  body {
    background-color: #222;
    color: #aaa;
  }
  
  hr {
    border-top-color: #333;
  }
  
  /** Top Bar **/
  .navbar.navbar-default {
    background-color: #282828;
  }
  
  .navbar.navbar-default .navbar-nav > li > a {
    background-color: #282828;
    color: #aaa;
  }
  
  .navbar.navbar-default .navbar-nav > li:hover > a {
    background-color: #333;
    color: #aaa;
  }
  
  /* 'Please Sign In' overlay */
  .modal-content {
    background-color: #222;
  }
  
  .modal-header {
    border-bottom-color: #333;
  }
  
  .modal-footer {
    border-top-color: #333;
  }
  
  .btn-default {
    background-color: #222;
    border-color: #333;
    color: #aaa;
  }
  
  .btn-default:focus, .btn-default.focus {
    background-color: #333;
    color: #aaa;
  }
  
  /* Sign in 2 - 'Get free access to more new and popular articles.' */
  
  .well {
    background-color: #222;
    border-color: #333;
  }
  
  /* Message overlays, near footer */
  
  .s-alert-success {
    background-color: #333;
    color: #aaa;
  }
  
  .s-alert-close::before, .s-alert-close::after {
    background-color: #aaa;
  }
  
  /* Spinner overlay */
  .spinner-container div[style*="background: rgb(0, 0, 0)"] {
    background-color: #666 !important;
  }
  
  
  /*** 'New', 'Popular',... Tabs ***/
  
  .nav-tabs {
    border-bottom-color: #333;
  }
  
  .nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus {
    background-color: #333;
    border-color: #333;
    color: #aaa;
  }
  
  /*** Tiles ***/
  
  .grid-block, .main, .article, .big-article, .comments li {
    background-color: #282828;
    border-color: #333;
  }
  
  /* Article date e.g. 'Yesterday', 'Thursday', etc. */
  .article-date-tag {
    background-color: #444;
    color: #ccc;
  }
  
  /* Text */
  .big-article a, .big-article a:link, .big-article a:visited, .big-article a:hover, .big-article a:active {
    color: #aaa;
  }
  
  /* Label e.g. 'HSK2', '5 min',... */  
  
  .label-time {
    background-color: #444;
    color: #aaa;
  }
  
  .label-lv1 {
    background-color: #494949; /* #e5e5e5; */
    color: #ccc;
  }
  
  .label-lv2 {
    background-color: #007900; /* #aaffaa; */
    color: #ccc;
  }
  
  .label-lv3 {
    background-color: #448800; /* #D0FFA1; */
    color: #ccc;
  }
  
  .label-lv4 {
    background-color: #676700; /* #FFFF99; */
    color: #ccc;
  }
  
  .label-lv5 {
    background-color: #ab5400; /* #FFC790; */
    color: #ccc;
  }
  
  .label-lv6 {
    background-color: #a30000; /* #ff8888; */
    color: #ccc;
  }
  
  
  /**** After login ****/
  
  /** Top bar **/
  .navbar.navbar-default .navbar-collapse {
    background-color: #282828;
  }
  
  
  .navbar.navbar-default .navbar-nav > li.active > a {
    background-color: #333;
    color: #aaa;
  }
  
  .navbar.navbar-default:not(.navbar-fixed-bottom):not(.no-border) .navbar-nav > .active > a::before {
    border-top-color: #333;
  }
  
  .navbar.navbar-default:not(.navbar-fixed-bottom) .navbar-nav > .active > a::after {
    border-top-color: #333;
  }
  
  span.badge[style*="background-color: #f0ad4e"] {
    background-color: #995e0d !important;
  }
  
  .navbar.navbar-default .navbar-nav > li.open > a {
    background-color: #333;
    color: #aaa;
  }
  
  .navbar.navbar-default .navbar-nav > li.open:hover > a {
    background-color: #333;
    color: #aaa;
  }
  
  .navbar.navbar-default .navbar-nav > li .dropdown-menu:not([class*="collaps"]) {
    background-color: #333;
    color: #aaa;
    border-color: #333;
  }
  
  .navbar.navbar-default:not(.navbar-fixed-bottom):not(.no-border) .navbar-nav > .open > a::before {
    border-bottom-color: #222;
  }
  
  .navbar.navbar-default:not(.navbar-fixed-bottom) .navbar-nav > .open > a::after {
    border-bottom-color: #333;
  }
  
  
  /** Tabs **/
  .nav > li > a:hover, .nav > li > a:focus {
    background-color: #333;
    border-color: #333 #333 #333;
    color: #aaa;
  }
  
  /** Opened Article top-right marker - near footer **/
  .article-opened::before {
    border-color: #363636 #282828;
  }
  
  /** Graduation Hat Tab -> 'My Decipher' title **/
  
  .list-group-item {
    background-color: #282828;
    border-color: #333;
  }
  
  .progress {
    background-color: #222;
  }
  
  .progress-bar-empty {
    background-color: #222;
  }
  
  .progress-bar-warning {
    background-color: #995e0d; /* #f0ad4e; */
  }
  
  /* 'Touch to add vocab' */
  .list-group-item.disabled, .list-group-item.disabled:hover, .list-group-item.disabled:focus {
    background-color: #222;
  }
  
  /** 'Review Vocab' tab **/
  
  .flashcard {
    background-color: #222;
    border-color: #333;
  }
  
  /** 'Manage Vocab' tab **/
  
  .table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {
    border-top-color: #333;
  }
  
  .table > thead > tr > th {
    border-bottom-color: #333;
  }
  
  /** 'Add Favourite articles to see them here!' **/
  
  .alert-success {
    background-color: #282828;
    border-color: #333;
  }
  
  
  /**** Individual News Page ****/
  
  /* Middle Pane buttons e.g. 'Simplified', 'Hints',... */
  .btn-default:hover {
    background-color: #333;
    border-color: #333;
    color: #aaa;
  }
  
  .btn-default:active, .btn-default.active, .open > .dropdown-toggle.btn-default {
    background-color: #333;
    border-color: #333;
    color: #aaa;
  }
  
  .btn-default:active:hover, .btn-default.active:hover, .open > .dropdown-toggle.btn-default:hover, .btn-default:active:focus, .btn-default.active:focus, .open > .dropdown-toggle.btn-default:focus, .btn-default.focus:active, .btn-default.active.focus, .open > .dropdown-toggle.btn-default.focus {
    background-color: #333;
    border-color: #333;
    color: #aaa;
  }
  
  /* Dropdown Menu */
  
  .dropdown-menu {
    background-color: #333;
  }
  
  .dropdown-menu > li > a {
    color: #aaa;
  }
  
  .dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus {
    background-color: #333;
    color: #ccc;
  }
  
  /* 'Just you reading'/'XYZ reading' dot messages */
  .fa.fa-square[style="color:yellow"] {
    color: #666 !important;
  }
  
  .fa.fa-square[style="color:greenyellow"] {
    color: #3c6300 !important;
  }
  
  /* Word */
  
  .word.lv1:hover, .word.lv2:hover, .word.lv3:hover, .word.lv4:hover, .word.lv5:hover, .word.lv6:hover, .word.lv7:hover {
    background-color: #333;
  }
  
  .popover {
    background-color: #222;
    border-color: #333;
  }
  
  .popover-title {
    background-color: #222;
    border-bottom-color: #333;
  }
  
  .popover > .arrow {
    border-bottom-color: #444 !important;
  }
  
  .popover > .arrow::after {
    border-bottom-color: #222 !important;
  }
  
  .word.lv1 {
    border-bottom-color: #494949; /* #e5e5e5; */
  }
  
  .word.lv2 {
    border-bottom-color: #007900; /* #aaffaa; */
  }
  
  .word.lv3 {
    border-bottom-color: #448800; /* #D0FFA1; */
  }
  
  .word.lv4 {
    border-bottom-color: #676700; /* #FFFF99; */
  }
  
  .word.lv5 {
    border-bottom-color: #ab5400; /* #FFC790; */
  }
  
  .word.lv6 {
    border-bottom-color: #a30000; /* #ff8888; */
  }
  
  /* Comments */
  
  .bs-callout {
    border-color: #333;
  }
  
  img.media-object[src="/img/face.png"] {
    filter: invert(60%);
    opacity: 0.3;
  }
  
  /* Footer */
  
  img[src="/img/weather-pouring.png"] {
    filter: invert(60%);
  }
  
  
  /**** Sign In Page - app.decipherchinese.com/sign-in ****/
  
  .form-control {
    background-color: #222;
    border-color: #333;
    color: #aaa;
  }
  
  /**** Upgrade Page - app.decipherchinese.com/upgrade ****/
  
  .panel {
    background-color: #222;
    border-color: #333;
  }
  
  .alert-info {
    background-color: #282828;
    border-color: #333;
  }

}
