css:
@-moz-document url("https://unitymenu.com/chat/fullpage"), url("https://unitymenu.com/chat/"), url("https://unitymenu.com/")
{
  #siropuChatFull #siropuChatContent
  {
    bottom: 6%!important;
  }

  #siropuChat
  {
    background-color: #2a2d32;
  }

  #siropuChatHeader
  {
    background: #2a2d32;
    border-radius: 0;
  }

  #siropuChatHeader h3
  {
    display: none;
  }
  #siropuChatPrivateConversations
  {
    background-color: #2a2d32;
  }

  #siropuChatOptions
  {
    float: none!important;
    text-align: right;
  }

  #siropuChatContent
  {
    border-top: 1px solid #464646;
    top: 6%!important;
  }

  #siropuChatNotice
  {
    display: none;
  }

  .tabs
  {
    border: none;
    padding: 0;
    margin-top: 0;
  }

  .tabs li a
  {
    border-radius: 0;
    border-right: 1px solid #464646!important;
    line-height: 16px!important;
    padding: 4px 16px!important;
  }

  .tabs li a,
.tabs.noLinks li
  {
    background-color: #2a2d32;
  }

  .tabs li.active a,
.tabs.noLinks li.active
  {
    background-color: #2a2d32;
    color: rgb(186, 124, 251);
  }

  #siropuChatTabs
  {
    background-color: #2a2d32;
    margin-top: 0;
  }
    .siropuChatTabs > li .tabRoom-0 active
  {
    background-color:#2a2d32;
  }
 
  .siropuChatMessages
  {
    padding: 0;
  }

  .siropuChatMessages > li,
.siropuChatPrivateMessages > li
  {
    margin: 0;
    padding-left: 10px!important;
    border-bottom: 1px solid #333;
  }
/** ALTERNATING BG FOR MESSAGE THREADS
.siropuChatMessages > li:nth-child(odd)
{	background: #2d2d2d;    }**/
  
  .siropuChatMessages
  {
    background-color: #2a2d32;
  }

  .siropuChatMessages > li .DateTime
  {
    background-color: transparent;
  }

  .siropuChatContentLeft
  {
    padding: 7px 0 7px 0;
  }

  .siropuChatContentLeft .avatar
  {
    display: none;
  }

  .bbCodeImageFullSize
  {
    left: 50%;
    width: inherit!important;
    height: 50%;
  }

  .siropuChatMeta
  {
    padding-right: 10px;
  }

  .DateTime
  {
    padding: 0!important;
    font-size: 12px!important;
    margin-top: 8%;
  }

  .siropuChatTagged,
.siropuChatAdmin .siropuChatTagged
  {
    background-color: rgba(255, 127, 80, 0.2)!important;
    border-radius: 0;
    padding: 0!important;
  }

  #siropuChatEditor
  {
    background-color: #2a2d32;
    border-top: 1px solid #464646;
    bottom: -30px!important;
  }
  
  #siropuChatEditor .textCtrl
  {
    width: 47%;
  }

  .textCtrl
  {
    border-radius: 25px;
    border: none;
  }

  #siropuChatBBCode
  {
    position: relative;
    top: -34px;
    left: -65px;
    float: right;
  }

  .button
  {
    background-color: #2a2d32;
  }

  #siropuChatSubmit
  {
    position: relative;
    top: -3px;
    right: 5px;
    color: #fff;
    background: rgb(105, 126, 196);
    border-radius: 30px;
  }

  .secondaryContent
  {
    background: transparent;
  }

  #siropuChatSmilies
  {
    background: #464646;
    border-radius: 25px;
    position: relative;
    top: 0;
    padding: 10px!important;
    display: inline;
  }

  .siropuChatUsers
  {
    background-color: #2a2d32;
    width: 250px;
  }

  .siropuChatUsers > li:first-child::before
  {
    width: 90%;
    content: 'Currently in-chat';
    position: absolute;
    top: 10px;
    padding-bottom: 5px;
    border-bottom: 1px solid #c9c4c4;
    overflow: auto;
  }

  .siropuChatUsers > li:first-child
  {
    padding: 30px 30px 0 10px;
  }

  .siropuChatUsers > li
  {
    padding: 2px 0 2px 10px;
  }

  .siropuChatUsers .username
  {
    padding-left: 10px;
    position: relative;
    top: 0;
  }

  .siropuChatUserStatus
  {
    position: relative;
    top: -5px;
    left: 32px;
    margin-left: 5px;
    width: 75%;
    padding: 2px 0 2px 0;
  }

  .siropuChatHasStatus
  {
    margin-bottom: -10px;
  }

  #siropuChat .siropuChatHasStatus .avatar img
  {
    top: 17px;
  }

  #siropuChat .avatar img
  {
    width: 24px;
    height: 24px;
    margin: 0 auto;
    top: 10px;
  }

  .avatar img,
.avatar .img,
.avatarCropper
  {
    border-radius: 25px;
  }

  .siropuChatActive,
.siropuChatIdle,
.siropuChatIdle2
  {
    background: none;
  }

  .siropuChatActive .avatar img
  {
    border: 2px solid #2ECC71;
  }

  .siropuChatIdle .avatar img
  {
    border: 2px solid #D3D3D3;
  }

  .siropuChatIdle2 .avatar img
  {
    border: 2px solid gold;
  }

  .siropuChatWhisperPolice .siropuChatWhisperAction
  {
    color: firebrick;
  }
}

@-moz-document url("https://xenmenu.com/chat/"), url("https://xenmenu.com/")
{
  #siropuChat
  {
    overflow: visible;
  }

  #siropuChatContent > div
  {
    height: 200%;
  }

  #content > .pageWidth > .pageContent
  {
    padding-bottom: 80px;
  }

  #siropuChatNotice
  {
    display: block!important;
    text-align: center;
  }

  .tabs li a
  {
    line-height: 36px!important;
  }

  .siropuChatMessages,
.siropuChatUsers
  {
    height: 88%!important;
  }

  .siropuChatMessages > li:nth-child(n+3)
  {
    border-left: 1px solid #464646;
  }

  #siropuChatEditor
  {
    border: 1px solid #464646;
    position: relative;
    bottom: -190px!important;
  }

  #siropuChatEditor .textCtrl
  {
    width: 30%;
  }

  .textCtrl .UserTagger::active
  {
    width: 80%;
  }

  #siropuChatBBCode
  {
    left: 20%!important;
  }

  .secondaryContent
  {
    padding: 10px!important;
  }

  #siropuChatSmilies
  {
    background: transparent!important;
  }

  #siropuChatSmilies .smilieContainer
  {
    width: 100%;
    position: relative;
    top: -100px;
  }

  .button
  {
    padding: 0 10px!important;
  }

  .button:nth-child(n+5)::before
  {
    content: ''!important;
  }

  #siropuChatSubmit
  {
    float: none!important;
    top: 0!important;
    right: 35px!important;
    background-color: #181818;
    border-left: 3px solid #464646;
  }

  #content .pageContent >:last-child
  {
    margin-top: 100px;
  }

  .siropuChatUsers
  {
    border-right: 1px solid #464646;
  }

  .breadBoxBottom
  {
    display: none;
  }

  .footer .pageContent
  {
    margin-top: 20px;
  }
}
