css:
@-moz-document regexp('https:\/\/fa\.wikipedia\.org\/wiki\/.*') {
	
body {
    font-family: Vazir !important;
    background: #222 !important;
    color: #efefef !important;
}
#mw-page-base {
    background: transparent !important;
}
a {
    color: #f1c40f !important;
}
a:hover {
    color: #2fffff !important;
    text-decoration: none;
}
.mw-body,
.parsoid-body {
    background: transparent !important;
    color: #fff;
}
div#content h1,
div#content h2,
div#content #firstHeading,
div#content .mw-editsection,
.mw-body .mw-editsection-like,
div#content #toc h2,
div#content .toc h2 {
    font-family: Vazir !important;
    color: #2fffff !important;
}
.CheckDictation-issues-label {
    background-color: rgba(250, 250, 250, 0.2) !important;
    padding: 3px !important;
}
table.ambox {
    background: transparent !important;
    border-right: 10px solid #2fffff !important;
}
element,
.CheckDictation-marked {
    background-color: rgba(250, 255, 255, 0.2) !important;
    padding: 2px !important;
}
#toc,
.toc,
.mw-warning,
.toccolours {
    border: 1px solid #a2a9b1 !important;
    background-color: transparent !important;
    padding: 19px !important;
    font-size: 95% !important;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    color: #2fffff !important;
    font-weight: normal !important;
    font-family: Vazir Code !important;
}
.navbox,
.navbox-subgroup {
    background: transparent !important;
}
.navbox {
    border: 1px solid rgba(100, 100, 100, 0.6) !important;
}
.navbox th,
.navbox-title {
    background: rgba(100, 100, 100, 0.6) !important;
}
.catlinks {
    background-color: rgba(250, 10, 10, 0.4) !important;
}
div.vectorTabs span {
    background-color: rgb(34, 34, 34) !important;
}
div.vectorMenu h3 span,
div.vectorMenu h3 span {
    color: #fff !important;
}
div.vectorMenu h3:hover span,
div.vectorMenu h3:focus span {
    color: #efefef !important;
}
#pt-notifications-alert .mw-echo-notifications-badge::before,
#pt-notifications-notice .mw-echo-notifications-badge::before {
    background-color: rgba(250, 250, 250, 0.6) !important;
}

.aside
{
    background-color: rgba(250, 255, 255, 0.1) !important;
}
.h2
{
    color:#222 !important;
}
}
