css:
@-moz-document url-prefix("http://libraryofthumbs.com"), url-prefix("http://www.libraryofthumbs.com") {
#header {
	background: none;
	padding-top: 50px
}

#header img {
	display: none;
}

#header a::after {
	content: 'Library of Thumbs';
	font: 600 6em Helvetica;
}

#white {
	margin-top: 40px;
	background-color: #000a;
	width: calc(800px - 5%);
	padding: 5%
}

#white2 {
	background: #0000;
	width: 100%;
	height: 40px;
	margin-top: 40px;
	padding: 0px;
	font-size: 11px;
	text-align: center;
}

#white2 > div:nth-child(1) {
	text-align: center !important;
	padding: 0 !important;
	margin: 0;
	float: none !important;
}

#white2 > div img {
	display: none !important;
}

#white2 > div:nth-child(1)::before {
	position: relative;
	content: url('data: image/png;base64,');
}

#white2 > b:nth-child(2) {
	line-height: 40px !important;
}
}

@-moz-document url-prefix("http://libraryofthumbs.com"), url-prefix("http://www.libraryofthumbs.com") {
/* ================== */

body {
	background-image: url('https://i.pinimg.com/originals/82/15/94/821594e5cda597955cf59c5b9ad8c7a6.jpg');
	background-attachment: fixed;
	background-position: left center;
	background-size: cover;
	color: #aaa !important;
}

body > div:nth-child(2) > center:nth-child(1) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > p:nth-child(2) > a:nth-child(1) > img:nth-child(1) {
	display: none;
}

body > div:nth-child(2) > center:nth-child(1) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > p:nth-child(2) > a:nth-child(1) {
	color: #004a;
	text-decoration: none;
	text-shadow: 0 0 5px #0002;
	font-weight: 600;
	font-family: 'Century Gothic', Helvetica;
	transition: all 1000ms ease-in-out;
}

body > div:nth-child(2) > center:nth-child(1) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > p:nth-child(2) > a:nth-child(1):hover {
	color: #000;
	color: rgba(150, 200, 255, 1);
	transition: all 1000ms ease-in-out;
}

body > div:nth-child(2) > center:nth-child(1) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > p:nth-child(2) > a:nth-child(1)::after {
	content: 'Library of Thumbs';
	font-size: 50pt;
}

a:link {
	color: rgba(150, 200, 255, .7);
	text-decoration: none;
}

a:hover {
	color: rgba(150, 200, 255, 1);
	text-decoration: none;
}

table {
	background-color: #0003 !important;
}

td {
	background-color: #00000011 !important;
}

body > div:nth-child(2) > center:nth-child(1) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > div:nth-child(2),
#kwt5610 > div:nth-child(2) > a:nth-child(1) > img:nth-child(1),
body > div:nth-child(3) > div:nth-child(4) > div:nth-child(2) > a:nth-child(1) > img:nth-child(1),
iframe {
	display: none;
}

table#mainmenu {
	border: none !important;
}

table#mainmenu td {
	padding: 0;
	border: solid 1px #fff6;
	transition: all 400ms ease-in-out;
}

table#mainmenu td a {
	color: rgba(150, 200, 255, .7);
	border: none !important;
	transition: all 400ms ease-in-out;
}
}

@-moz-document url("http://www.libraryofthumbs.com/index2.html"), url("http://libraryofthumbs.com/index2.html"), url-prefix("http://www.libraryofthumbs.com/archive/"), url-prefix("http://libraryofthumbs.com/archive") {
