body > div:nth-child(1) > center:nth-child(1) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > p:nth-child(2) > a:nth-child(1) {
	color: #004a;
	text-decoration: none;
	text-shadow: 0 0 5px #0002;
	font-weight: 600;
	font-family: 'Century Gothic', Helvetica;
	transition: all 1000ms ease-in-out;
}
}
