/* Scratchpad style */
#nbextension-scratchpad .cell-wrapper {
	height: 100%;
	overflow: auto;
	background-color: #ffffff;
}

#nbextension-scratchpad {
	position: absolute;
	right: 0;
	bottom: 0;
	width: 50%;
	border-left: 2px solid #f07823 !important;
	border-top:  2px solid #f07823 !important;
	z-index: 9000;
}

.CodeMirror-dialog input {
  border: none !important;
  outline: none !important;
  background: #fafafa;
  width: 99%;
  color: #202020;
  font-family: 'mononoki', 'Meslo LG S', monospace !important;
  font-size: 12px;
}

/* Make Lists more beatiful */
.rendered_html * + ol {
    margin-top: 0em;
    margin-bottom: 1em;
}
	
.rendered_html * + ul {
    margin-top: 0em;
    margin-bottom: 1em;
