/* Simple Dark Yahoo PH
/* Version: 1.1.0
/* by Dennis Allan Barba
/* Initial commit on 19/DEC/17
/* Last Updated on 22/DEC/17
/* Tested on Google Chrome Version 63.0.3239.84 (Official Build) (64-bit)
/*•••••••••••••••••••••••••••••••••••••••••••••••••••••••*/

@-moz-document domain("ph.yahoo.com") {
/* HEADER/NAV */
  #mega-uh-wrapper .Bgc\(\#fff\) {
  background: rgb(63,63,63); /* Old browsers */
  background: -moz-radial-gradient(center, ellipse cover, rgba(63,63,63,1) 0%, rgba(15,15,15,1) 100%); /* FF3.6-15 */
  background: -webkit-radial-gradient(center, ellipse cover, rgba(63,63,63,1) 0%,rgba(15,15,15,1) 100%); /* Chrome10-25,Safari5.1-6 */
  background: radial-gradient(ellipse at center, rgba(63,63,63,1) 0%,rgba(15,15,15,1) 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
  }

  #uh-search-box {
      color: #dcdcdc;
  }

  #atomic .Bgc\(\#0f69ff\) {
  	background-color: #3e4654;
    border: 1px solid #b0b0b0;
  }

  #atomic .Bdc\(\#0f69ff\)\:f:focus {
    border: 1px solid #b0b0b0;
  }

  #mega-uh-wrapper #mega-bottombar {
      background: #191919;
  }

  #my-adsLDRB {
  	display: none;
  }

  .two-col .logo-datauri {
      background-image: url('https://i.imgur.com/5pNsO7u.png');
  }

  #atomic .Bg\(\$topbarBgc\) {
      background: #191919;
  }

  #uh-search .yui3-aclist-content {
      background-color: #16171f;
      color: #dcdcdc;
      border: 1px solid #0f69ff;
  }

  /* MAIN */

  html {
  /* 	background: rgb(25, 25, 25); */
      background: url('https://i.imgur.com/UTXC7ni.jpg');
      background-size: cover;
      background-repeat: no-repeat;
      background-attachment: fixed;
  }

  body {
      background: rgba(0, 0, 0, 0);
  }

  #atomic .Bgc\(\#fff\) {
      background-color: rgba(34, 34, 34, 0);
  }

  #atomic .C\(\$link\) {
      color: #a5dee1;
  }

  #atomic .C\(\$link\):visited {
      color: #a5dee1;
  }

#atomic .C\(\#4d00ae\),
#atomic .C\(\#6001d2\),
#atomic .C\(\#000\),
#atomic .C\(\#26282a\),
#atomic .C\(\$gray\) {
      color: #dcdcdc;
  }

  #atomic .Bgc\(\#6b03f6\) {
      background-color: #3a3a3a7d;
  }

  #atomic .Bgi\(\$hpsetBtnGradient\) {
      background: rgb(32, 32, 32);
  }
}

@-moz-document domain("ph.news.yahoo.com") {
/* HEADER/NAV */
#atomic .Bgc\(\#2d1152\) {
	background: #191919;
}

#YDC-UH .Bgc\(\#fff\) {
  background: rgb(63,63,63); /* Old browsers */
  background: -moz-radial-gradient(center, ellipse cover, rgb(58, 58, 58) 0%, rgba(15,15,15,1) 100%); /* FF3.6-15 */
  background: -webkit-radial-gradient(center, ellipse cover, rgb(58, 58, 58) 0%,rgba(15,15,15,1) 100%); /* Chrome10-25,Safari5.1-6 */
  background: radial-gradient(ellipse at center, rgb(58, 58, 58) 0%,rgba(15,15,15,1) 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
}

input[aria-label="Search"] {
	color: #dcdcdc;
}

#atomic .Bdc\(searchBdrFoc\)\:f:focus {
	border-color: #b0b0b0;
}

#UH-0-Header #uh-logo {
	background-image: url('https://i.imgur.com/OwypOyZ.png');
}

#atomic .C\(\$c-fuji-purple-1-c\) {
	color: #dcdcdc;
}

svg.Cur\(p\) {
	fill: #fff !important;
    stroke: #fff !important;
}

#atomic .Bgc\(\$bg-content\)\! {
	background-color: #191919 !important;
    border-top: 1px solid#444;
    border-bottom: 1px solid#444;
}

#atomic .Bdc\(\$c-divider\) {
	border-color: #444;
}

#atomic .Bgc\(\$c-divider\) {
	background-color: #444;
    height: 1px;
}

#atomic .news.PH.en-PH .Bgc\(\$bg-header\) {
	background-color: #191919;
}

#atomic .C\(lightTheme\) {
	color: #dcdcdc! important;
}

#atomic .C\(lightTheme\)\:h:hover {
	color: #188fff;
}

#atomic .Bgc\(searchBtn\) {
	background-color: #3e4654;
    box-shadow: inset 0 -2px 0 0 #3b4352;
}

#atomic .Bgc\(searchBtn\):hover {
    background-color: #004fff;
}

#atomic .Maw\(1260px\) {
	max-width: 1350px;
}


/* MAIN */

#atomic .canvas-body a {
	color: #a5dee1;
}

html,
body,
#atomic .Bgc\(\$bg-body\) {
	background-color: #121212;
}

#atomic .C\(\#222\), #atomic .C\(\#000\),
#atomic .C\(\$c-fuji-blue-1-a\), 
#atomic .C\(\$c-fuji-blue-4-b\) { 
	color: #a5dee1 !important;
}

h1,
body * h3,
body * time,
body * p,
body * ul,
#atomic .C\(\#222\),
#atomic .C\(\#000\),
#atomic .C\(\#3f3f3f\),
#atomic .C\(\#3c3c5b\),
#atomic .C\(\$c-fuji-grey-l\) {
	color: #dcdcdc;
}

#atomic .Bgc\(\$bg-content\) {
	background-color: #121212;
}

#atomic .Bgc\(\#fff\) {
	background-color: rgba(0,0,0,0);
}

#atomic .C\(\#787d82\) {
	color: #a2a9b1;
}

button[data-sharetype="tumblr"],
button[data-sharetype="facebook"],
button[data-sharetype="twitter"],
button[data-sharetype="mail"] {
	background-color: rgb(53, 58, 64);
    border: 1px solid #404950;
}

#atomic .Bgc\(\$c-fuji-grey-a\) {
	background-color: #121212;
}

form[enctype="multipart/form-data"] * textarea,
form[enctype="multipart/form-data"] * span {
	color: #dcdcdc;
}

form[enctype="multipart/form-data"] * textarea {
	background-color: #2d2d2d;
}

#atomic .Bdc\(\$c-fuji-grey-c\) {
	border-color: #404040;
    border-radius: 10px;
}
}
