css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gitxiv.com") {
  
  body {
    background-color: #222;
  }
  
  body, textarea, input {
    color: #aaa;
  }
  
  /* Top Logo */
  .logo-image img {
    filter: invert(60%);
  }
  
  .dark-bg {
    background-color: #222 !important;
    color: #aaa !important;
  }
  
  /* 'Post' button on right */  
  undefined, input[type="submit"], button, .button, .auth-buttons #login-buttons #login-buttons-password, .btn-primary, .header .btn-primary, .header .btn-primary:link, .header .btn-primary:visited, .error, .mobile-menu-button, .login-link-text, .post-category:hover, .post-admin a:hover {
    background-color: #333;
  }
  
  .btn-primary {
    color: #aaa !important;
  }
  
  /* Search box */
  #search {
    background-color: #282828;
    color: #aaa;
  }
  
  /* Top space */
  .views-menu-wrapper {
    background-color: #222;
  }
  
  /* 'Categories' dropdown */
  .menu-dropdown .menu-items, .menu-dropdown .menu-child-items {
    background-color: #222;
  }
  
  .menu-dropdown .menu-item:hover {
    background-color: #282828;
  }
  
  .dark-bg {
    color: #aaa !important;
  }
  
  .menu-dropdown .menu-item-label-text {
    color: #aaa;
  }
  
  label.category-label {
    color: #aaa;
  }
  
  .categories-menu .category-posts-count {
    color: #aaa;
  }
  
  
  /* Category Title */
  .category-heading-wrapper .category-heading {
    background-color: #282828;
  }
  
  /* 'Daily' subtitle headinds */
  .posts-day-heading {
    color: #aaa;
  }
  
  /* 'Load Next Days' button near footer */
  .grid-module, .at-form {
    background-color: #282828;
  }
  
  /** Post row **/
  
  .post {
    background-color: #222;
    border-bottom-color: #333;
  }
  
  .post-thumbnail {
    opacity: 0.6;
  }
  
  .post-vote .vote-actions {
    border-color: #333;
  }
  
  .post-category {
    background-color: #282828;
  }
  
  .post-discuss .action {
    color: #333;
  }
  
  /**** Post Page ****/
  
  .post-body, .page-body {
    background-color: #222;
  }
  
  /* Separator */
  .col_sm-description {
    border-bottom-color: #333;
  }
  
  div[style*="background-color:#f9f9f9;"] {
    background-color: #282828 !important;
  }
  
  /* Comments */
  .grid-block, .comment-content, .comment-new {
    background-color: #222;
  }
  
  /**** Competitions Page ****/
  
  .markdown img {
    filter: invert(86.5%);
  }
  
  .markdown blockquote {
    border-left-color: #333;
  }

}
