css:
@-moz-document domain("www.insomnihack.fr") {
.p-body {
    flex-grow: 1;
    background-color: #151515;
}
.p-sectionLinks {
    font-size: 13px;
    color: #727174;
    background: #151515;
    border-bottom: 1px solid #151515;
    box-shadow: inset 0px -2px 16px 0px #00ff00;
}
.p-breadcrumbs {
    overflow: hidden;
    position: relative;
    background-color: #151515;
    border-radius: 3px;
    box-shadow: 0 0 12px 0px #00ff00;
}
.p-body .p-body-inner .p-body-header {
    margin: 20px 0;
    background-color: #151515;
    border-radius: 3px;
    padding: 6px 20px;
    color: #00ff00;
    box-shadow: 0 0 11px 0px #00ff00;
}
::-webkit-scrollbar-track {
    background-color: #151515;
}
.block-container {
    color: #666;
    background: #151515;
    border-width: 1px;
    border-style: solid;
       border-top-color: #15151500;
    border-right-color: #15151500;
    border-bottom-color: #15151500;
    border-left-color: #15151500;
    border-radius: 4px;
    -webkit-transition: all .25s ease;
    transition: all .25s ease;
    -webkit-transition-property: border margin;
    transition-property: border margin;
    box-shadow: 0 0 13px #00ff00;
}
.block-footer {
    padding: 6px 10px;
    font-size: 12px;
    color: #666;
    background: #151515;
    border-top: 1px solid #151515;
}
.p-body .p-body-inner .p-body-sidebar .block .block-container .block-body {
    font-size: 12px;
    background-color: #151515;
    padding: 10px 0;
}
.p-body .p-body-inner .p-body-sidebar .block .block-container .block-body.staff-content li:nth-child(2n) {
    background-color: #151515;
}
.p-body .p-body-inner .p-body-sidebar .block .block-container .block-body.post-content li:nth-child(2n) {
    background-color: #151515;
}
.p-body .p-body-inner .p-body-main .p-body-content .p-body-pageContent .block .block-container .block-body .node {
    border: none;
    background-color: #151515;
}
.p-body .p-body-inner .p-body-main .p-body-content .p-body-pageContent .block .block-container .block-body .node:nth-child(2n + 1) {
    background-color: #151515;
}
.fr-toolbar {
    color: #313146;
    background: #151515;
    border: 1px solid #151515;
}
#siropuChatEditor .fr-wrapper {
    background: #151515;
    border-width: 0;
    border-style: solid;
}
.editorSmilies {
    display: none;
    border: 1px solid #ededed;
    border-top: none;
    background: #151515;
}
.tabs--editor {
    color: #666;
    background: #151515;
    font-weight: 400;
    border-bottom: 1px solid #e0e0e0;
    font-size: 0;
}
.input {
 
    background: #151515;
}
#siropuChatEditor .fr-toolbar {
    border-top: 1px solid #151515;
    border-right-width: 0;
    border-right-style: solid;
    border-bottom: 1px solid #151515;
    border-left-width: 0;
    border-left-style: solid;
}
#siropuChatNotice {
    padding: 10px;
    border-bottom: 1px solid #151515;
    color: #64646e;
}
.p-footer {
    font-size: 13px;
    color: #8d8c8f;
    background: #151515;
    box-shadow: 0 0 13px #00ff00;
}
.tooltip--member .tooltip-content {
    color: #313146;
    background: #151515;
    padding: 0;
    text-align: left;
    border: 1px solid #e0e0e0;
    box-shadow: 0 5px 10px 0 rgba(0,0,0,0.35);
}
.bbCodeBlock {
    margin: .75em 0;
    background: #151515;
    border: 1px solid #666;
    border-radius: 3px;
}
.bbCodeBlock-title {
    padding: 6px 10px;
    font-size: 13px;
    color: #666;
    background: #151515;
    border-bottom: 1px solid #151515;
}
h1.p-title-value {
    margin-left: auto;
}
.block-footer {
    border-bottom: 1px solid #151515;
}
.block-outer {
    margin-bottom: 20px;
    background-color: #151515;
    border-radius: 4px;
    box-shadow: 0 0 15px #00ff00;
}
.structItemContainer .structItem {
    background-color: #151515;
    border: none;
}
.structItemContainer .structItem:nth-child(2n) {
    background-color: #151515;
}
.structItemContainer .structItem:nth-child(2n) .structItem-cell--meta {
    background-color: #151515;
}

.structItemContainer .structItem .structItem-cell--meta {
    background-color: #151515;
}
.block--messages .block-body.js-replyNewMessageContainer {
    background-color: #151515;
    padding: 0 10px 0 20px;
}
.block--messages .message, .block--messages .block-row {
    color: #666;
    background: #fefefe;
    border-width: 1px;
    border-style: solid;
    border-top-color: #d8d8d8;
    border-right-color: #e0e0e0;
    border-bottom-color: #ededed;
    border-left-color: #e0e0e0;
    border-radius: 4px;
}
.likesBar {
    display: none;
    opacity: 0;
    -webkit-transition: all .25s ease;
    transition: all .25s ease;
    -webkit-transition-property: all,-xf-opacity;
    transition-property: all,-xf-opacity;
    overflow-y: hidden;
    height: 0;    
    background: #fff;
    border: 1px solid #151515;
    border-left: 2px solid #d12e36;
    padding: 6px;
    font-size: 12px;
    margin-top: 6px;
    box-shadow: 0 0 14px #00ff00;
}
.block--messages .message .message-inner .message-footer .likesBar.is-active {
    border-left: 1px solid transparent;
    border-top-color: transparent;
    border-right-color: transparent;
    background-color: #151515;
    border-radius: 4px;
}
.blockMessage.blockMessage--none {
    background: #151515;
    padding: 4px 15px;
    box-shadow: 0 0 13px #00ff00;
}
form .block-container {
    border-left: none;
    border-right: none;
    border-radius: 0;
    padding: 20px 10px 20px 20px;
    background: #151515;
}
.p-body .p-body-inner .p-body-main .p-body-content .p-body-pageContent .block .block-container {
    border-color: #151515;
    box-shadow: 0 0 14px #00ff00;
}
form .block-container .message--quickReply .message-inner .message-cell--main .message-editorWrapper .bbWrapper .fr-toolbar {
    border-top: 1px solid transparent;
    border-right-color: transparent;
    border-left-color: transparent;
    background: #151515;
    box-shadow: 0 0 13px #00ff00;
}
.fr-box.fr-basic .fr-wrapper {
    background: #151515;
    border: 1px solid #151515;
}
.fr-box.fr-basic .fr-element {
    color: #666;
}
.block--messages .message .message-inner .message-cell--user .message-user {
    padding: 10px;
    background: #151515;
    border-radius: 3px;
}
.button.button--cta, a.button.button--cta {
    color: #545454;
    background: #212121;
    border-color: #00ff00 #00ff00 #00ff00 #00ff00;
}
.button, a.button {
    color: #545454;
    background: #212121;
    border-color: #545454 #545454 #545454 #545454;
}
a {
    color: #545454;
}
.block-header {
    padding: 6px 10px;
    margin: 0;
    font-weight: 400;
    text-decoration: none;
    font-size: 20px;
    color: #cccdcd;
    background: #23272a;
    border-bottom: 1px solid #2c2c2c;
    box-shadow: 0 0 13px #00ff00;
}
.notices--floating .notice {
    box-shadow: 0px 0px 13px #00ff00;
}
.p-nav-list .p-navEl.is-selected {
    color: #00ff00;
}
.siropuChatDateTime {
    background: #212121;
}

.siropuChatMention {
    background-color: #23272a;
}
.siropuChatMention .siropuChatDateTime {
    background: #212121;
}
.block--messages .message, .block--messages .block-row {
    color: #545454;
    background: #151515;
   border-top-color: #151515;
    border-right-color: #151515;
    border-bottom-color: #151515;
    border-left-color: #151515;
}
.message-cell.message-cell--user, .message-cell.message-cell--action {
    background: #151515;
}
::-webkit-scrollbar-thumb {
    background-color: #00ff00;
}
#siropuChatEditor .fr-toolbar {
   box-shadow: 0 0 13px #00ff00;
}
.button.button--link, a.button.button--link {
    background: #23272a;
    color: #c1cdcd;
}
.menu-content {
    margin: 0;
    padding: 0;
    list-style: none;
    text-align: left;
    font-size: 13px;
    color: #545454;
    background: #151515;
    border-radius: 3px;
    border-top: 0px solid #212121;
}
.menu-linkRow {
    display: block;
    padding: 6px 15px 6px 12px;
    border-left: 3px solid transparent;
    color: #545454;
    text-decoration: none;
}
.menu-footer {
    background: linear-gradient(180deg, #151515, #151515);
}
.button.button--primary, a.button.button--primary {
    color: #a2a2a2;
    background: #3c4043;
    min-width: 80px;
    border-color: #a2a2a2 #a2a2a2 #a2a2a2 #a2a2a2;
}
.menu-row.menu-row--highlighted {
    color: #cccdcd;
    background: #151515;
}
.menu-content {
    box-shadow: 0 0 13px #00ff00;
}
.block-minorHeader {
    background-color: #23272a;
}
.tooltip--member .tooltip-content {
    border: 1px solid #151515;
    color: #545454;
    box-shadow: 0 0 13px #00ff00;
}
.tooltip--preview .tooltip-content {
    background: #151515;
    border: 0px solid #252525;
    box-shadow: 0 0 13px 0 #00ff00;
}
.tooltip--preview .tooltip-content-inner .tooltip-content-cover {
    background: linear-gradient(to bottom, rgba(75,75,75,0) 160px, #151515 200px);
}

.siropuChatConversation.siropuChatUsers>li[data-conv-id].siropuChatActiveConversation {
    background: #23272a;
}
.p-footer-linkList {
    box-shadow: 0 0 13px #00ff00;
}
.u-concealed, .u-concealed a, .u-cloaked, .u-cloaked a {
    box-shadow: 0 0 13px #00ff00;
}
.pageNav-jump {
    color: #545454;
}
.pageNav-page {
    color: #545454;
}
.pageNav-page.pageNav-page--current {
    color: #1e2225;
}
.pollResult-bar>i {
    background: #212121;
}
.pollResult--voted .pollResult-response:before {
    color: #545454;
}
.node--unread .node-icon i:before {
    color: #00ff00;
}
}
