css:
@-moz-document domain("wrongthink.net") {
body {
	background-color: #101010;
	color: #EEEEEE;
}

.panel, .offnavmenu, .sidebar-profile-style, .greetalert, .featured-users {
	background-color: #212121 !important;
}

.form-control {
	background-color: #383838;
	border-color: #606060;
}

.post .post-description p, a {
	color: #E0E0E0;
}

.post .post-heading .meta .title a {
	color: #5FBED6;
}

.hash {
	color: #FA5858;
}

.sidebar-conatnier .sidebar-profile-style .card.hovercard .info .title a {
	color: #EEEEEE;
}

.fa.fa-arrow-right, .post .post-heading .meta .time {
	color: #BBBBBB;
}

.list-group {
	background-color: #212121;
}

.list-group-item {
	background-color: #212121;
}

.filterbyz .avtive {
	background-color: #383838;
}

.chat-container {
	background-color: #212121 !important;
	color: #EEEEEE;
}

div.tab button.active {
	color: #FA5858;
	border-bottom-color: #FA5858;
}

.no-online-users, .recipient-chat-user .chat-user-text {
	color: #EEEEEE;
}

.user-lastseen {
	color: #BBBBBB;
}

.online-text {
	color: #00BB00;
}

.main-color, .small-text a, .second-user-color {
	color: #FA5858 !important;
}

.activities-wrapper .notification-list .notification-text, .activities-wrapper .notification-list .notification-time {
	color: #BBBBBB;
}

.panel-white {
	border-color: #383838;
}

.side-widget-name {
	background-color: #383838 !important;
	color: #DDDDDD !important;
}

.list-group {
	border-color: #383838;
}

.post .post-heading {
	border-color: #606060;
}

.greetalert, .featured-users, .featured-users .list-inline li {
	border-color: #606060 !important;
}

.sidebar-profile-style {
	/* box-shadow: 0 1px 0 0 #e3e4e8, 0 0 0 1px #f1f1f1; */
	box-shadow: 0 1px 0 0 #606060, 0 0 0 1px #606060;
}

hr {
	border-color: #606060;
}

.order-by {
	border-color: #606060;
}

.filterbyz li, .filterbyz .avtive {
	border-right-color: #606060 !important;
}

span.zmdi.zmdi-apps {
	color: #DDDDDD;
}

.featured-users span {
	color: #DDDDDD !important;
}

.post-fetched-url .fetched-url-text h4 {
	color: #BBBBBB;
}

span a.pointer span {
	color: #ffa412 !important;
}

.order-by {
	color: #BBBBBB !important;
}

.cussidepageda, .wonderful-user, .sideprolist {
	border-bottom-color: #606060;
}

li.list-group-item.text-muted {
	background-color: #383838 !important;
}

.list-group-item {
	color: #DDDDDD !important;
	border-bottom-color: #606060 !important;
}

.chat-container {
	border-left-color: #383838;
}

.activity-container .activity {
	border-bottom-color: #606060;
}

.chat-toggle-bar {
	background-color: #383838;
	color: #EEEEEE;
	border-color: #606060;
}

.zmdi.zmdi-group-work, .zmdi.zmdi-more-vert {
	color: #DDDDDD;
}

.publisher-box .publisher-box-footer {
	background-color: #212121;
}

.publisher-box .publisher-box-footer {
	border-top-color: #606060;
}

.form-control.postText.ui-autocomplete-input {
	color: #DDDDDD;
}

.user-status-home {
	background-color: #212121;
	border-color: #383838;
}

.post .post-footer {
	background-color: #212121;
	border-top-color: #606060;
}

.post .post-footer .comments-list .comment {
	border-bottom-color: #606060;
}

.comment-text, .reply-text, .comment-options {
	color: #DDDDDD;
}

#comment-likes, #comment-wonders, .comment-options {
	color: #BBBBBB;
}

#profilenav {
	background-color: #212121;
	border-color: #383838;
}

.groupmenulist {
	border-bottom-color: #212121;
}

.groupmenulist:hover {
	background-color: #FFFFFF;
}

#profilenav ul li {
	border-right-color: #606060;
}

.profile-lists.list-group .list-group-item {
	background-color: #383838;
}

/* Reply and search for users box. */
.form-control, output {
	color: #DDDDDD;
}

/* Articles section. */
.content-container.container {
	background-color: #111111;
}

.read-blog {
	background-color: #212121;
}

.article-title-res {
	color: #EEEEEE;
}

.read-blog-desc p {
	color: #BBBBBB;
}

.blog-com-wrapper {
	background-color: #212121;
	box-shadow: 0 0;
}

.blog-com-top {
	border-bottom-color: #606060 !important;
}

.blog-com-box-act {
	background-color: #606060 !important;
}

.blog-com-box-act button {
	color: #606060;
}
}
