css:
@-moz-document domain(www.drive2.ru)
{
html, body {
    color: #ddd;
    background-color: #222;
}
a {
    color: #70a0e0;
}
.c-link--color00:not(.c-link--nd) {
    color: #eee;
}
.l-content {
    background-color: #333333;
}
.c-nav-block__link {
    color: #aaa;
}

.c-block {
    background-color: #333;
}
.c-lb-card {
   background-color: #222;
}
.c-link {
    color: #aaa;
}
.c-button {
    color: #5890e6;
}
.c-block__more {
    background: #111;
    color: #70a0d0;
    border-top: 0px solid #000;
}
.c-button--solid, .c-button-hover:hover .c-button-hover__item {
    color: #fff !important;
}
.footer-container {
    background: #444;
}
.c-block__footer {
    background-color: #444;
}
.c-block--collapse-top, .c-block__button, .c-block__footer {
    border-top: 0px solid #707070;
}
.c-link--text:not(.c-link--nd) {
    color: #FFF;
}
.c-car-card__info {
    color: #ccc;
}
.c-msg__body {
    color: #fff;
    background: #555;
}
.bubble-body {
    color: #ccc;
}
.bubble-ntfy .bubble-body {
    padding: 0;
    background-color: #444;
}
.ntfy-links-item {
    background: #444;
    color: #70a0d0;
}
.c-link--color09:not(.c-link--nd) {
    color: #27b02b;
}
.c-post__desc {
    color: #ccc;
}
.c-block-nav__link span {
    color: #bbb;
}
.c-block-nav__link strong {
    color: #70a0d0;
}
.c-input, .c-select>option, .c-textarea {
    color: #ccc;
    background: #555;
}
.imagelist-controls {
    background: #222;
}
.c-button--gray {
    color: #aaa !important;
}
.bubble-tooltip .bubble-body {
    background-color: #333;
}
.c-user-card__cars {
    color: #ccc;
}
.c-user-card__status {
    color: #3e3e3e;
}
a.c-block-nav__link:hover {
    background-color: #333;
}
.c-nav-block__item:hover {
    background-color: #444;
}
.c-pc-card {
    border-bottom: 0px solid #900;
    background-color: #555;
}
.dialog {
    background: #444;
}
.c-msgr-contact__status {
    color: #eee;
}
.post-promo--standalone {
    background: #444;
}
.c-pager--alt {
    background-color: #444;
    border-bottom: 0px solid #fff;
}
.c-car-desc.is-shaded:before {
    background: -webkit-linear-gradient(top,rgba(255,255,255,0),#444);
    background: linear-gradient(to bottom,rgba(255,255,255,0),#444);
}
.l-alt {
    display: none;
}
.c-dv-sns {
    display: none;
}
.c-post-card--h .c-post-card__body {
    background-color: #444;
}
.c-button--solid.c-button--lightgray, .c-button-hover:hover .c-button-hover__item.c-button--lightgray {
    background-color: #444;
}
.c-button--solid.c-button--lightgray:hover:not(.is-off):not([disabled]) {
    background-color: #222 !important;
}
.c-cp-body {
    background: #444;
}
.c-cp-block:nth-of-type(2n+1) {
    background-color: #222;
}
.c-cp-block--alt {
    background-color: #222;
}
.c-cp-stats {
    color: #ccc;
}
.c-cp-feedback-stub {
    background-color: #222;
}
.c-select {
    color: #ccc;
    background: #222;
}
.c-block__more:hover {
    background-color: #222 !important;
}
.c-msgr-contact:hover{
    background: #222;
}
.page-header {
    color: #aaa;
}
.totop {
    color:rgba(255, 255, 255, 0.5);
    fill:#fff;
}
.totop:hover {
    background-color:rgba(34, 34, 34, 0.5);
}
.offer-card--wide .offer-card__location {
    color: #aaa;
}
.offer-card:hover {
    background-color: #222 !important;
}
/*[[ads]]*/
}
