css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#nav-bar
{
  height: 38px !important;
}

#back-button[disabled="true"] {
  display: none !important;
}

#back-to-close-button .toolbarbutton-icon {
  opacity: 0 !important;
}

#back-to-close-button {
  background: url("data:image/png;base64,") !important;
  border: none !important;
  width: 38px !important;
  height: 37px !important;
  background-repeat: no-repeat !important;
  background-position: 1px 2px !important; 
}

#back-to-close-button:hover {
  background: url("data:image/png;base64,") !important;
  background-repeat: no-repeat !important;
  background-position: 1px 2px !important; 
}

#back-to-close-button:active {
  background: url("data:image/png;base64,") !important;
  background-repeat: no-repeat !important;
  background-position: 1px 2px !important; 
}

#nav-bar[iconsize="small"]
{
  height: 28px !important;
}

#nav-bar[iconsize="small"] #back-to-close-button {
  background: url("data:image/png;base64,") !important;
  border: none !important;
  padding: 0px !important;
  width: 26px !important;
  height: 24px !important;
  background-repeat: no-repeat !important;
  background-position: 2px 2px !important; 
}

#nav-bar[iconsize="small"] #back-to-close-button:hover {
  background: url("data:image/png;base64,") !important;
  background-repeat: no-repeat !important;
  background-position: 2px 2px !important;
}

#nav-bar[iconsize="small"] #back-to-close-button:active {
  background: url("data:image/png;base64,") !important;
  background-repeat: no-repeat !important;
  background-position: 2px 2px !important; 
}
