css:
@-moz-document domain("fimfiction.net") {
.post_table .button,
.styled_button:not(.styled_button_blue):not(.styled_button_dark_grey):not(.styled_button_grey):not(.styled_button_white),
.styled_button.button-submit {
    box-shadow: 0 1px #409cdd inset;
    border: 1px solid #3480b5;
    color: #fff;
    text-shadow: -1px -1px #3787bf;
    background: #276fa2;
}
.styled_button.styled_button_brown .fa {
    color: #fff;
}
.post_table .button,
.styled_button:not(.styled_button_blue):not(.styled_button_dark_grey):not(.styled_button_grey):not(.styled_button_white):hover,
.styled_button.button-submit:hover {
    background: #2675ad;
}
.comment .author .name {
    background-color: #4d3fa8;
    text-shadow: -1px -1px #443286;
    box-shadow: 0px 1px 0px #7345b9 inset;
    border: 1px solid #523286;
}
.toggleable-switch input:checked + a::before {
    color: #5c5d8d;
}
.drop-down-pop-up-container .drop-down-pop-up .std input[type="text"]:focus, .drop-down-pop-up-container .drop-down-pop-up .std input[type="url"]:focus, .drop-down-pop-up-container .drop-down-pop-up .std input[type="email"]:focus, .drop-down-pop-up-container .drop-down-pop-up .std select:focus,
.properties input.valid, .properties input:required:valid,
.properties input:focus, .properties select:focus {
    background-color: #c0d5f0;
}
.drop-down-pop-up-container .drop-down-pop-up .std ul li a:hover {
    background-color: #e7e7fb;
}
.toggleable-switch input:checked + a {
    background-color: #a8beda;
}
.bookshelves-popup-list .bookshelf .add-status .added {
    color: #1c2094;
}
.fa.fa-fw.fa-warning {
    color: #2522d1 !important;
}
.user-page-header .online {
    color: #321f8e;
}
.sidebar-blog-post-list .date {
    background-color: #6738b9;
    background: #483bc2;
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #483bc2), color-stop(100%, #4c35b0));
    background: -webkit-linear-gradient(top, #483bc2 0%, #4c35b0 100%);
    background: linear-gradient(to bottom, #483bc2 0%, #4c35b0 100%);
    border: 1px solid #51309d;
    border-bottom-color: #48309d;
	border-bottom-color: #3c2d94;
}
.rating_container .rating-bar .like-bar,
.story-card .rating-bar span {
    background: #4651f7;
}
.story_container .content-rating-teen {
    border-color: #0012d9;
    background-color: #0051ff !important;
}
.story_container .content-rating-teen:hover {
    background-color: #1e00ff !important;
    border: 1px solid #1d00d9;
}
.front_page .featured_box .featured_story .content_rating_teen,
.story-card .content_rating_teen {
    background-color: #0051ff !important;
}
.story_container .content-rating-everyone,
.front_page .featured_box .featured_story .content_rating_everyone,
.story-card .content_rating_everyone {
    background-color: #4084ac !important;
}
.group-card.nsfw .banner,
.group-card.nsfw .group-name a::before,
.story_container .content-rating-mature,
.front_page .featured_box .featured_story .content_rating_mature,
.story-card .content_rating_mature {
    background-color: #2f53c0 !important;
}
.story-tags > li a.tag-character {
    background-color: #233ab9;
    border: 1px solid #1e489d;
}
.story-tags > li a.tag-character:hover {
    background-color: #2b2edf;
    border: 1px solid #3121ad;
}
.currently-reading-list li .read-progress {
    background: #b0deff;
}
.nav_bar .mail-link div, .nav_bar .notifications-link div, .nav_bar .feed-link div {
    background-color: #5f22d1;
}
.message, .error-message, .success-message {
    background-color: #dfeeff;
    color: #151669;
}
.user_cp .tabs ul li.tab_selected {
    background: #abbdff;
}
.rating_container .like_button_selected,
.comment .comment_information .comment-like .like-text,
.comment .comment_information .comment-like[data-liked="true"] .like {
    color: #2838c3;
}
.sidebar-header, .user-page .right .module-gallery > h1, .user-page .right .module-text > h1,
.user_cp .tabs h1,
.post_table thead td {
    background-color: #7a66d6;
    border-bottom: 1px solid #4d52ee;
    border-right: 1px solid #513bc8;
    border-top: 1px solid #4639bf;
}
.sidebar-header, .user-page .right .module-gallery > h1, .user-page .right .module-text > h1,
.user_cp .tabs h1 {
    border-left: 1px solid #513bc8;
}
.sidebar-header .count, .user-page .right .module-gallery > h1 .count, .user-page .right .module-text > h1 .count,
.sidebar-header.header-tabs a.selected {
    background: #815cad;
    box-shadow: 0 1px 0 #4e4b5a inset;
}
.post_table thead td input {
    border: 1px solid #343cce;
    background-color: #3234b7;
}
.user_cp .tabs h1 i {
    color: #fff;
}
.tag-header .image {
    background-color: #e0dcf0;
}
.group-header .banner {
    background: #77f;
}
.tag-header .image .img {
    background: #77f;
    border-radius: 4px;
}
.group-header .banner .banner_overlay,
.tag-header .image img {
    mix-blend-mode: screen;
}
}
