css:
/* i really want this to be global */
@font-face {
 font-family: 'Rubik', sans-serif;
 src: url("https://fonts.googleapis.com/css?family=Rubik") format("woff");
}
body{
	background-color: #f3f3f3;
}
.oppost-wrapper{
    padding: 15px;
}
.oppost-wrapper .post{
	padding: 0;
    background: transparent;
}
.post {
    background-color: #fff;
    border: none;
    font-family: 'Rubik', sans-serif;
    font-size: 17px;
    padding: 15px;
    border-radius: 5px;
}
section.moneymoneymoney,
section.desktop,
div.adminbar-bottom,
#report-form,
footer hr{
	display: none;
}
footer > div{
	display: none;
}
footer div.norm-reply{
	display: block!important;
    padding: 30px;
}
.post-wrapper{
	margin: 10px 0 10px 0px;	
}
blockquote{
	color: #505050;
    margin: 1.5em 35px;
}
.post-details{
    color: #777777;
    font-size: 14px;
}
.postbtn-hide{
    width: 13px;
    height: 13px;
	background: url('https://image.flaticon.com/icons/svg/130/130469.svg')
}
.postbtn-report{
    width: 13px;
    height: 13px;
	background: url('https://image.flaticon.com/icons/svg/130/130468.svg')
}
.postbtn-options{
    width: 13px;
    height: 13px;
	background: url('https://image.flaticon.com/icons/svg/109/109418.svg')
}
.ABU-refmap{
	font-size: 65%;
}
#fullscreen-container{
	background-color: rgb(12, 12, 12) !important;
}
.hidden-post-box{
	border-radius: 5px;
    border: 1px solid #b7b7b7 !important;
    padding: 0px 10px;
    background-color: #f4f3f3;
}
.hidden-post-box .post-details{
	color: #505050;
    font-size: 12px;
}
.post .file-attr{
	font-size: 0.7em;
	padding-top: 10px;    
}
.post-message a{
	font-size: 14px;
}
.reply.hiclass{
	background: #ffe8d6;
}
.modal{
	background-color: #fff;
}
