css:
@-moz-document domain("www.insomnihack.fr") {
html {
    background: #131313;
    color: #ff0000;
   
}
.p-header-logo {
    margin-left: auto;
}
img {
     margin-right: 350px;
}
.p-title-value {
    font-size: 80px;
    margin-left: auto;
    font-family: hacked;
}
.p-sectionLinks {
    color: #727174;
    background: #151515;
    border-bottom: 1px solid #151515;
    box-shadow: inset 0px -2px 16px 0px #f00;
}
::-webkit-scrollbar-track {
    background-color: #131313;
}
::-webkit-scrollbar-thumb {
    background-color: #ff0000;
}
.p-nav-list .p-navEl.is-selected {
    color: #ff0000;
}
a {
    color: #545454;
}
.fa-home:before {
    color: #ff0000;
}
.p-breadcrumbs {
    box-shadow: 0 0 13px #ff0000;
        margin-top: 12px;
}
.fa-newspaper-o:before {
    color: #ff0000;
}
.fa-bitcoin:before, .fa-btc:before {
    color: #ff0000;
}
.fa-group:before, .fa-users:before {
    color: #ff0000; 
}
.fa-commenting:before {
    color: #ff0000;
}
.p-navgroup-link.p-navgroup-link--conversations i:after {
    color: #ff0000;
}
.p-navgroup-link.p-navgroup-link--alerts i:after {
    color: #ff0000;
}
.p-navgroup-link.p-navgroup-link--search i:after {
    color: #ff0000;
}
.button.button--cta, a.button.button--cta {
    color: #545454;
    background: #131313;
    border-color: #ff0000 #ff0000 #ff0000 #ff0000;
}
.button.button--icon--write>.button-text:before, a.button.button--icon--write>.button-text:before {
    color: #ff0000;
}
.button, a.button {
    color: #545454;
    background: #131313;
    border-color: #545454 #545454 #545454 #545454;
}
.block-container {
    background: #131313;
    border-width: 0px;
        color: #a7a7a7;
        box-shadow: 0 0 13px #ff0000;
}
.block-minorHeader {
    background-color: #23272a;
    box-shadow: 0 0 13px #ff0000;
}
.block-footer {
    color: #545454;
    border-top: 1px solid #131313;
    background: linear-gradient(180deg, #131313, #131313);
}
.block-header {
    background: linear-gradient(0deg, #23272a, #23272a);
    box-shadow: 0 0 13px #ff0000;
}

.fa-navicon:before, .fa-reorder:before, .fa-bars:before {
    color: #ff0000;
}
.fa-gears:before, .fa-cogs:before {
    color: #ff0000;
}
.fa-life-bouy:before, .fa-life-buoy:before, .fa-life-saver:before, .fa-support:before, .fa-life-ring:before {
    color: #ff0000;
}
.fa-bullhorn:before {
    color: #ff0000;
}
.fa-legal:before, .fa-gavel:before {
    color: #ff0000;
}
.fa-user:before {
    color: #ff0000;
}
.fa-bar-chart-o:before, .fa-bar-chart:before {
    color: #ff0000;
}
.fa-comments:before {
    color: #ff0000;
}
.fa-share-square:before {
    color: #ff0000;
}
.shareButtons--iconic .shareButtons-button>i {
    min-width: 20px;
    color: #ff0000;
}
.p-footer {
    background: #131313;
}
.p-footer a {
    color: #545454;
}
#siropuChat.block-container {
    box-shadow: 0px -5px 15px rgba(0, 0, 0, 0);
}
.fa-folder-open-o:before {
    color: #ff0000;
}
.fa-user-plus:before {
    color: #ff0000;
}
.fr-toolbar {
    color: #545454;
    background: #131313;
    border-top: 3px solid #212121;
}
#siropuChatEditor .fr-wrapper {
    background: #131313;
}
.button.button--link, a.button.button--link {
    background: #131313;
    color: #545454;
}
.fa-send:before, .fa-paper-plane:before {
    color: #ff0000;
}
.menu-content {
    background: #131313;
    border-top: 0px solid #131313;
    box-shadow: 0 0 13px #ff0000;
}
.menu-footer {
    border-top: 1px solid #545454;
    background: linear-gradient(180deg, #131313, #131313);
}
.input {
    background: #23272a;
}
.button.button--primary, a.button.button--primary {
    color: #545454;
    background: #131313;
    border-color: #545454 #545454 #545454 #545454;
}

.menu-separator {
    border-top: 1px solid #545454;
}
.notices--floating .notice.notice--dark {
    box-shadow: 0 0 13px #ff0000;
}
.siropuChatDateTime {
    background: #212121;
    color: #545454;
}
.tabs--editor {
    background: #131313;
}
.tabPanes {
    background-color: #131313;
}
.fr-command.fr-btn+.fr-dropdown-menu .fr-dropdown-wrapper .fr-dropdown-content ul.fr-dropdown-list {
    background-color: #131313;
}
.p-footer-linkList {
    box-shadow: 0 0 13px #ff0000;
}
.node--unread .node-icon i:before {
    color: #ff0000;
}
.p-title {
    margin-bottom: 30px;
    box-shadow: 0 0 13px #ff0000;
    margin-top: 30px;
        text-shadow: -2px 12px 13px black;
}
.fa-paint-brush:before {
    color: #ff0000;
}
.fa-globe:before {
    content: "\f0ac";
    color: #ff0000;
}
.tooltip--member .tooltip-content {
    color: #545454;
    background: #131313;
    box-shadow: 0 0 13px #ff0000;
}
.fr-toolbar .fr-command.fr-btn, .fr-popup .fr-command.fr-btn {
    color: #ff0000;
}
.fr-toolbar .fr-command.fr-btn.fr-dropdown::after, .fr-popup .fr-command.fr-btn.fr-dropdown::after {
    border-top: 4px solid #ff0000;
}
#siropuChatTabs a>span.siropuChatTabCountInactive {
    background: #ff0000;
}
.p-title-pageAction {
    margin-right: 8px;
}
.pageNav-page.pageNav-page--current {
    color: #ff0000;
    border: 1px solid #ff0000;
}
.pageNav-page {
    background: linear-gradient(0deg, #23272a, #23272a);
    color: #545454;
}
a[data-nav-id="siropuChat"] .badge--highlighted {
    background: #ff0000;
}
.pageNav-jump {
    display: inline-block;
    background: linear-gradient(0deg, #23272a, #23272a);
    color: #545454;
}
.block-filterBar {
    background: linear-gradient(0deg, #23272a, #23272a);
}
.block--messages .message, .block--messages .block-row {
    background: #131313;
}
.message-cell.message-cell--user, .message-cell.message-cell--action {
    background: #131313;
}
.likeIcon:before {
    color: #ff0000;
}
.likesBar {
    background: #131313;
    border: 1px solid #131313;
    box-shadow: 0 0 13px #ff0000;
}
label.iconic>input[type="checkbox"]+i:before {
    color: #ff0000;
}
.actionBar-action.actionBar-action--like:before {
    color: #ff0000;
}

.actionBar-action.actionBar-action--mq:before {
    color: #ff0000;
}
.actionBar-action.actionBar-action--reply:before {
    color: #ff0000;
}
.fa-rss-square:before {
    color: #ff0000;
}
.fa-thumbs-up:before {
    color: #ff0000;
    
}
.fa-comment:before {
    color: #ff0000;
}
.fa-shopping-cart:before {
    color: #ff0000;
}
.fa-key:before {
    color: #ff0000;
}
.fa-lock:before {
    color: #ff0000;
}
.fa-pencil:before {
    color: #ff0000;
}
.fa-user-times:before {
    color: #ff0000;
}
.fa-trophy:before {
    color: #ff0000;
}
.fa-tags:before {
    color: #ff0000;
}
.fa-clock-o:before {
    color: #ff0000;
}
.actionBar-action--report:before {
    color: #ff0000;
}
.actionBar-action--warn:before {
    color: #ff0000;
}
.userBanner.userBanner--staff, .userBanner.userBanner--primary {
    color: #ff0000;
}
.bbCodeBlock-title {
    font-size: 20px;
    color: #ff0000;
    background: #212121;
}
.bbCodeBlock {
    background: #212121;
    border-left: 3px solid #545454;
}
.fa-rotate-left:before, .fa-undo:before {
    color: #ff0000;
}
.fa-rotate-right:before, .fa-repeat:before {
    color: #ff0000;
}
.button.button--icon--reply>.button-text:before, a.button.button--icon--reply>.button-text:before {
    color: #ff0000;
}
.button.button--icon--preview>.button-text:before, a.button.button--icon--preview>.button-text:before {
        color: #ff0000;
}
.fr-box.fr-basic .fr-wrapper {
    background: #212121;
}
.smilie {
    margin-right: 10px;
}
.siropuChatMention {
    background-color: #212121;
}
.siropuChatTag {
    color: #ff0000;
}
.siropuChatMention .siropuChatDateTime {
    background: #131313;
}
.siropuChatConversation.siropuChatUsers>li[data-conv-id].siropuChatActiveConversation {
    background: #131313;
}
.p-footer-rssLink span {
    background-color: #131313;
}
#siropuChatEditor .fr-toolbar {
    box-shadow: 0 0 13px #ff0000;
}
.button.button--link, a.button.button--link {
    margin-bottom: -10px;
}
}
