css:
@-moz-document url-prefix("https://userstyles.org") {
/* code section 1 / 10 */

body {
	background-color : #101010;
}
#footer {
	background-color : #2e3e49;
}
a {
	color : #95b3c7;
}
a:visited {
	color : #c5d0da;
}
a:visited:hover {
	color : #c5d0da;
}
.footer_link a {
    color: #95b3c7 !important;
}
#bottomLinks {
	color: #95b3c7;
}
#copyrights {
	color : #95b3c7;
}
#rightFooter > div > a img.logo {
	display : none;
}
#rightFooter > div > a {
	background-image    : url(https://userstyles.org//ui/images/stylish-logo-2.svg);
	background-size     : 111px;
	background-position : center center;
	background-repeat   : no-repeat;
	display             : inline-block;
	height              : 34px;
	width               : 111px;
}
#nav-view {
	background-color : #222;
}
.navigation ,
#navigation {
	background-color : #222;
}
#view-responsive {
	background-color : #222;
}
.links-box .navigation-link img ,
.links-box .navigation-link img {
	filter : invert(100%);
}
*:not(.notes_textarea):not(.text_style_page):not(.style_description_long):not(.us-username):not(.NotificationLine-Content)>a  ,
.navigation .navigation-category {
	color : #b3b3b3;
}
.navigation .links-box .navigation-link .navigation-title {
	color       : #b3b3b3;
	margin-left : 5px;
}
.navigation .links-box .navigation-link:hover {
	background-color : #333;/*
	border-top       : 1px solid #888;
	box-sizing       : border-box;*/
	/* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#8c9299+0,8c9299+1,8c9299+1,333333+1,333333+1,333333+100 */
	background: #8c9299; /* Old browsers */
	background: -moz-linear-gradient(top, #dfe1e3 0%, #dfe1e3 2%, #333333 2%, #333333 100%); /* FF3.6-15 */
	background: -webkit-linear-gradient(top, #dfe1e3 0%,#dfe1e3 2%,#333333 2%,#333333 100%); /* Chrome10-25,Safari5.1-6 */
	background: linear-gradient(to bottom, #dfe1e3 0%,#dfe1e3 2%,#333333 2%,#333333 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
	/* will throw an error on userstyles.org */
	/* filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#dfe1e3', endColorstr='#333333',GradientType=0 ); */ /* IE6-9 */
}
.navigation .links-box .navigation-link:hover .navigation-title {
	color : #FFF;
}
.linksBox a:first-of-type .navigation_link:hover {
	background : #333;
}
.style_card {
	background-color : #000;
}
h4 a {
	color : #95b3c7 !important;
}
h4 a:visited {
	color : #c5d0da !important;
}
h4 a:hover {
	color: #95b3c7 !important;
}
h1 {
	color : #95b3c7;
}
#pages a , 
#pages a:active , 
#pages a:visited {
	color : #95b3c7;
}
.div-arrow {
	background-color : #000;
	border-color     : #111;
}
.us-stylecard--short {
	background-color : #000;
}
@media (min-width: 801px) {
	.navigation {
		left : -17px;
	}
}
@media (max-width: 1280px) {
	body {
		background-color: #222222;
	}
	.header_button:last-child {
		margin-right : 0px;
	}
	#top_logo {
		margin-left : 0px;
	}
	#footer-inside {
		margin-left : 44px;
	}
	#rightFooter {
		margin-right : 40px;
	}
	#navigation {
		min-width : inherit;
		padding   : 0px;
	}
	#view {
		margin-right : 0px;
		min-width    : inherit;
	}
	/* only usefull if body color is not the same as #navigation & #container #view-inner #iframe body changed - otherwise this has no effekt */
	#container ,
	#nav-view {
		border-bottom-right-radius : 10px;
		border-bottom-left-radius  : 10px;
	}
	#navigation {
		border-bottom-left-radius  : 10px;
	}
}
}

@-moz-document url-prefix("https://userstyles.org/categories/app"), url-prefix("https://userstyles.org/categories/site") {
/* code section 2 / 10 */

.style_category_card_wrapper {
	background-color : #000;
}
#category_style_card_title {
	color : #95b3c7;
}
}

@-moz-document url-prefix("https://userstyles.org/styles/") {
/* code section 3 / 10 */

.style_title ,
#share_div ,
#headline ,
#information_key_left ,
#infomation_value_left ,
#applies_link ,
#information_key ,
#information_value ,
.text_style_page {
	color : #95a0a5;
}
#notes_textarea {
	background-color : #000;
	color            : #95a0a5;
}
#install_style_button ,
#install_style_button:hover ,
#archive_style_button ,
#archive_style_button:hover {
	box-shadow : 0 6px 16px 0 rgba(0, 0, 0, 0.35);
}
iframe {
	filter     : grayscale(100%) brightness(50%);
	transition : all linear 2s;
}
iframe:hover {
	opacity : 1;
	filter  : grayscale(0%) brightness(100%);
}
iframe[src^="/"] ,
iframe[src^="/"]:hover {
	filter : none;
	transition : none;
}
.archived_text {
	color : #95a0a5;
}
.white_button ,
#css_button {
	background-color : #000;
	color            : #3498db;
	border           : 1px solid #3498db;
}
#stylish-code {
	font-family : monospace;
}
#style_installed_button {
	background-color : #2ecc71;
	border-color     : #f5f7fa;
	color            : #f5f7fa;
}

#style_installed_button img[src$=".svg"] {
	border        : 1px solid #f5f7fa;
	border-radius : 15px;
}

.advancedsettings_shown {
	background-color : #000;
}
.title_setting {
	color : #3498db;
}
#style-settings select {
	background-color : #333;
	color            : #FFF;
}
@media (max-width: 1024px) {
	#style_info {
		position : absolute;
		right : 10px;
	}
}
}

@-moz-document url-prefix("https://userstyles.org/d/") {
/* code section 4 / 10 */

body {
	background-color : #222;
	color            : #95a0a5;
}
input ,
textarea {
	background-color : #000;
	border           : 1px solid #95a0a5;
	border-radius    : 3px;
	color            : #95a0a5;
}

}

@-moz-document url-prefix("https://userstyles.org/d/styles/new") {
/* code section 5 / 10 */

body ul li {
	color : #FFF;
}

input[type="text"] ,
input[type="url"] {
	padding    : 10px;
}
#style_short_description ,
#style_long_description ,
#style_additional_info , 
#css ,
#example-url ,
.form-controls table ,
#style_pledgie_id ,
#license-none {
	margin-top : 10px;
}
label[for="license"] {
	display       : block;
	margin-bottom : 10px;
}
.linking-note {
	display    : block;
	margin-top : 10px;
}

}

@-moz-document url-prefix("https://userstyles.org/users/80032") {
/* code section 6 / 10 */

@media (min-width: 1237px) {
	#view {
		min-width : 1000px;
	}
	#view_inner {
		width : 100%;
	}
}
/*
@media (min-width: 969px) and (max-width: 1236px) {
	#view {
		min-width : 100%;
	}
	#view_inner {
		width : 100%;
	}
}
*/
}

@-moz-document url-prefix("https://userstyles.org/d/users/") {
/* code section 7 / 10 */

h2 ,
th ,
td {
	color : #95a0a5;
}
.numeric-value {
	text-align : center;
}

thead tr th {
	background-color : #393939;
}
tr:nth-of-type(2n) {
	background-color : #393939;
}

th:empty {
	border-top  : 0px;
	border-left : 0px;
	background-color : transparent;
}
/*
tr.style-warnings {
	display : table-row;
}
*/
tr.style-warnings .style-warning {
	background-color : transparent;
	color            : #FF6;
	padding          : 5px;
	font-size        : 0.965em;
}
tr:nth-of-type(2n) {
	background-color : transparent;
}
tr:not(.style-warnings):nth-of-type(2n) {
	background-color : #393939;
}

.author-styles tr td.obsolete a {
	color           : #dc1111 !important;
	text-decoration : none;
}
.author-styles tr:not(.style-warnings) th ,
.author-styles tr:not(.style-warnings) td {
	vertical-align : top;
}
.author-styles tr:not(.style-warnings) td {
	padding-bottom : 5px;
	padding-left   : 3px;
	padding-top    : 3px;
}
/*
.author-styles thead tr th:nth-of-type(6):before {
	content : 'Recent disc.';
	display : table-cell;
}
.author-styles thead tr th:nth-of-type(6) {
	width    : 66px;
	overflow : hidden;
	height   : 2em;
}
*/
}

@-moz-document url-prefix("https://userstyles.org/d/help/widget?") {
/* code section 8 / 10 */

pre {
	border-color     : #95a0a5;
	border-style     : solid;
	border-radius    : 3px;
	background-color : #000;
}
select {
	background-color : #000;
	border           : 1px solid #95a0a5;
	border-radius    : 3px;
	color            : #95a0a5;
}
}

@-moz-document url-prefix("https://userstyles.org/styles/browse") {
/* code section 9 / 10 */

#centralMsg {
	color : #95a0a5;
}
}

@-moz-document url-prefix("https://userstyles.org/contact"), url-prefix("https://userstyles.org/d/contact") {
/* code section 10 / 10 */

.contact-bold-font {
	color : #FFF;
}
.contact-input {
	background-color : #000;
}
.contact-input-large {
	background-color : #000;
}
.contact-font {
	color : #FFF;
}
}
