css:
@-moz-document domain(2ememain.be) {
  /*////////////////////////////////////////*/
  /*   Create by Phantonium  */
  /*////////////////////////////////////////*/
  /*   Create  20 / 12 / 2017   */
  /*////////////////////////////////////////*/

  /* PRINCIPAL */
  body {
      background-color: #263c56;
  }
  a {
      color: #03A9F4;
      transition: color 0.8s;
  }
  a:hover,
  a:focus {
      color: #FFF;
  }

  /* AUTRE... */
  .site-header {
      background-color: #25363e;
  }
  .site-header .desktop-menu {
      background-color: #25363e;
  }
  .ui-button-quaternary {
      border: 2px solid #0065a4;
      background-color: transparent;
      color: #d6d6d6;
      transition: all 0.8s;
  }
  .ui-button-quaternary:hover {
      border: 2px solid #AAA;
      background-color: #333;
      color: #FFF;
  }
  .ui-button-clean-alt {
      color: #6b6b6b;
  }
  .search-term {
      background-color: #305177;
  }
  .search-bar {
      background-color: #305177;
  }
  .search-bar-content {
      background-color: #305177;
  }
  .search-bar .select-distance .autocomplete-input {
      background-color: #545454;
      color: #CCC;
  }
  .search-term input[type=search] {
      background-color: #545454;
      color: #CCC;
  }
  .search-options .custom-select {
      border: 2px solid #545454;
  }
  .search-options .autocomplete-input, .search-options .custom-select {
      background-color: #244961;
  }
  .search-options .autocomplete-input:hover, .search-options .custom-select:hover,
  .search-options .autocomplete-input:focus, .search-options .custom-select:focus {
      background-color: #267294;
  }
  option {
      background: #333;
      color: #AAA;
  }
  .browse-departments .ui-button-tertiary {
      background-color: #244961;
      border: 2px solid #545454;
  }
  .browse-departments .ui-button-tertiary:hover,
  .browse-departments .ui-button-tertiary:focus {
      background-color: #267294;
  }
  .search-bar .browse-departments .ui-button-tertiary {
      border-color: #545454;
  }
  .browse-departments .is-visible .ui-button-tertiary {
      background-color: #267294;
      border-color: #267294;
      color: #b7b7b7;
  }
  .browse-departments .is-visible .ui-button-tertiary:focus, 
  .browse-departments .is-visible .ui-button-tertiary:hover {
      background-color: #2a4769;
      border-color: #2a4769;
  }
  .browse-departments .browse-levels {
      background-color: #2a4769;
  }
  .browse-departments .browse-levels:before {
      background-color: #2a4769;
  }
  .ui-link-dark-menu {
      color: #b6b6b6;
  }
  .ui-link-dark-menu:hover,
  .ui-link-dark-menu:focus {
      color: #fff;
  }
  /*  Create by Phantonium */
  .search-bar-wrapper {
      box-shadow: 0px 0px 20px 0px rgba(0, 0, 0, 0.56);
  }
  .browse-departments .list-description>a {
      color: #b6b6b6;
  }
  .browse-departments .list-description>a:hover,
  .browse-departments .list-description>a:focus {
      color: #fff;
  }
  .browse-departments .list-description .label {
      border-right: 2px solid #3F51B5; 
  }
  .browse-departments .list-description {
      border-bottom: 2px solid #3f51b5;
  }
  .browse-departments .options-wrapper {
      background-color: #305177;
  }
  /*  Create by Phantonium */
  .home-top-item {
      background-color: #335884;
  } 
  .home-top-item {
      border: 2px solid #244961;
      border-bottom: 8px solid #244961;
  }
  .bottom-row .home-top-item {
      border-bottom: 8px solid #244961;
  }
  .panel {
      background-color: #436a98;
      border: 1.5px solid #1c6284;
      border-radius: 4px;
      color: #CCC;
  }
  .home-tip .tip-info {
      width: 95%;
  }
  .shortcuts .icon {
      height: 50px;
      width: 50px;
      margin-bottom: 8px;
      margin-left: 20px;
      margin-right: 20px;
      border-radius: 50px;
      background-color: #436a98;
  }
  .autocomplete-results-wrapper {
      box-shadow: 0px 7px 20px 2px #000;
  }
  .recent-search-header {
      border: 1px solid #244961;
      background-color: #25363e;
      color: #AAA;
  }
  .recent-searches {
      border-color: #244961;
  }
  .autocomplete-results-wrapper .selected-list-item {
      background-color: #25363e;
  }
  .autocomplete-results-wrapper .search-link {
      color: #FFF;
  }
  .home-top-item .top-item-price {
      color: #fff;
  }
  .site-header .user-menu {
      background-color: #334f6f;
  }
  .site-header .user-menu .menu-item.menu-item-secondary {
      background-color: #5a3636;
      transition: background-color 1s ;
  }
  .site-header .user-menu .menu-item.menu-item-secondary:hover,
  .site-header .user-menu .menu-item.menu-item-secondary:focus {
      background-color: #863535;
  }
  .ui-button-clean-alt {
      color: #b9b9b9;
      transition: color 0.8s ;
  }
  .ui-button-clean-alt:hover,
  .ui-button-clean-alt:focus {
      color: #FFF;
  }
  .site-header .user-menu:before {
      color: #334f6f;
      text-shadow: 0 -4px 6px #156fd6;
  }
  /* SUITE */
  .top-bar-container {
      background-color: #305177;
      border-bottom: 1.5px solid #3F51B5;
  }
  .dfp-top-bar {
      background-color: #263c56;
      box-shadow: inset 0 0 20px 0px black;
  }
  input[type=email], input[type=file], 
  input[type=number], input[type=password],
  input[type=search], input[type=tel], 
  input[type=text], input[type=url], 
  textarea {
      border: 1.5px solid #3F51B5;
      background: #444;
      color: #AAA;
  }
  .my-menu .username {
      font-size: 20px;
      color: #DDD;
  }
  .sidebar-title {
      color: #cecece;
  }
  .breadcrumb .breadcrumb-list li a {
      color: #9e9e9e;
  }
  .breadcrumb .active-breadcrumb-content {
      color: #BBB;
  }
  .sell-your-item-form {
      background-color: #305177;
      border: 2px solid #3F51B5;
      color: #CCC;
  }
  .sell-your-item-form h2 {
      color: #DDD;   
  }
  .enhanced.custom-checkbox label .icon-checkbox {
      background-color: #333;
  }
  .js .custom-select {
      border: 1px solid #3F51B5;
      background-color: #444444;
  }
  .sell-your-item-form .options-info,
  .info-description,
  .advanced-search-form .options-info{
      background-color: #2f3a7b;
      border-top: 2px solid #3F51B5;
      border-bottom: 2px solid #3F51B5;
  }
  .my-profile .user,
  .advanced-search-form{
      background-color: #305177;
      border: 1.5px solid #3F51B5;
      border-radius: 8px;
      color: #CCC;
  }
  .my-profile .about-user {
      border-bottom: 1.8px solid #3F51B5;
  }
  .my-profile .personal {
      border-bottom: 1.8px solid #3F51B5;
  }
  .my-profile .user-photo .no-photo, 
  .my-profile .user-photo img {
      border: 2.2px solid #3F51B5;
  }
  .my-profile .password li {
      background: #152a42;
  }
  .ratings .user-ratings {
      background-color: #2f3a7b;
      border: 2px solid #3F51B5;
      color: white;
  }
  /* ARTICLE */
  .panel-item footer {
      background-color: #263c56;
      border-top: 1.6px solid #3F51B5;
  }
  .panel-item {
      background-color: #305177;
      border: 1.5px solid #3F51B5;
      border-radius: 8px;
      color: #CCC;
  }
  .contact-options .contact-inner {
      background: #263c56;
      border-left: 1.6px solid #3F51B5;
  }
  .contact-options .contact-highlight .contact-button:focus .contact-inner,
  .contact-options .contact-highlight .contact-button:hover .contact-inner {
      background: #2a4769;
  }
  .contact-options .contact-highlight .contact-button:focus .contact-option-addon,
  .contact-options .contact-highlight .contact-button:hover .contact-option-addon {
      background: #2a4769;
  }

  .contact-options .contact-button:focus .contact-inner,
  .contact-options .contact-button:hover .contact-inner {
      background-color: #2a4769;
  }
  .contact-options .contact-option-addon {
      background: #263c56;
      color: #DDD;
      font-size: 15px;
      padding: 4px;
      border-left: 1.6px solid #3F51B5;
      border-top: 1.6px solid #3F51B5;
      border-bottom: 1px solid #3F51B5;
  }
  .contact-options .contact-option-addon:hover,
  .contact-options .contact-option-addon:focus  {
      background-color: #2a4769;
  }

  .contact-options .contact-highlight .contact-button:focus .contact-inner,
  .contact-options .contact-highlight .contact-button:hover .contact-inner {
      background: #2a4769;
  }

  .contact-options .contact-highlight .contact-button:focus .contact-option-addon,
  .contact-options .contact-highlight .contact-button:hover .contact-option-addon {
      background: #2a4769;
  }
  .contact-options .contact-button:focus .contact-option-addon,
  .contact-options .contact-button:hover .contact-option-addon {
      background: #2a4769;
  }
  .listed-adv-item .listed-item-no-photo, .listed-adv-item .listed-item-photo {
      background-color: #25363e;
      box-shadow: inset 0 0 18px #3F51B5;
  }
  .listed-adv-item .listed-item-details, .listed-adv-item .listed-item-imagery {
      background-color: #25363e;
      border: 2px solid #3F51B5;
  }
  .dfp-button {
      background: #555;
      border-color: #3F51B5;
  }
  .item .item-kenmerken {
      border-bottom: 1.6px solid #3F51B5;
  }
  .image-viewer .image-carousel {
      border-top: 1.6px solid #3F51B5;
      border-bottom: 1.6px solid #3F51B5;
  }
  .advanced-search-form .submit-wrapper.is-fixed .fixed-button-wrapper {
      background-color: #263c56;
      border-top: 1px solid #3F51B5;
      border-left: 1px solid #3F51B5;
      border-right: 1px solid #3F51B5;
  }
}
