css:
@-moz-document domain(tukif.com) {
/*////////////////////////////////////////*/
/*   Create by Phantonium  */
/*////////////////////////////////////////*/
/*   Create  21 / 12 / 2017   */
/*////////////////////////////////////////*/
    /* PRINCIPAL */
    body {
                                            background: #404040;
                                            color: #CCC;
    }
    a {
                                            color: #FFF;
    }
    a:hover,
    a:focus {
                                            color: #ff28cf;
    }
    textarea {
                                            background: #323232;
                                            outline: 0;
                                            border: 0px;
                                            border-top: 2.5px solid #ff28cf;
                                            border-bottom: 2.5px solid #ff28cf;
                                            margin-top: 16px;
                                            box-shadow: 0 0 20px 2px black;
                                            padding: 8px 18px;
    }
    /*/////////////////////////////////*/
    /*/////////////////////////////////*/
    /* AUTRE... */

    .sub_header_slogan, 
    .sub_header_slogan a {
                                            color: #c55858;
    }
    .sidebar_section_item {
                                            color: #FFF;
    }
    .sidebar_section_item:hover,
    .sidebar_section_item:focus {
                                            color: #ff28cf;
    }
    .content_section_content {
                                            border-top: 3px solid #ff28cf;
                                            background-color: #333;
                                            box-shadow: inset 0 0 20px black;
    }
    .header_wrapper {
                                            background-color: #333;
                                            border-bottom: 1px solid #ff28cf;
                                            box-shadow: inset 4px -5px 20px black;
    }
    .sidebar_section_content {
                                            border: solid #ff28cf;
    }
    img.logo_img_desktop {
                                            display: inline-block;
                                            border-top: 2px solid #ff28cf;
                                            border-right: 2px solid #ff28cf;
                                            border-left: 2px solid #ff28cf;
                                            border-radius: 15px 15px 5px 5px;
    }
    .sidebar_section_subtitle {
                                            color: #DDD;
                                            background-color: #2f2f2f;
                                            box-shadow: 0px -2px 12px black;
    }
    .sidebar_main_categories_wrapper .dropdown_btn {
                                            border: 0px solid transparent;
                                            border-bottom: #924a82 solid 3px;
                                            background-color: #636363;
                                            color: #DDD;
                                            box-shadow: inset -30px 0px 9px 0px black;
    }
    .sidebar_main_categories_wrapper .dropdown_btn .dropdown_caret {
                                            color: #FFF;
                                            border-bottom: #924a82 solid 3px;
                                            background-color: #4f4f4f;
    }
    .search_btn {
                                            border-radius: 12px;
                                            background-color: #ff28cf;
    }
    .search_text {
                                            border: 1.5px solid #2f2f2f;
                                            background-color: #636363;
                                            color: #EEE;
                                            box-shadow: inset 0px 0px 15px 0px #111;
    }
    .search_text:focus {
                                            outline: 0;
    }
    .search_wrapper .dropdown_btn {
                                            background-color: transparent;
                                            border-radius: 50px;
    }
    .header_button,
    .showcase_profile_button,
    .btn_post_action,
    .btn_orange,
    .btn_comments_activate,
    .btn_comments_new{
                                            border-top: #a03288 solid 0px;
                                            border-left: #a03288 solid 0px;
                                            border-right: #a03288 solid 2px;
                                            border-bottom: #a03288 solid 4px;
                                            background-color: #ff28cf;
                                            transition: all 1s;
    }
    .header_button:hover,
    .showcase_profile_button:hover,
    .btn_post_action:hover,
    .btn_orange:hover,
    .btn_comments_activate:hover,
    .btn_comments_new:hover{
                                            border-right: #a03288 solid 0px;
                                            border-bottom: #a03288 solid 0px;
                                            border-left: #a03288 solid 2px;
                                            border-top: #a03288 solid 4px;
                                            background-color: #a91c89;
                                            transition: all 2s;
    }
    .load_more_wrapper {
                                            border: 1px solid #212121;
                                            background-color: #4e4a4a;
    }
    .filter_wrapper .dropdown_btn,
    .thumb_size_wrapper .dropdown_btn{
                                            background-color: #636363;
                                            color: #CCC;
                                            border: 1px solid #333;
    }
    .dropdown_caret {
                                            background-color: #4f4f4f;
    }
    .filter_wrapper {
                                            background-color: #404040;
    }
    .showcase_item_wrapper {
                                            border-top: 2px solid #ff28cf;
                                            background-color: #353535;
    }
    .showcase_ranking_txt {
                                            color: black;
    }
    .vjs-progress-control .vjs-play-progress {
                                            background: #ff28cf !important;
    }
    .video-js .vjs-play-progress:before {
                                            top: -0.38em;
                                            font-size: 14px;
    }
    .vjs-progress-control .vjs-mouse-display:before, .vjs-progress-control .vjs-play-progress:before {
                                            color: #ff0057 !important;
    }
    .video_stats {
                                            background-color: transparent;
                                            border-right: 1px solid #434343;
                                            border-left: 1px solid #434343;
                                            border-bottom: 2px solid #555;
                                            box-shadow: inset 0px -7px 20px #232323;
    }
    .comments_top_title {
                                            padding-left: 25px;
                                            background-color: #4e4e4e;
    }
    .comment_action_item, .comment_highlighted {
                                            background-color: #272727;
                                            color: #EEE;
    }
    .comments_section_wrapper {
                                            box-shadow: 0px 5px 12px 0px black;
    }
    .dropdown_menu {
                                            border: 1px solid #000000;
                                            background-color: #353535;
                                            color: #DDD;
    }
    .dropdown_menu_item:hover {
                                            background-color: #333;
    }
    .dropdown_menu_item {
                                            color: #DDD;
    }
    .main_menu_item {
                                            transition: all 0.8s;
    }
    .main_menu_item:hover {
                                            background: #555;
                                            transition: all 0.8s;
    }
    .pornstar_add_btn, 
    .pornstar_modify_btn, 
    .video_add_category_btn, 
    .video_add_pornstar_btn, 
    .video_title_description_btn {
                                            border: 1px solid #222;
                                            background-color: #ff28cf;
    }
    .pornstar_add_btn:hover, 
    .pornstar_modify_btn:hover, 
    .video_add_category_btn:hover, 
    .video_add_pornstar_btn:hover, 
    .video_title_description_btn:hover {
                                            border: 1px solid #222;
                                            background-color: #a91c89;
    }
    .pornstar_modify_btn .btn_icon_wrapper, 
    .video_add_category_btn .btn_icon_wrapper, 
    .video_add_pornstar_btn .btn_icon_wrapper, 
    .video_title_description_btn .btn_icon_wrapper {
                                            background-color: transparent;
    }
    .video_description_item>a {
                                            color: #e600ff;
    }

    /*PARAMETRE SUPLEMENTAIRE*/
    ::-webkit-scrollbar {
                                            width: 12px;
    }

    ::-webkit-scrollbar-track { /* BARRE FIXE */
                                            background: #444;
    }

    ::-webkit-scrollbar-thumb { /* BARRE QUI BOUGE */
                                            border-radius: 10px;
                                            background: #666;
    }
    ::-webkit-scrollbar-thumb:hover { 
                                            background: #999;
    }

    ::selection {
                                            background: #ffb1ee;
                                            color: black;
    }
}
