css:
@-moz-document url-prefix("https://learning.oreilly.com/library/view") {
/* info page of a book ------------------------------------------------------------ */
 #container {
	max-width: 56rem;
}
.pod {
	padding: 4rem;
	font-size: 1rem;
}
/* title info */
 .pod .title-info {
	/* 	text-align: left; */
	/* 	margin-bottom: 0; */
	padding-bottom: 2em;
	border-bottom: 1px solid #eee;
}
@media(min-width: 768px) {
	[class*=detail-].pod .cover {
		width: 20.69863%;
		margin: 0 1em 1.2em 0;
	}
}
.pod .title-info .title {
	margin: 0 0 .6em;
	/* 	font-size: 1.475rem; */
	font-weight: 600;
}
.pod .title-info .metadata div, .topic-display li {
	font-size: 1rem;
}
/* add to playlist */
 .menu-control {
	width: 100%;
}
.js-content-uri .collections-dropdown {
	margin-bottom:0;
}
.js-playlist-icon {
	padding: 0;
	margin: .4em 0 0 .2em;
}
.metadata .js-playlist-icon {
	width: 100%;
}
.metadata .js-playlist-icon:after {
	content:"add to playlist";
	margin: .4em;
	/* 	text-align: right; */
	text-transform: capitalize;
}
.icon-add-to-playlist-sml {
	position: relative;
	bottom: -2px;
	right: 0;
}
/* btn-action */
[class*=detail-].pod .action {
	float: left;
	width: auto;
	padding: 0;
	/* 			margin-left: 13.698630137%; */
}
@media(min-width: 1024px) {
	[class*=detail-].pod .action {
		margin-left: 20.69863%;
	}
}
@media(min-width: 768px) {
	[class*=detail-].pod .action {
		margin-left: 0;
	}
}
[class*=detail-].pod .action div {
	display: inline-block;
}
[class*=detail-].pod .action a {
	width: auto !important;
	/* 	border: none; */
	margin: auto 1.2em auto auto;
	/* 	padding: 0; */
	font-size: inherit;
	font-weight: 600;
	text-transform: none;
}
/* description ------------------------------------------------------------ */
[class*=detail-].pod h2 {
	margin: 1.2em 0 1em;
	/* 	font-size: 1.3rem; */
	/* 	font-weight: 600; */
}
.pod .publisher-logo {
	/* 	background: white; */
	/* 	padding: .6em .8em; */
	margin-left: .8em;
}
/* toc ------------------------------------------------------------ */
 .detail-toc .collections-dropdown {
	margin-left: -33px;
}
.detail-toc ol {
	margin: 0;
}
.detail-toc .toc-level-1>ol {
	margin-bottom: .4em;
}
.detail-toc .toc-level-2>ol {
	margin-bottom: .6em;
}
.detail-toc li {
	margin: 0;
	/* 	padding: .2em 0 .2em 1.5em; */
}
.detail-toc li a {
	color: rgba(0, 0, 0, .8);
	line-height: 1.3;
	/* 	padding: .1em 0 .3em; */
	padding: .2em 0;
	width: 100%;
}
.detail-toc .toc-level-1 {
	padding: .8em 0 .8em 1.5em;
	border-bottom: 1px solid rgba(0, 0, 0, .1);
}
.detail-toc .toc-level-1>a {
	font-size: 1.3em;
	font-weight: 600;
}
.detail-toc .toc-level-2>a {
	font-size: 1.2em;
	/* 	font-weight: 600; */
}
.detail-toc .toc-level-3 a {
	font-size: 1em;
}
.detail-toc .toc-level-4 a {
	color: rgba(0, 0, 0, .38);
}
/* line before toc */
 .toc-level-3.t-toc-level-3.js-content-uri
/* , .toc-level-2.t-toc-level-2.js-content-uri  */
 {
	margin-left: .4em;
	padding-left: 1.4em;
	/* 	border-left: 1px rgba(246, 93, 34, .3) solid; */
	border-left: 1px rgba(0, 0, 0, .3) dotted;
}
.toc-level-5.t-toc-level-5.js-content-uri, .toc-level-4.t-toc-level-4.js-content-uri {
	margin-left: .4em;
	padding-left: 1.4em;
	border-left: 1px rgba(0, 0, 0, .3) dotted;
}
}

@-moz-document url-prefix("https://www.safaribooksonline.com/search"), url-prefix("https://learning.oreilly.com/search") {
/* search page of learning.oreilly.com ------------------------------------------------------------ */

/* hitted results */
 .highlight {
	color: #fa4150;
	/* 	color: #5581d5; */
	/* 	color: #0066c0; */
}
p strong {
	font-weight: normal;
}
/* book info */
 h4 {
	font-size: 1.2rem !important;
	margin-bottom: .6em !important;
}
p a {
	color: #0066c0;
}
.src-RatingStars-writeFirstReview, .src-RatingStars-writeFirstReview a {
	color: #3d3b49;
	font-size: 14px;
}
/* publisher */
 span a, span span {
	font-weight: normal!important;
}
}

@-moz-document regexp("https...learning.oreilly.com.library.view.*xhtml.*"), regexp("https...learning.oreilly.com.library.view.*html.*"), regexp("https...www.safaribooksonline.com.library.view.*xhtml.*"), regexp("https...www.safaribooksonline.com.library.view.*html.*") {
/* books' content pages ------------------------------------------------------------ */
 #container {
	max-width: none;
}
.js-toc {
	/* 	background: #525659; */
	background: rgb(82, 86, 89);
	background: rgba(0, 0, 0, .20);
}
#sbo-rt-content {
	background: white;
	margin: 2em auto !important;
	padding: 2em 4em;
}
#sbo-rt-content div {
	font-family: source sans pro, Helvetica, sans-serif;
}
}
