@-moz-document domain("groups.google.com") {
/* Background colour */
body {
    background-color: #222 !important;
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-attachment: scroll !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
}

/* Main text colour */
div {color: #c0c0c0;}

/* Answer input */
div#p-b-0 {background-color: #181818 !important;}

/* Reply bar */
div.F0XO1GC-md-b {
    background-color: transparent !important;
    border: 1px solid #333 !important;
    border-top: 1px solid #333 !important;
}

/* Top bar */
.gb_Qf {background: #181818 !important;}

/* 9 square icon */
#gb #gbwa > div > a[title="Google apps"] {
    background: transparent url("http://i.imgur.com/77PNFVV.png") no-repeat center !important;
    border: 1px solid transparent !important;
}

/* Search Input */
.gbqfqw {background: #444 !important;}
.gssb_m {
    background: #333 !important;
	margin-bottom: 1px !important;
}
.gwt-SuggestBoxPopup, .gwt-PopupPanel, .modal-dialog {
    background: #333 !important;
}
input[type="text"], input[type="password"] {
    background-color: #444 !important;
    color: #f1f1f1 !important;
    border: 0px solid #d9d9d9;
    border-top: 0px solid #c0c0c0;
}

/* Search Button */
#gbqfb {background: #222 !important;}

/* Horizontal lines */
.F0XO1GC-b-o {border-bottom: 1px solid #333 !important;}
.F0XO1GC-nb-W.F0XO1GC-mb-l {border-bottom: 1px solid #333 !important;}
.F0XO1GC-mb-x {border-bottom: 1px solid #333 !important;}
.F0XO1GC-f-h {border: 1px solid #333 !important;}
.F0XO1GC-nb-W.F0XO1GC-mb-o {border-top: 1px solid #333 !important;}
.F0XO1GC-d-k {border-top: 1px solid #333 !important;}
.F0XO1GC-p-r {border-bottom: 1px solid #333 !important;}
.F0XO1GC-p-M {border-bottom: 1px solid #333 !important;}
.F0XO1GC-p-o td {border-bottom: 1px solid #333 !important;}

/* Vertical line */
.F0XO1GC-f-A .F0XO1GC-f-C {border-left: 1px solid #333;}

/* Left bar headings */
.F0XO1GC-d-B a, .F0XO1GC-d-C a, .F0XO1GC-d-B a:visited {color: #555 !important;}
h1, h2, h3, h4, h5 {color: #888 !important;}
.F0XO1GC-d-n {background-color: #333 !important;}

/* Main view */
a.F0XO1GC-p-Q, a.F0XO1GC-p-Q:visited {color: #c0c0c0 !important;}
.F0XO1GC-rb-b {color: #666 !important;}
.F0XO1GC-p-V tr, .F0XO1GC-p-o tr {background-color: rgba(255,255,255,0.05) !important;}
.F0XO1GC-p-X tr.F0XO1GC-p-M {background-color: transparent !important;}

/* Authors name */
.F0XO1GC-D-a {color: #CCC !important;}

/* Title */
.F0XO1GC-b-Yb {color: #ccc !important;}

/* Links */
a {color: #4183C4 !important;}

/* Welcome */
.F0XO1GC-xb-b {background-color: transparent !important;}
.F0XO1GC-p-Z {border-bottom: 1px solid #333 !important;}

/* This message has been deleted */
.F0XO1GC-nb-C {background-color: #181818 !important;}
}
