css:
@-moz-document domain(rencontre-ados.net) {
/*////////////////////////////////////////*/
/*   Create by Phantonium  */
/*////////////////////////////////////////*/
/*   Create  21 / 12 / 2017   */
/*////////////////////////////////////////*/
  /* PRINCIPAL */
  body {
      background: #1a1423;
      color: #BBB;
  }
  label {
      color: #bfbfbf;
  }
  header {
      background: #711336;
  }
  input, 
  textarea, 
  select {
      border: 1px solid #343434;
      background: #555;
      transition: all 0.8s;
      outline: 0;
  }
  input:focus, 
  textarea:focus, 
  select:focus {
      border: 1px solid #AAA;
      background: #333;
  }

      /*PARAMETRE SUPLEMENTAIRE*/
      ::-webkit-scrollbar {
                                              width: 12px;
      }

      ::-webkit-scrollbar-track { /* BARRE FIXE */
                                              background: #444;
      }

      ::-webkit-scrollbar-thumb { /* BARRE QUI BOUGE */
                                              border-radius: 10px;
                                              background: #666;
      }
      ::-webkit-scrollbar-thumb:hover { 
                                              background: #999;
      }

      ::selection {
                                              background: #ffb1ee;
                                              color: black;
      }
  /*//////////////////////////////////////*/
  /*//////////////////////////////////////*/
  /* AUTRE... */
  .alert-info {
      background: #115784;
  }

  .g7 > a {    
      background: #542269;
      box-shadow: 3px -3px 17px 2px black;
  }
  .white {
      background: #404040;
      border: 1.6px solid #673AB7;
  }
  table.feed tr {
      border-bottom: 2px solid #673ab7;
  }
  .settings.active {
      background: #a02553;
      background: -moz-linear-gradient(top, rgb(113, 19, 54) 0%, #a02553 100%);
      background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgb(113, 19, 54)), color-stop(100%,#a02553));
      background: -webkit-linear-gradient(top, rgb(113, 19, 54) 0%,#a02553 100%);
      background: -o-linear-gradient(top, rgb(113, 19, 54) 0%,#a02553 100%);
      background: -ms-linear-gradient(top, rgb(113, 19, 54) 0%,#a02553 100%);
      background: linear-gradient(to bottom, rgb(113, 19, 54) 0%,#a02553 100%);
  }
  .settings .quick-access {
      background: #a02553;
      box-shadow: 2px 2px 2px rgba(0, 0, 0, 0.56);
      top: 64.8px;
  }
  .btn.green {
      background: #56772a;
  }
  .btn.green:hover {
      background: #304811;
  }
  .btn.pink {
      background: #903558;
  }
  .btn.pink:hover {
      background: #5f1833;
  }
  .btn.red {
      background: #a5352a;
  }
  .btn.red:hover {
      background: #65160e;
  }
  .btn.orange {
      background: #b37614;
  }
  .btn.orange:hover {
      background: #754d0b;
  }
  .btn {
      background: #1f5b82;
      transition: all 0.8s;
  }
  .btn:hover {
      background: #0f3d5a;;
  }
  .chrome-extension .mainbar {
      background-color: #4d5052;
  }
  .chrome-extension .act-tab,
  .chrome-extension .address-bar{
      background-color: #74797b;
  }
  .chrome-extension .address-bar .input-web {
      background: #abaaaa;
      color: #424242;
  }
  .chrome-extension .extension-body {
      background: #807b7b;
      border-color: #807b7b;
  }
  .noUi-base {
      background: #6f7071;
  }
  .noUi-connect {
      background: #4f0fc1;
  }
  .noUi-handle {
      background: #673AB7;
      box-shadow: 0 0 0px black;
  }
  .noUi-handle:hover,
  .noUi-handle:focus {
      box-shadow: 0 0 15px black;
  }
  .form .errors,
  .form .errors {
      color: #fff;
      background: #a50d1a;
  }
  .form-switch .form-icon {
      background: #555;
      border: .1rem solid #333;
  }
  .form-switch input:checked+.form-icon {
      background: #195b86;
      border-color: #17405a;
  }
  .form-switch .form-icon::after {
      background: #462386;
      box-shadow: 0 0 4px black;
  }
  .btn.grey, .btn.grey:hover {
      background: #555555;
  }
  .list-vcard .little-vcard {
      background: #291744;
      border: 1.8px solid #673AB7;
  }
  .list-vcard .little-vcard .actions {
      background: #1b1129;
      border-top: 1.5px solid #673ab7;
  }
  .list-vcard .little-vcard .who {
      color: #AAA;
  }
  .hidden-text .gradient {
      background: linear-gradient(to bottom, rgba(26, 20, 35, 0.33) 0%,rgb(26, 20, 35) 100%);
  }
  .nav-profil {
      background: #2c2538;
      border: 1px solid #673AB7;
  }
  .nav-profil li span {
      border-bottom: 4px solid #673ab7;
  }
  .load-more {
      border-top: 1px solid #673ab7;
  }
  table.feed tr td.detail a img {
      border: 2px solid #333;
      border-radius: 8px;
  }

  .g12 .album.bg-girl .photo, 
  .album.bg-boy .photo {
      transition: all 0.8s;
  }
  .g12 .album.bg-girl .photo:hover .infos, 
  .album.bg-boy .photo:hover .infos {
      background: #4d2a82;
      background: rgba(88, 31, 173, 0.4);
  }
  .action {
      border-top: 1px solid #673AB7;
  }
  .warning {
      background: #8a3c23;
  }
  .article-text {
      background: #232029;
      border-left: 1px solid #333;
      border-right: 1px solid #333;
      border-bottom: 2px solid #333;
  }
  .article-box .article-cover {
      border: 1px solid #333333;
  }
  .tabs, .editor {
      background: #555555;
      border: 1px solid #2d2d2d;
  }
  .tabs ul li.active, .tabs ul li.active:hover {
      box-shadow: inset 0 -2px 0 #c81fe4;
  }
  .smiley-list .tabs ul li {
      border-right: 1px solid #2d2d2d;
  }
  .chat .smiley-box, .message .smiley-box {
      border-top: 1px solid #2d2d2d;
      border-left: 1px solid #2d2d2d;
  }
  .smiley-box {
      background: #555555;
      border-bottom: 1.6px solid #3a275a;
  }
  .tabs ul li, .editor ul li {
      border-right: 1px solid #333333;
      color: #dedede;
  }
  .editor ul li.right {
      border-right: 0;
      border-left: 1px solid #333333;
      float: right;
  }
  .fullscreen.active .editor {
      border-top: 1px solid #333333;
  }
  .editor ul li a {
      color: #ffffff;
  }
  #qolduhqp > div {
      background: #424242;
      border: 2.5px solid #673AB7;
      border-radius: 20px / 60px;
  }
}
