css:
@-moz-document domain(userstyles.org) {
/*////////////////////////////////////////*/
/*   Create by Phantonium  */
/*////////////////////////////////////////*/
/*   Create  21 / 12 / 2017   */
/*////////////////////////////////////////*/
/*         VERSION 1.0         */
/*////////////////////////////////////////*/
body {
	background-color: #1a3548;  
    color: #CCC;
}
a,
a:visited {
   color: white; 
    text-decoration: none;
}
a:hover,
a:focus,
a:visited:hover,
a:visited:focus {
    color: #AAA;
    text-decoration: underline;
}
dl > dd {
	margin-bottom: 12px;
}
pre {
    background-color: #403f3d;
}
h4 a,
h4 a:visited {
    color: #DDD !important;
}
h4 a:hover,
h4 a:visited:hover {
    color: #fff !important;
}
h1 {
	color: #BBB;
}
label {
	font-size: 17px;
	padding-bottom: 5px;
}
input[type=text],
input[type=url],
textarea {
	background: #343434;
	color: #CCC;
	border: 3.5px solid #222;
	border-top: 2.5px solid #555;
	border-left: 2.5px solid #555;
	outline: 0;
	padding: 8px 15px;
	transition: all 0.8s;
	width: 750px;
}
input[type=text]:focus,
input[type=url]:focus,
textarea:focus {
	background: #565656;
	color: #FFF;
	border: 2.5px solid #777;
	border-top: 3.5px solid #444;
	border-left: 3.5px solid #444;
}
input[name=new_screenshot_description_1],
input[name=new_screenshot_description_2],
input[name=new_screenshot_description_3],
input[name=new_screenshot_description_4],
input[name=new_screenshot_description_5],
input[type=file] {
	background: #343434;
	color: #CCC;
	border: 3.5px solid #222;
	border-top: 2.5px solid #555;
	border-left: 2.5px solid #555;
	outline: 0;
	padding: 8px 15px;
	transition: all 0.8s;
	margin-top: 6px;
	margin-bottom: 16px;
}
input[name=new_screenshot_description_1]:focus,
input[name=new_screenshot_description_2]:focus,
input[name=new_screenshot_description_3]:focus,
input[name=new_screenshot_description_4]:focus,
input[name=new_screenshot_description_5]:focus,
input[type=file]:focus {
	background: #565656;
	color: #FFF;
	border: 3.5px solid #777;
	border-top: 2.5px solid #444;
	border-left: 2.5px solid #444;
}
input[type=button],
input[type=submit] {
	background: #343434;
	color: #CCC;
	border: 3.5px solid #222;
	border-top: 2.5px solid #555;
	border-left: 2.5px solid #555;
	outline: 0;
	padding: 8px 15px;
	transition: all 0.8s;
}
input[type=button]:hover,
input[type=button]:focus,
input[type=submit]:hover,
input[type=submit]:focus {
	background: #565656;
	color: #FFF;
	border: 3.5px solid #777;
	border-top: 2.5px solid #444;
	border-left: 2.5px solid #444;
}
/*PARAMETRE SUPLEMENTAIRE*/
::-webkit-scrollbar {
	width: 12px;
}

::-webkit-scrollbar-track { /* BARRE FIXE */
	background: #444;
}

::-webkit-scrollbar-thumb { /* BARRE QUI BOUGE */
	border-radius: 10px;
	background: #666;
}
::-webkit-scrollbar-thumb:hover { 
	background: #999;
}
.cm-tab-wrap-hack > .cm-tab:last-child {
	border-right: 0.8px solid transparent;
}
.cm-tab {
	border-right: 0.8px solid #545249;
}
.CodeMirror-gutters {
	background: #2F3129;
	color: #8F908A;
}
.cm-s-default .cm-meta {
	color: #E6DB74;
}
.CodeMirror-scroll {
    background: #272822;
}
.cm-s-default {
	color: #FFF;
}
.cm-s-default .cm-def {
    color: #F92672;
}
.cm-s-default .cm-tag {
    color: #DDD;
}
.cm-s-default .cm-property {
	color: #66D9EF;
}
.cm-s-default .cm-atom {
	color: #AE81FF;
}
.cm-s-default .cm-keyword {
	color: #66D9EF;
}
.cm-s-default .cm-number {
	color: #F92672;
}
.cm-s-default .cm-string {
	color: #E6DB74;
}
.cm-s-default .cm-qualifier {
    color: #A6E22E;
}	
.cm-s-default .cm-builtin {
    color: #F92672;
}
.cm-s-default .cm-variable-3 {
	color: #E6DB74;
}
.cm-s-default .cm-comment {
	color: #75715E;
}
.CodeMirror div.CodeMirror-cursor {
    border-left: 1.4px solid #F8F8F8;
}
.CodeMirror-focused .CodeMirror-selected {
	background: #49483E;
}
.CodeMirror-selected {
	background: #42423E;
}
div.CodeMirror span.CodeMirror-matchingbracket {
    color: #fff;
	border: 1px solid #42423E;
}






div.form-controls > table {
	width: 1100px;
    box-shadow: 0 0 20px 0px rgba(0, 0, 0, 0.78);
	border: 2px solid #222;
	margin-top: 15px;
}
div.form-controls > table > thead > tr > th,
div.form-controls > table > tbody > tr > td{
	padding: 5px 12px;
	border: 2px solid #222;
}
#title_long a,	
#title_long a:visited {
    color: #BBB !important;
}
#title_long a:hover,	
#title_long a:visited:hover {
    color: #fff !important;
}
.style_author_long {
    color: #547aa0;
}
.pageNumber {
	color: #DDD;
}
#view_inner {
    width: 820px;
    margin-left: 25px;
}
#nav-view {
    background-color: #1a3548;
}
.author-styles {
    background: #2d4250;
    color: #CCC;
    box-shadow: 0 0 20px 0px rgba(0, 0, 0, 0.78);
}
.div-arrow {
    background-color: #355267;
    border: solid 1px #245dbb;
}
.author-styles > thead > tr > th,
.author-styles > tbody > tr > td {
 	padding: 8px 15px; 
    border: 1.5px solid #165f90;
}
.author-styles > tbody > tr > td > a {
	margin-right: 20px;
}
.notice {
    background-color: #156561;
    box-shadow: 4px 4px 7px black;
}
#footer {
    background-color: #2e3e49;
}
#copyrights {
    color: #BBB;
}
#navigation {
    background-color: #2d4250;
}
.navigation_title {
    color: #FFF;
}
.logo_navigation_link {
    background: #DDD;
    height: 20px;
    width: 20px;
    padding: 8px;
    border-radius: 20px;
}
.navigation_link {
    transition: all 1s;
    box-shadow: inset 0 0 20px 10px transparent;
}
.navigation_link:hover {
    background: #1a3548;
    box-shadow: inset 0 0 20px 10px #000;
	border-radius: 0px 0px 15px 15px;
}
.style_card {
    background-color: #05548a;
    box-shadow: 0 5px 10px 0 rgba(0, 0, 0, 0.64);
	color: #DDD;
}
.view_details {
    background-color: #525252;
    color: #83cdff;
	transition: all 0.8s;
}
.view_details:hover,
.view_details:focus {
    background-color: #333;
    color: #0098ff;
}
.style_title {
    color: #bfc0c1;
}
#share_div {
    color: #AAA;
}
#headline {
    color: #8a8c8e;
}
#information_key_left {
    color: #BBB;
}
#infomation_value_left {
    color: #e4e4e4;
}
#information_key {
    color: #BBB;
}
#information_value {
    color: #2879ca;
}
#applies_link {
    color: #e4e4e4;
}
.text_style_page {
    color: #e4e4e4;
}
#notes_textarea {
    background-color: #2d4250;
    color: #DDD;
    padding: 10px 20px;
}
.css_button,
.advanced_button,
.see_more{
    background-color: #1a4e73;
    color: #c0e0f5;
	box-shadow: 0 0 14px 2px black;
	transition: all 0.8s;
}
.css_button:hover,
.css_button:focus,
.advanced_button:hover,
.advanced_button:focus,
.see_more:hover,
.see_more:focus{
    background-color: #434343;
	margin-top: 20px;
	box-shadow: 0 5px 5px 0px black;
    color: #FFF;
}

#send_feedback_button,
.white_button 	{
    background-color: #1a4e73;
    color: #c0e0f5;
	border: 1.6px solid #333;
	box-shadow: 0 0 14px 2px black;
	transition: all 0.8s;
}
#send_feedback_button:hover,
#send_feedback_button:focus	{
    background-color: #434343;
    color: #FFF;
	box-shadow: -5px 5px 5px 0px black;
	margin-left: 5px;
	margin-top: 5px;
	margin-bottom: 23px;
}
.white_button:hover,
.white_button:focus {
    background-color: #434343;
    color: #FFF;
	box-shadow: -5px 5px 5px 0px black;
}
.adContainer,
.adLong {
	display: none;
}
#install_style_button:hover {
    box-shadow: 0 6px 16px 0 rgba(0, 0, 0, 0.59);
}

#install_style_button {
    box-shadow: 0 6px 16px 0 rgba(4, 8, 10, 0.35);
}
#archive_style_button {
    box-shadow: 0 6px 16px 0 rgba(255, 87, 34, 0.18);
}
}
