css:
@-moz-document url-prefix("https://www.coingecko.com"), url-prefix("http://www.coingecko.com")
{
body {
    color: #c4dcde;
}

.main-menu {
    background: #211e1e;
    border-bottom: 1px solid #8cc63f;
    border-top: 1px solid #007279;
}

.nav.coingecko li a {
color: #00a571;
}

#wrapper {
    background-color: #262626;
}
#gecko-table thead th {
    background-color: #303030;
}
#gecko-table {
    background-color: rgb(64, 64, 64);
}

#gecko-table rect[fill] {
    fill: rgb(64, 64, 64);
}

.filter-menu {
    background-color: #29323d;
}

.pagination li a {
    background-color: #313131 !important;
}

.pagination li a:hover {
    background-color: #8cc63f !important;
}

rect[fill] {
    fill: #262626;
}

.daily-changes-value {
    background-color: rgb(64, 64, 64) !important;
}

.daily-changes-value td {
    border-color: rgb(64, 64, 64) !important;
}

.card-header {
    background-color: #303030
}

.card-header .nav-item .active {
    background-color: rgb(64, 64, 64);
    border-color: rgb(115, 139, 128);
}

.nav-tabs .nav-link.active, .nav-tabs .nav-item.show .nav-link {
    color: #57d078;
}

.card-body {
    background-color: rgb(64, 64, 64);
}

.card .row, .card .table {
    background-color: rgb(64, 64, 64);
    margin: 0;
}

.plugins {
    background: #303030;
}

.breadcrumb {
    color: #d3d3d3;
}

#upcoming .table tbody tr:first-child, #upcoming .table tbody tr:nth-child(2), #upcoming .table tbody tr:nth-child(3), #upcoming .table tbody tr:nth-child(4), #upcoming .table tbody tr:nth-child(5) {
    background: #3a585f !important;
}

#ongoing .table tbody tr:first-child, #ongoing .table tbody tr:nth-child(2), #ongoing .table tbody tr:nth-child(3), #ongoing .table tbody tr:nth-child(4), #ongoing .table tbody tr:nth-child(5) {
    background: #3a585f !important;
}

#ended .table tbody tr:first-child, #ended .table tbody tr:nth-child(2), #ended .table tbody tr:nth-child(3), #ended .table tbody tr:nth-child(4), #ended .table tbody tr:nth-child(5) {
    background: #3a585f !important;
}

.progress .text-center {
    color: #646464;
}

.select2-dropdown {
    border: #333333;
}

.select2-results {
    background-color: #333333;
}

.top0 {
    background-color: rgb(64, 64, 64) !important;
}

.post-body {
    color: #aaaaaa;
}
}
