.organic-listing .link.promotedlink .siteTable_organic .ad_1 {
    display: none;
}
.dfp-ad-container {
    display: none;
}
*/

/* Header Styling (alt home image: https://s13.postimg.org/4850krxtj/reddithome.png) */

#header {
    background: rgb(38, 38, 38) !important;
    height: 51px;
    position: fixed;
    left: 0;
    top: 0;
    right: 0;
    z-index: 2147483647;
    border-bottom: 3px solid #5f99cf;
}
#header-img {
    display: inline-block !important;
    -moz-box-sizing: border-box !important;
    box-sizing: border-box !important;
    background: url(https://s13.postimg.org/hnifmkfaf/reddit-logo-white.png) no-repeat !important;
    width: 120px;
    height: 40px;
    padding-left: 120px;
    z-index: 2147483647 !important;
}
.RESDropdownList {
    z-index: 2147483647 !important;
}
#RESConsoleContainer.slideIn {
    z-index: 2147483647 !important;
}
.sr-bar a {
	color: rgb(204, 204, 204) !important;
}
#RESShortcutsEditContainer, #RESShortcutsSort, #RESShortcutsRight, #RESShortcutsLeft, #RESShortcutsAdd {
	background-color: rgb(38, 38, 38) !important;
}
#srList {
    position: fixed !important; 
    z-index: 2147483647 !important;
}
#sr-more-link {
    color: rgb(204, 204, 204) !important;
    background-color: rgb(38, 38, 38) !important;
}
#sr-header-area {
	background: rgb(38, 38, 38) !important;
    color: rgb(210, 90, 50);
    border-bottom: 1px solid gray;
} 
#sr-header-area .drop-choices.srdrop {
    max-height: calc(100vh - 40px);
    overflow-y: auto !important;
}
.dropdown.srdrop .selected {
	color: rgb(204, 204, 204) !important;
}
.link .rank {
    color: rgb(255, 69, 0) !important;
}
.pagename a {
    color: rgb(210, 90, 50) !important;
    letter-spacing: 1px !important;
}
.pagename {
    font-size: 1.2vw;
    position: relative;
    top: -16px !important;
}
.tabmenu {
    position: absolute;
    top: 27px;
    font-size: 1vw !important;
    letter-spacing: 1px !important;
}
.tabmenu li a {
    color: rgb(130, 159, 176);
    background: transparent;
}
.tabmenu li.res-tabmenu-button a {
    display: none !important;
}
.tabmenu a:hover {
    color: rgb(210, 90, 50) !important;
}
.tabmenu li.selected a {
    background: transparent;
    text-decoration: transparent !important;
    color: rgb(210, 90, 50) !important;
}
a.title.may-blank:hover {
    color: rgb(210, 90, 50) !important;
}
.listing-chooser {
    position: fixed !important;
}
#header-bottom-right {
    right: 205px;
    height: 20px;
    top: 20px !important;
    border: 1px solid gray;
    border-radius: 5px !important;
    background-color: rgb(51, 53, 56);
    vertical-align: middle !important;
}
#header-bottom-right a {
    color: rgb(140, 179, 217);
}

/* Hide user karma to save space */

.userkarma {
	display: none; 
}

/* Move & Style search bar */

#search input[type=text] {
    width: 200px;
    position: fixed;
    right: 3px;
    top: 20px;
    z-index: 2147483647;
    border-radius: 5px;
    background-color: rgb(51, 53, 56);
}
#search input[type=submit] {
    position: fixed;
    top: 27px !important;
    right: 16px !important;
    z-index: 2147483647;
    float: right;
}
#searchexpando {
    position: fixed;
    z-index: 2147483647;
    top: 50px;
    right: 2px;
    border: 3px solid gray;
    border-radius: 5px;
}
.combined-search-page #search input[type=text] {
    position: static;
}
/* Side Bar */

.side {
    border: 2px solid gray;
    border-radius: 10px;
    padding: 6px;
    margin-top: 7px;
}
/* Body Styling */

.infobar {
    top: 55px;
}
.login-form-side {
    position: absolute;
    background-color: rgb(51, 53, 56);
    top: 55px;
}
body {
    padding-top: 55px !important;
}
.top-matter {
    top: 55px !important;
}
#RESPinnedHeaderSpacer {
    display: none !important;
}
.res-nightmode .RES-keyNav-activeElement {
    background-color: transparent !important;
}
.link.last-clicked {
    border: none;
}
/* Hide Gold Button */

.give-gold-button,
.goldvertisement,
.embed-comment,
.nub {
    display: none !important;
}
/* RES Even link colors */

.res-nightmode .thing.odd.link {
    padding: 7px !important;
    margin: 0;
    background-color: rgb(51, 53, 56);
}
.res-nightmode .thing.even.link {
    background-color: rgb(38, 38, 38);
    padding: 7px !important;
    margin: 0;
}

}
