css:
@-moz-document domain("www.baidu.com") {
#content_left {
    column-count: 2;
    column-gap: 0px;
    width: 1800px;
    overflow: auto;
    padding-left: 0;
    margin-left: 120px;
}
.result,
.c-container {
    width: 90% !important;
}
.c-container {
    height: 100%;
    overflow: auto;
}
#rs_top_new,
.hit_top_new {
    width: 1280px;
    column-span: all;
    margin-bottom: 4px;
}
#rs_top_new .res-gap-right16 {
    background: #f1f1f1;
    color: #4285F4;
    padding: 6px 16px;
    border-radius: 4px;
    margin: 2px;
    display: inline-block;
    transition: background 2s, color .4s;
}
#rs_top_new .res-gap-right16:hover {
    background: #4285F4;
    color: #f1f1f1;
    transition: background .8s, color .1s;
}
@media screen and (max-width: 1024px) {
    body, #head, #container, #content_left {
        width: 100%;
    }
    body {
        max-width: 100%;
        min-width: 100%;
    }
    #container {
        overflow: hidden;
    }
    #content_left {
        column-count: 1;
    }
}
}
