css:
/* i really want this to be global */

.ace-chrome {
    background-color: #000000;
    color: white;
}
.ace-chrome .ace_support.ace_function {
    color: rgb(237, 255, 0);
}
.ace-chrome .ace_string {
    color: #21f10b;
}
.ace-chrome .ace_keyword {
    color: rgb(228, 17, 17);
}
.ace-chrome .ace_print-margin {
    width: 1px;
    background: #000000;
}
.ace-chrome .ace_variable {
    color: rgb(0, 212, 255);
}
.ace-chrome .ace_meta.ace_tag {
    color: yellow;
}
.ace-chrome .ace_entity.ace_other.ace_attribute-name {
    color: #ff8f00;
}
.show-ace-editor{
    background-color: #000;
}
.ace-chrome .ace_gutter {
    background: #505050;
    color: #fff;
    overflow: hidden;
}
.ace-chrome .ace_cursor {
    color: #ffffff;
}
.ace_string {
    color: #0cb551;
}
.ace-chrome .ace_comment {
    color: #238825;
}
#topnav li a {
    color: #fff;
}
#topnav {
    background: #000000;
    border-radius: 0;
    border-bottom: 1px solid #ffffff;
}
#topnav li.disabled a {
    color: #fff;
    text-decoration: none;
    cursor: not-allowed;
    opacity: 0.7;
}
#subleft {
    display: block;
    float: left;
    height: 100%;
    padding: 5px 10px;
    overflow: auto;
    width: 330px;
    background-color: #000;
}
#subright {
    overflow: hidden;
    height: 100%;
    padding: 0;
    background-color: #000;
}
.yui-skin-sam .yui-dt-scrollable table {
    border: 0;
    background-color: #000;
}
.yui-skin-sam tr.yui-dt-even {
    background-color: #000;
}
.yui-skin-sam tr.yui-dt-odd {
    background-color: #1d1c1c;
    border: 1px solid #fff;
}
td.ygtvcell {
    border: 0;
    padding: 0;
    color: #0eff00;
}
.ygtvtp:after, .ygtvtph:after {
    font-family: FontAwesome;
    position: absolute;
    padding: 2px;
    content: "\f067";
    color: #fff;
    top: 0;
    left: -12px;
    font-size: 10px;
}
.ygtvlm:after, .ygtvlmh:after {
    font-family: FontAwesome;
    position: absolute;
    padding: 2px;
    content: "\f068";
    color: #00fff5;
    top: 0;
    left: -12px;
    font-size: 10px;
}
.yui-skin-sam .yui-dt-liner {
    padding: 5px 10px;
    color: #b1b1b1;
}
#collapseAll{
 	color: #333;
    background-color: #9c9c9c;
    border-color: #000;   
}
.input-group-addon {
    padding: 6px 12px;
    font-size: 14px;
    font-weight: 400;
    line-height: 1;
    color: #fff;
    text-align: center;
    background-color: #9c9c9c;
    border: 1px solid #ccc;
    border-radius: 4px;
}
.ygtvloading:before {
    font-family: FontAwesome;
    position: absolute;
    padding: 2px;
    content: "\f021";
    color: #fff;
    animation: fa-spin 2s infinite linear;
}
.yui-skin-sam .yui-dt-scrollable .yui-dt-bd {
    border-left: 1px solid #7f7f7f;
    border-bottom: 1px solid #7f7f7f;
    border-right: 1px solid #7f7f7f;
    background-color: #000;
}
.fa-file-text {
    font-size: 20px;
    color: #ffffff;
}
.fa-file-code-o {
    font-size: 20px;
    color: #ffffff;
}
.fa-file-archive-o {
    font-size: 20px;
    color: #ffffff;
}
.yui-skin-sam tr.yui-dt-selected td:first-child {
    border-left: 2px solid #ff0000;
}
.yui-skin-sam tr.yui-dt-selected td, .yui-skin-sam tr.yui-dt-selected td.yui-dt-asc, .yui-skin-sam tr.yui-dt-selected td.yui-dt-desc {
    background-color: #797c7d;
    color: #fff;
}
.btn-link {
    color: #00f1ff;
    font-weight: 500;
    cursor: pointer;
    border-radius: 0;
}
.btn-link:hover, .btn-link:focus {
    color: #ffffff;
    text-decoration: underline;
    background-color: transparent;
}
#srtoolbar ul li.disabled>a {
    color: #00ff21;
    cursor: not-allowed;
}
.navbar-cpanel {
    background-color: #0f374a;
    border-color: #ffffff;
    color: #fff;
}
.cpanel_body {
    max-width: none;
    background-color: #000;
}
.widget-collapsible {
    max-height: 75000px;
    padding: 0;
    background-color: #101010;
}
.itemTextWrapper {
    padding: 10px 0 10px 5px;
    display: inline-block;
    vertical-align: middle;
    width: 200px;
    color: #fff;
}
.panel>.table:last-child, .panel>.table-responsive:last-child>.table:last-child {
    border-bottom-right-radius: 3px;
    border-bottom-left-radius: 3px;
    background-color: #101010;
    color: #fff;
}
.ace-chrome .ace_support.ace_type, .ace-chrome .ace_support.ace_class.ace-chrome .ace_support.ace_other {
    color: rgb(105, 231, 245);
}
.ace-chrome .ace_support.ace_constant {
    color: rgb(7, 212, 62);
}
.ygtvtm:after, .ygtvtmh:after {
    font-family: FontAwesome;
    position: absolute;
    padding: 2px;
    content: "\f068";
    left: -12px;
    top: 0;
    color: #fff;
    font-size: 10px;
}
.ygtvlp:after, .ygtvlph:after {
    font-family: FontAwesome;
    position: absolute;
    padding: 2px;
    content: "\f067";
    color: #fff;
    top: 0;
    left: -12px;
    font-size: 10px;
}
.ace-chrome .ace_indent-guide {
    background: #000
}
html[dir="ltr"] #sidebar {
    left: 0;
    background-color: #000;
}
#pma_navigation {
    width: 240px;
    position: fixed;
    top: 0;
    left: 0;
    height: 100%;
    color: #000;
    z-index: 800;
    background-color: #000;
}
#pma_navigation_tree_content a.hover_show_full {
    position: relative;
    z-index: 100;
    color: lime;
}
#pma_navigation_tree li.activePointer{
	background-color: #656565;
}
#page_content {
    margin: 0 .5em;
    background-color: #e5e5e5;
}
.group {
    border: 1px solid #fff;
    background: #525252;
    -moz-border-radius: 4px;
    -webkit-border-radius: 4px;
    border-radius: 4px;
    -moz-box-shadow: 2px 2px 5px #ccc;
    -webkit-box-shadow: 2px 2px 5px #ccc;
    box-shadow: 2px 2px 5px #ececec;
    margin-bottom: 1em;
    padding-bottom: 1em;
}
.ace-chrome .ace_marker-layer .ace_selection {
    background: rgb(74, 74, 74);
}
.ace-chrome .ace_constant.ace_language {
    color: rgb(68, 185, 255);
}
.ace-chrome .ace_keyword.ace_operator {
    color: rgb(255, 255, 255);
}
.table>tbody>tr>td.warning, .table>tbody>tr.warning>td {
    background-color: #ada056;
}
