css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* Pale-blue Lengthwise Folder */
@-moz-document url(chrome://browser/content/bookmarks/bookmarksPanel.xul), url(chrome://scrapbook/content/scrapbook.xul) {
  treechildren::-moz-tree-image(container) {
  list-style-image: url(data:image/png;base64,) !important;
  -moz-image-region: auto !important; }
}

/* Hide Sidebar Folder Tree */
@-moz-document url(chrome://browser/content/bookmarks/bookmarksPanel.xul), url(chrome://browser/content/history/history-panel.xul), url(chrome://scrapbook/content/scrapbook.xul) {
  treechildren::-moz-tree-twisty {
  -moz-appearance: none !important;
  padding-right: 0px !important;
  padding-top: 0px !important;
  width: 0px !important;
  list-style-image: none !important; }
}
