css:
@-moz-document url-prefix("https://laravel.com/docs/"), url-prefix("https://lumen.laravel.com/docs/") {
/* Laravel/Lumen docs */
:root {
  --fg: #f0f0f0;
  --bg: #222;
  --border: #525252;
  --well: #303030;
}
body {
  color: var(--fg);
  background-color: var(--bg) !important;
}
nav.main,
nav.main a.brand,
nav.main ul.main-nav,
nav.main .switcher,
.sidebar {
  border-color: var(--border);
}
nav.main ul.main-nav > li > a {
  color: var(--fg);
}

.docs article h2 a,
.docs article h2 a:hover {
  color: var(--fg);
}
.docs article h1 + ul {
  border-color: var(--border);
}

.docs article table td,
.docs article table th {
  border-color: var(--border);
}

.docs blockquote {
  opacity: 0.95;
}
.docs blockquote.has-icon .tip {
  background-color: #3683C0;
}
.docs blockquote.has-icon .note {
  background-color: #ac4542;
}

:not(pre) > code[class*="language-"],
pre[class*="language-"] {
  background-color: var(--well);
}
code[class*="language-"],
pre[class*="language-"] {
  color: var(--fg);
  text-shadow: none;
}
.token.builtin, .token.char, .token.inserted, .token.selector, .token.string {
  color: #80CE84;
}
.language-css .token.string, .style .token.string, .token.entity, .token.operator, .token.url,
.token.function {
  color: #bbb;
}
.token.cdata, .token.comment, .token.doctype, .token.prolog, .token.punctuation {
  color: #aaa;
}
.token.atrule, .token.attr-value, .token.keyword {
  color: #65bee4
}
.token.attr-name, .token.boolean, .token.constant, .token.deleted, .token.number, .token.property, .token.scope, .token.symbol, .token.tag {
  color: #EC7C71;
}

footer.main {
  background-color: var(--well);
}
footer.main > ul > li > a {
  color: var(--fg);
}

}

@-moz-document url("https://laravel.com/"), url("https://lumen.laravel.com/") {
/* Laravel/Lumen common landing page styles */
:root {
  --fg: #f0f0f0;
  --bg: #222;
  --border: #525252;
  --well: #303030;
}
body {
  color: var(--fg);
  background-color: var(--bg);
}

.panel.features .blocks .block {
  border-color: var(--border);
}
.panel.features .blocks .block p {
  color: var(--fg);
}

.browser-window,
.terminal-window {
  background-color: transparent;
  border-color: #666;
}
.browser-window .top-bar,
.terminal-window .top-bar {
  background-color: #444;
  border-color: #444;
}
.browser-window .circle,
.terminal-window .circle {
  background-color: #777;
}
.browser-window .window-content pre[class*="language-"] {
  background-color: var(--well);
}
code[class*="language-"],
pre[class*="language-"] {
  color: var(--fg);
  text-shadow: none;
}
.token.builtin, .token.char, .token.inserted, .token.selector, .token.string {
  color: #80CE84;
}
.language-css .token.string, .style .token.string, .token.entity, .token.operator, .token.url,
.token.function {
  color: #bbb;
}
.token.cdata, .token.comment, .token.doctype, .token.prolog, .token.punctuation {
  color: #aaa;
}
.token.atrule, .token.attr-value, .token.keyword {
  color: #65bee4
}
.token.attr-name, .token.boolean, .token.constant, .token.deleted, .token.number, .token.property, .token.scope, .token.symbol, .token.tag {
  color: #EC7C71;
}
.line-numbers .line-numbers-rows {
  background-color: var(--well);
}


}

@-moz-document url("https://laravel.com/") {
/* Laravel landing page */
nav.main,
nav.main a.brand,
nav.main ul.main-nav,
nav.main .switcher,
.sidebar {
  border-color: var(--border);
}
nav.main ul.main-nav > li > a {
  color: var(--fg);
}

.home .hero .macbook svg {
  opacity: 0.6;
  filter: brightness(0.3);
}
pre[class*="language-"] {
  background-color: var(--bg);
}

.callout.rule {
  border-bottom-color: var(--border);
  color: var(--fg);
}
.callout.rule .text {
  background-color: var(--border);
}
.callout.minimal {
  border-color: var(--border);
}
.callout.minimal .callout-title,
.callout.minimal p {
  color: var(--fg);
}

.panel.dark {
  background-color: var(--well);
}
.panel.features .blocks {
  background-color: var(--bg);
}

.panel.ecosystem {
  padding-top: 50px;
  background-image: none;
}
.callout.pop .callout-body {
  height: auto;
}
.packages .third:not(:last-child) {
  border-right-color: var(--border);
}
.packages .package .package-title {
  color: var(--fg);
}
.packages .package p {
  color: #a6b5c5;
}

footer.main {
  background-color: var(--well);
}
footer.main > ul > li > a {
  color: var(--fg);
}

}

@-moz-document url("https://lumen.laravel.com/") {
/* Lumen landing page */
.panel.color {
  background-image: linear-gradient(to right, #6c3332, #755640)
}
.panel.dark {
  background-color: var(--bg);
}

.panel.features .blocks {
  background-color: var(--well);
}
.chart .bar {
  background-color: transparent;
}

.panel.features .logo {
  opacity: 0.65;
}

footer.main {
  background-color: var(--well);
}
footer.main > ul > li > a {
  color: var(--fg);
}

}
