css:
@-moz-document domain("dmzj.com")
{
html,
body,
div,
h1,
h2,
h3,
h4,
h5,
h6,
p,
form,
ul,
li,
dl,
dt,
dd,
ol,
table,
.gray12,
.black_font12,
.content_r .text {
	font: 400 calc(7pt + .3vw) sans-serif;
}

body {
	background-color: #bbb;
}

.numfont,
.dy_r p .new {
	font: 400 calc(6pt + .3vw) sans-serif
}

.mainNav_inner,
.logobox,
.navbg1,
.search_content,
.newpic_content,
.wrap,
.ucenter_wrap,
.ucentop {
	width: calc(100% - 24mm);
	min-width: 1080px;
	margin: 0 auto;
}

.navbg1 {
	height: auto;
	background: linear-gradient(180deg,
			rgba(150, 200, 210, .9) 0,
			rgba(50, 120, 150, .9) 15%,
			rgba(40, 100, 130, .9) 85%,
			rgba(30, 70, 90, .9) 100%);
	border-radius: 2mm;
	box-shadow: 0 0 0 1px rgba(0, 0, 0, .3);
}

.navigation_list {
	float: right;
}

.qqnew {
	margin-right: 25px;
	text-shadow: 1px 1px 1px #fff,
		-1px 1px 1px #fff,
		1px -1px 1px #fff,
		-1px -1px 1px #fff,
		0 0 5mm #fff;
}

.navbg1 ul {
	display: flex;
}

.navbg1 ul li {
	flex: 1 1 auto;
	float: none;
	padding: 0;
	height: auto;
	line-height: 1.4;
}

.navbg1 ul li a:link {
	display: block;
	font-size: 12pt;
	padding: .4em 0;
	color: #ddd;
}

.navbg1 ul li a:hover {
	color: #fff;
	background: rgba(255, 255, 255, .1);
	box-shadow: 0 0 2mm #d55;
}

.navbg1 ul li a:active {
	background: rgba(0, 0, 0, .1);
	box-shadow: inset 0 1px 2mm #555;
}

a.navbg1btn_on {
	padding: 0;
	margin: 0;
	height: auto;
	background: none;
}

a.navbg1btn_on span {
	display: block;
	height: 100%;
	line-height: 1.4;
	color: #ddd;
	background: none;
	margin: 0;
	padding: 0;
}

a.navbg1btn_on:hover span {
	color: #fff;
}

.today_recommended_bigpic {
	width: auto;
	display: flex;
}

.today_recommended_bigpic1,
.today_recommended_bigpic2,
.today_recommended_bigpic2_a1,
.today_recommended_bigpic2_a2 {
	flex: 1 1 auto;
	width: auto;
	border: none;
}

.today_recommended_bigpic2_a,
.today_recommended_bigpic2_b {
	display: flex;
	width: 100%;
}

.today_recommended_bigpic2_b1,
.today_recommended_bigpic2_b2 {
	flex: 1 1 auto;
	width: auto;
	min-width: 12em;
	padding: 0 2mm;
	margin: 0;
}

.blue_bg {
	background: none;
}

.search_content {
	box-shadow: inset 0 0 1mm #666;
}

.search_list1 {
	background: rgba(200, 240, 255, .6);
}

.search_list3 {
	background: none;
}

.wrap {
	background: rgba(200, 210, 220, .7);
	padding: 2mm 0;
}

.hottest_animation ul span {
	padding-right: .5em;
	margin: 0;
}

.hottest_animation ul li {
	padding: 0;
}

.hottest_animation ul li a:link,
.hottest_animation ul li a:visited {
	color: #333;
	padding: 0 .5em;
	border-left: 1px solid #888;
}

.leftmiddle,
.middleright,
.ucenter_warp_r {
	width: calc(100% - 280px);
}

.wrap .right {
	zoom: 1.2
}

.girlnew,
.topnav2,
.tcaricature_block2,
.tcaricature_block {
	width: 100%;
	display: flex;
	flex-wrap: wrap;
}

.tcaricature_block ul {
	flex: 1 1 auto;
}

div.pages {
	flex: 0 0 100%;
}

.adiv,
.adivhidden {
	width: 100%;
}

.tcaricature_block ul {
	flex: 1 1 10%;
	max-width: 240px;
	height: auto;
	padding: 2mm;
	margin: 1pt;
	background: rgba(255, 255, 255, .5);
	border-radius: 3mm;
	box-shadow: 0 0 1mm #888;
}

.tcaricature_block2 ul,
.topnav2 li {
	display: inline-block;
	flex: 1 1 200px;
	width: 200px;
	max-width: 360px;
	height: auto;
	min-height: 310px;
	padding: 2mm;
	margin: 1mm;
	background: rgba(255, 255, 255, .5);
	border-radius: 3mm;
	box-shadow: 0 0 1mm #888;
}

.tcaricature_block ul li,
.tcaricature_block2 ul li {
	width: auto;
}

.tcaricature_block2 ul li>a,
.topnav2 li>a {
	display: block;
	width: 100%;
	height: 240px;
	text-align: center;
	overflow: hidden;
}

.tcaricature_block ul li img,
.tcaricature_block2 ul li img,
.topnav2 li img {
	width: 100%;
	height: 100%;
	margin: 0 auto;
	object-fit: cover;
	transition: .2s;
}

.tcaricature_block ul li a {
	border-radius: 1mm;
}

.tcaricature_block ul li a img {
	max-height: 240px;
	border: none;
}

.tcaricature_block2 ul li a:hover img {
	border: none;
	transform: scale(1.1);
}

.mainNav_in_r ul li a .c_2 {
	font-weight: 600;
	margin-left: 1mm;
}

.caricature_see1,
.caricature_see2,
.caricature_see3 {
	width: auto;
	display: flex;
}

.caricature_see1_a,
.caricature_see2_a,
.caricature_see3_a {
	flex: 1 1 auto;
	margin: 0 2mm;
	padding: 2mm 4mm;
	background: rgba(255, 255, 255, .4);
}

.caricature_see1_a1,
.caricature_see1_a2 {
	width: 100%;
}

.caricature_see1_a1 a {
	float: right;
}

.tcaricature_see1_a2_b {
	width: calc(100% - 120px);
	margin-left: 3mm;
}

.tcaricature_see1_a3,
.tcaricature_see1_a3_a {
	width: calc(45% - 10px);
}

.initialsnewindex,
.twoer_index,
.twoer_index-navi,
.pictextli {
	width: auto;
}

.zptypetitnew {
	width: 6em;
}

.twoer_index-navi span {
	width: calc(100% - 6em);
}

.icn-02_index {
	width: 18%;
}

.newpic_bg,
.newpic_bgno {
	display: flex;
	background: none;
	padding: 1mm 0;
	margin: 0;
}

.boxdiv1 {
	flex: 1 1 22%;
	display: flex;
	width: auto;
	margin: 0 1mm;
	padding: 2mm;
	background: rgba(255, 255, 255, .4);
	border-radius: 2mm;
	box-shadow: 0 0 1mm #aaa;
	transition: .2s;
}

.boxdiv1:hover {
	background: #eee;
	box-shadow: 0 0 2mm #666;
}

.picborder {
	flex: 1 1 110px;
	height: auto;
}

.picborder a,
.picborder img {
	width: 100%;
	height: 100%;
	padding: 0;
	margin: 0;
}

.picborder img {
	object-fit: cover;
}

.pictext {
	flex: 5 5 120px;
	width: auto;
}

a.pictextst:link,
a.pictextst:visited {
	font-size: unset;
	margin: -.2em -.4em;
	padding: .2em .4em;
	border-radius: 1em;
}

a.pictextst:hover {
	text-decoration: none;
	color: #633;
	background: #fff;
	box-shadow: 0 1px 5pt 1px #666;
}

a.pictextst:active {
	text-decoration: none;
	background: #ddd;
	box-shadow: inset 0 1px 5pt #222;
}

.boxdiv1 .gray12 {
	margin-left: .7em;
}

.cartoon_online_border,
.cartoon_online_border_other {
	background: #fff;
}

.cartoon_online_border li,
.cartoon_online_border_other li {
	display: inline-block;
	float: none;
	width: auto;
	height: auto;
	line-height: 1em;
	overflow: visible;
}

.cartoon_online_border li a,
.cartoon_online_border_other li a {
	display: block;
	min-width: 5em;
	max-width: 12em;
	text-align: center;
	padding: .3em .5em;
	margin: 1pt 0;
	transition: .2s;
	border-radius: 1em;
}

.cartoon_online_border li a:hover,
.cartoon_online_border_other li a:hover {
	color: #cef;
	background: #267;
}

.search_list_m {
	background: rgba(255, 255, 255, .4);
}

.search_list_m_cat {
	width: 5em;
}

.search_list_m_all {
	width: 3em;
}

.search_list_m_right {
	width: calc(100% - 10em);
}

.rank_tabs_li {
	display: flex;
}

.ucenter_warp_r {
	background: rgba(255, 255, 255, .6)
}

.sub_center_con {
	background: rgba(0, 0, 0, .1);
}

.message_center,
.sub_center,
.inter_center,
.trend_center,
.modify_center,
.message_center_h,
.sub_center_h,
.inter_center_h,
.trend_center_h,
.modify_center_h,
.message_center_con,
.sub_center_con,
.message_content,
.sub_content,
.sub_content .letter_option,
.sub_content .Tip,
.sub_content .dy_wrap,
.dy_wrap .dy_wrap_con,
.dy_content,
.dy_r h3,
.dy_r p {
	width: auto;
}

.dy_r p {
	height: auto;
	margin: 0;
}

.dy_r a.begin,
.dy_r a.qx {
	margin: 2mm 1mm 0;
}

.dy_r p em.c_space {
	width: auto;
	max-width: calc(100% - 9em);
}

.dy_r p .new {
	width: auto;
	height: auto;
	color: #fff;
	padding: 0 .2em;
	border-radius: 1mm;
	background: #d55;
}

.dy_r p .new:after {
	content: "NEW";
}

.dy_content {
	display: flex;
	flex-wrap: wrap;
}

.dy_content .dy_content_li {
	flex: 1 1 320px;
	padding: 2mm;
	margin: 0;
	background: rgba(255, 255, 255, .4);
	box-shadow: 0 0 0 1px #ccc;
	transition: .2s;
}

.dy_content .dy_content_li:hover {
	border-radius: 2mm;
	box-shadow: 0 0 1mm 1px #888;
	background: #fff;
	z-index: 2;
	transform: scale(1.02);
}

.dy_content_li .dy_r {
	width: calc(100% - 100px);
}

.header-box {
	position: relative;
	max-height: 10mm;
	margin: 0;
	overflow: hidden;
	transition: .3s;
}

.header-box:after {
	position: absolute;
	display: block;
	bottom: 2mm;
	right: calc(50vw - 530px);
	content: "➤";
	font-size: 1.2em;
	text-align: center;
	border-radius: 1em;
	transform: rotate(90deg);
	transition: 1s;
}

.header-box:hover {
	max-height: 100px;
	opacity: 1;
}

.header-box:hover:after {
	transform: rotate(270deg);
}

.display_graybg {
	position: relative;
	width: auto !important;
	background: transparent !important;
	padding: 0 6mm;
	z-index: 2;
	opacity: .1;
	backdrop-filter: blur(2mm);
	-webkit-backdrop-filter: blur(2mm);
	transition: .5s;
}

.display_graybg:hover {
	background: rgba(255, 255, 255, .7) !important;
	opacity: 1;
	transition: .2s;
}

.display_graybg .display_middle {
	float: none;
	width: calc(100% - 380px);
	margin: 0 auto;
}

.funcdiv div>* {
	font-size: 11pt !important;
	line-height: 2;
	padding: 0 .5em;
}

.box_text {
	width: auto;
	height: auto;
}

.hotrm_focus,
.hotrm_list {
	background: #eee;
}

.hotrm_list .img_desc {
	overflow: hidden;
}

.adiv2hidden {
	width: auto;
}

#center_box {
	position: relative;
	top: -12mm;
	direction: rtl;
	text-align: center;
	max-width: calc(100% - 4mm);
	margin: 0 auto;
	border-radius: 6mm;
}

.inner_img {
	direction: ltr;
	display: inline-block;
	margin: 0 0 2mm !important;
}

.curr_page {
	height: auto;
	line-height: 1.4;
	color: #eee;
}

#center_box img,
#comicShow.scrollMode img {
	height: calc(100vh - 80px);
	padding: 0 !important;
	border: none !important;
	z-index: -1;
}

.float_code {
	opacity: .3;
	transition: .3s;
}

.float_code:hover {
	opacity: 1;
}

.top_list li>* {
	vertical-align: middle;
}

.week_mend_nav li,
.read_week a,
.top_list .num {
	font-size: 10pt;
}

.read_week .float_right {
	font-size: 8pt;
}

.read10_mend_mr_particular,
.read10_mend_mr_particular .float_left {
	display: flex;
}

.read10_mend_mr_particular .apinknum {
	top: 0;
}

.read10_mend_mr_particular .float_left,
.read10_mend_mr_particular .linehidden {
	flex: 1 1 auto;
}

@media screen and (min-width: 144vh) {
	#center_box {
		max-width: 150vh;
		box-shadow: inset 0 0 0 1px #40546a;
	}

	#center_box:hover {
		box-shadow: inset 0 0 8mm rgba(80, 100, 120, .6);
	}

	#center_box:hover .inner_img:first-child {
		display: block;
	}

	#center_box:before {
		position: fixed;
		top: 20%;
		right: max(calc(50vw - 70vh), 5mm);
		direction: ltr;
		font-size: 0;
		opacity: .8;
		background: rgba(0, 0, 0, .5);
		box-shadow: 0 0 1mm #eee;
		padding: 5px;
		border-radius: 5px;
		z-index: 2;
	}

	#center_box:before {
		content: url('data:image/png;base64,');
	}

	#center_box:hover:before {
		content: url('data:image/png;base64,');
	}
}

@supports (-webkit-hyphens: none) {

	#center_box img,
	#comicShow.scrollMode img {
		height: calc(100vh - 67px);
	}
}
}
